import React from 'react'
import { AppBar, Toolbar, Button, Box } from '@mui/material'
import Logo from '../assets/logo2.png'

const Navbar = () => {
  const scrollTo = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <AppBar
      position="sticky"
      elevation={0}
      sx={{
        backgroundColor: 'rgba(23,49,62,0.6)',   // slightly more transparent
        backdropFilter: 'blur(12px)',
        px: { xs: 1, md: 3 },
        py: { xs: 0.2, md: 0.4 },
      }}
    >
      <Toolbar disableGutters sx={{
        display: 'flex',
        alignItems: 'center',
        height: { xs: 48, md: 54 },
        position: 'relative',
      }}>
        {/* Logo flush left, even smaller */}
        <Box
          onClick={() => scrollTo('home')}
          sx={{
            cursor: 'pointer',
            ml: { xs: 0.5, md: 1 },
            transition: 'transform 0.3s ease',
            '&:hover': { transform: 'scale(1.1) rotate(-2deg)' },
          }}
        >
          <Box
            component="img"
            src={Logo}
            alt="Odyssey Logo"
            sx={{
              height: { xs: 28, md: 36 },
              filter: 'drop-shadow(0 0 2px rgba(255,255,255,0.15))',
              borderRadius: 1,
            }}
          />
        </Box>

        {/* Centered nav items */}
        <Box
          sx={{
            position: 'absolute',
            left: '50%',
            transform: 'translateX(-50%)',
            display: 'flex',
            gap: { xs: 1.5, md: 3 },
          }}
        >
          {['about', 'projects', 'feedback'].map((id) => (
            <Button
              key={id}
              onClick={() => scrollTo(id)}
              sx={{
                position: 'relative',
                color: '#F3E2D4',
                textTransform: 'none',
                fontWeight: 300,
                fontSize: { xs: '0.85rem', md: '1.2rem' },
                px: 0,
                minWidth: 0,
                '&:after': {
                  content: '""',
                  position: 'absolute',
                  left: 0,
                  bottom: -1,
                  width: '0%',
                  height: '1px',
                  backgroundColor: '#C5B0CD',
                  transition: 'width 0.25s ease',
                },
                '&:hover': {
                  color: '#C5B0CD',
                  '&:after': { width: '100%' },
                },
              }}
            >
              {id.charAt(0).toUpperCase() + id.slice(1)}
            </Button>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  )
}

export default Navbar
