import { Typography, Grid, Card, CardContent, CardMedia } from "@mui/material";

const team = [
  {
    name: "John Doe",
    role: "Co-founder & Project Manager",
    image: "/assets/john.jpg",
    bio: "Passionate about youth mobility and international education.",
  },
  // Add more team members...
];

export default function About() {
  return (
    <>
      <Typography variant="h4" gutterBottom>About Us</Typography>
      <Typography variant="body1" mb={4}>
        Odyssey of Wisdom is a Greek youth association connecting young people with Erasmus+ opportunities across Europe...
      </Typography>

      <Grid container spacing={4}>
        {team.map((person, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={person.image}
                alt={person.name}
              />
              <CardContent>
                <Typography variant="h6">{person.name}</Typography>
                <Typography variant="subtitle2" color="text.secondary">
                  {person.role}
                </Typography>
                <Typography variant="body2">{person.bio}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </>
  );
}
