import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania2.ξπγ";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

// positions for the five subtle vignettes — feel free to tweak
const vignettePositions = [
  {
    top: "8%",
    left: "6%",
    width: { xs: "34%", md: "22%" },
    height: { xs: "30%", md: "40%" },
    rotate: "-6deg",
  }, // top-left
  {
    top: "6%",
    right: "6%",
    width: { xs: "30%", md: "20%" },
    height: { xs: "28%", md: "36%" },
    rotate: "6deg",
  }, // top-right
  {
    top: "30%",
    left: { xs: "4%", md: "8%" },
    width: { xs: "28%", md: "18%" },
    height: { xs: "36%", md: "48%" },
    rotate: "-3deg",
  }, // mid-left
  {
    top: "30%",
    right: { xs: "4%", md: "8%" },
    width: { xs: "28%", md: "18%" },
    height: { xs: "36%", md: "48%" },
    rotate: "3deg",
  }, // mid-right
  {
    bottom: "6%",
    left: "34%",
    width: { xs: "32%", md: "28%" },
    height: { xs: "22%", md: "30%" },
    rotate: "-1deg",
  }, // bottom-center
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images for smooth transitions
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original background as the base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Subtle vignette photo layer (discrete, decorative) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {images.map((src, i) => {
          // compute a position for this vignette by cycling positions array
          const pos = vignettePositions[i % vignettePositions.length];
          const isActive = i === index;

          return (
            <Box
              key={src}
              sx={{
                position: "absolute",
                top: pos.top ?? "auto",
                bottom: pos.bottom ?? "auto",
                left: pos.left ?? "auto",
                right: pos.right ?? "auto",
                width: pos.width,
                height: pos.height,
                borderRadius: { xs: 2, md: 3 },
                overflow: "hidden",
                transform: `rotate(${pos.rotate}) ${
                  isActive ? "scale(1.02)" : "scale(0.98)"
                }`,
                transition:
                  "opacity 700ms ease, transform 900ms cubic-bezier(.2,.9,.2,1), filter 700ms ease",
                opacity: isActive ? 0.32 : 0.12, // subtle; active is visible but still discreet
                boxShadow: isActive
                  ? "0 12px 30px rgba(6,12,34,0.28)"
                  : "0 8px 20px rgba(6,12,34,0.16)",
                border: "1px solid rgba(243,226,212,0.06)",
                backgroundImage: `url(${src})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                // make photos discrete: desaturated, slightly darker, soft contrast
                filter: isActive
                  ? "grayscale(30%) contrast(0.95) brightness(0.9) saturate(0.8)"
                  : "grayscale(50%) contrast(0.9) brightness(0.82) saturate(0.6)",
                // gentle blend so they integrate with the gradient
                mixBlendMode: "normal",
              }}
            >
              {/* soft vignette gradient inside each image for better legibility and style */}
              <Box
                sx={{
                  position: "absolute",
                  inset: 0,
                  background:
                    "linear-gradient(180deg, rgba(255,255,255,0.00) 0%, rgba(20,30,40,0.12) 100%)",
                  pointerEvents: "none",
                }}
              />
            </Box>
          );
        })}

        {/* optional soft noise / texture overlay to give subtle film feel (very low opacity) */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 1,
            backgroundImage:
              "radial-gradient(circle at 10% 10%, rgba(255,255,255,0.012), transparent 6%), radial-gradient(circle at 80% 80%, rgba(0,0,0,0.012), transparent 8%)",
            pointerEvents: "none",
          }}
        />
      </Box>

      {/* small decorative blurred circle (keeps original feel but softer) */}
      <Box
        sx={{
          position: "absolute",
          top: "-18%",
          right: "-8%",
          width: { xs: 160, md: 260 },
          height: { xs: 160, md: 260 },
          bgcolor: "rgba(243,226,212,0.22)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(10px)",
          pointerEvents: "none",
        }}
      />

      {/* content (above the vignette layer) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.15rem", md: "3.4rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.4)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.18rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
