import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

// Desktop pane positions â€” decorative, subtle
const paneLayout = [
  { top: "6%", right: "6%", width: 220, height: 140, rotate: -6, z: 6 },
  { top: "20%", right: "14%", width: 260, height: 170, rotate: 6, z: 5 },
  { top: "42%", right: "8%", width: 200, height: 130, rotate: -3, z: 7 },
  { top: "62%", right: "16%", width: 240, height: 150, rotate: 4, z: 4 },
  { top: "78%", right: "6%", width: 190, height: 120, rotate: -8, z: 3 },
];

export default function Hero() {
  const [index, setIndex] = useState(0);
  const intervalRef = (useRef < number) | (null > null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  // cycle index every 4s (or slower if reduced motion)
  useEffect(() => {
    if (intervalRef.current) {
      window.clearInterval(intervalRef.current);
    }

    const delay = prefersReducedMotion ? 8000 : 4000;
    intervalRef.current = window.setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, delay);

    return () => {
      if (intervalRef.current) window.clearInterval(intervalRef.current);
    };
  }, [prefersReducedMotion]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 3, md: 8 },
        zIndex: 2,
        // keep the original gradient background
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* --- Decorative image cluster for md+ (subtle, editorial) --- */}
      <Box
        aria-hidden
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          display: { xs: "none", md: "block" },
        }}
      >
        {/* a faint veil to keep the cluster tasteful */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.02) 0%, rgba(10,18,26,0.06) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {paneLayout.map((p, paneIndex) => {
          const imgIdx = (index + paneIndex) % images.length;
          const src = images[imgIdx];

          return (
            <Box
              key={paneIndex}
              sx={{
                position: "absolute",
                top: p.top,
                right: p.right,
                width: p.width,
                height: p.height,
                transform: `rotate(${p.rotate}deg) translateZ(0)`,
                zIndex: p.z,
                borderRadius: 12,
                overflow: "hidden",
                boxShadow: "0 10px 30px rgba(3,12,24,0.28)",
                border: "1px solid rgba(243,226,212,0.08)",
                transition:
                  "transform 520ms cubic-bezier(.2,.9,.2,1), opacity 520ms ease",
                opacity: 0.95,
                pointerEvents: "none",
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`hero-thumb-${paneIndex}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // make them discreet: gentle desat and warm tint
                  filter: "grayscale(8%) saturate(82%) contrast(0.98)",
                  opacity: 0.95,
                  transition: "transform 12000ms linear, opacity 520ms ease",
                  transform: prefersReducedMotion ? "scale(1)" : "scale(1.03)",
                }}
              />

              {/* a subtle vignette to make the pane feel like design, not a stamp */}
              <Box
                sx={{
                  position: "absolute",
                  inset: 0,
                  background:
                    "linear-gradient(180deg, rgba(0,0,0,0) 10%, rgba(0,0,0,0.12) 100%)",
                  pointerEvents: "none",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* --- Mobile: subtle horizontal ribbon of thumbnails (discrete but visible) --- */}
      <Box
        aria-hidden
        sx={{
          position: "absolute",
          left: "50%",
          transform: "translateX(-50%)",
          bottom: { xs: 84, sm: 96, md: "auto" },
          zIndex: 2,
          display: { xs: "flex", md: "none" },
          gap: 10,
          alignItems: "center",
          justifyContent: "center",
          px: 2,
          pointerEvents: "none",
        }}
      >
        {images.map((src, i) => {
          const active = i === index;
          return (
            <Box
              key={i}
              sx={{
                width: active ? 68 : 54,
                height: active ? 46 : 36,
                borderRadius: 8,
                overflow: "hidden",
                boxShadow: active
                  ? "0 8px 20px rgba(3,12,24,0.28)"
                  : "0 6px 14px rgba(3,12,24,0.18)",
                transform: active
                  ? "translateY(-6px) scale(1.02)"
                  : "translateY(0) scale(1)",
                transition: "all 360ms cubic-bezier(.2,.9,.2,1)",
                border: active
                  ? "1px solid rgba(243,226,212,0.16)"
                  : "1px solid rgba(243,226,212,0.08)",
                pointerEvents: "none",
                backgroundColor: "rgba(10,14,20,0.03)",
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`mobile-thumb-${i}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  filter: "grayscale(10%) saturate(80%)",
                  opacity: active ? 1 : 0.88,
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* Decorative soft circle accent (keeps original vibe) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.20)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
          pointerEvents: "none",
        }}
      />

      {/* --- Content (logo, heading, subtitle, CTA) --- */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 5,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.35)",
          zIndex: 5,
          maxWidth: 920,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={720}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 5,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 22px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 5,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom svg wave (keeps original) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 5,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
}
