import { Box, Typography, Grid, Card, CardMedia, CardContent, Chip } from '@mui/material'
import { projects } from '../data/projects'

const Projects = () => (
  <Box id="projects" sx={{ position: 'relative', background: 'linear-gradient(120deg, #EADAE3 0%, #F3E2D4 100%)', color: '#17313E' }}>
    {/* Top wave to connect from About */}
    <Box
      component="svg"
      viewBox="0 0 1440 80"
      sx={{ position: 'absolute', top: 0, left: 0, width: '100%' }}
    >
      <path
        fill="#EADAE3"
        d="M0,0 C360,100 1080,-100 1440,0 L1440,80 L0,80 Z"
      />
    </Box>

    <Box sx={{ position: 'relative', zIndex: 1, pt: 12, pb: 8, px: { xs: 3, md: 10 } }}>
      {/* Section Title */}
      <Typography
        variant="h3"
        sx={{
          fontFamily: "'DM Serif Display', serif",
          fontWeight: 400,
          textAlign: 'center',
          mb: 2,
          color: '#415E72',
        }}
      >
        Our Projects
      </Typography>
      <Typography
        variant="h6"
        maxWidth={900}
        mx="auto"
        sx={{
          fontFamily: 'Inter, sans-serif',
          fontWeight: 300,
          textAlign: 'center',
          color: '#17313E',
          mb: 6,
          lineHeight: 1.6,
        }}
      >
        From sustainability exchanges to cultural food projects, explore the Erasmus+ journeys we’ve participated in and organized—each fostering growth, dialogue, and community.
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {projects.map((p) => (
          <Grid item xs={12} sm={6} md={4} key={p.title}>
            <Card
              elevation={6}
              sx={{
                borderRadius: 3,
                overflow: 'hidden',
                transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                '&:hover': {
                  transform: 'translateY(-8px)',
                  boxShadow: '0 12px 24px rgba(0,0,0,0.15)',
                },
                display: 'flex',
                flexDirection: 'column',
                height: '100%',
              }}
            >
              <Box
                component="img"
                src={p.image}
                alt={p.title}
                sx={{
                  width: '100%',
                  height: 240,
                  objectFit: 'cover',
                }}
              />
              <CardContent sx={{ flexGrow: 1, px: 3, py: 2.5 }}>
                <Typography
                  variant="h5"
                  sx={{
                    fontFamily: "'DM Serif Display', serif",
                    fontSize: '1.5rem',
                    color: '#415E72',
                    mb: 1,
                  }}
                >
                  {p.title}
                </Typography>

                <Chip
                  label={p.date}
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    backgroundColor: '#C5B0CD',
                    color: '#17313E',
                    mb: 1,
                    px: 1.5,
                    py: 0.25,
                    fontSize: '0.8rem',
                  }}
                />
                <Chip
                  label={p.location}
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    backgroundColor: '#C5B0CD',
                    color: '#17313E',
                    mb: 2,
                    ml: 1,
                    px: 1.5,
                    py: 0.25,
                    fontSize: '0.8rem',
                  }}
                />

                <Typography
                  variant="body2"
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    color: '#415E72',
                    lineHeight: 1.6,
                  }}
                >
                  {p.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  </Box>
)

export default Projects
