import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const IMAGES = [flag_photo, romania1, romania2, romania3, romania2_3];

const cardLayouts = [
  // subtle cluster positions (percent, percent, rotation, scale)
  { left: "6%", top: "18%", rot: -8, w: { xs: 92, md: 140 } },
  { left: "18%", top: "8%", rot: -4, w: { xs: 72, md: 110 } },
  { left: "80%", top: "12%", rot: 6, w: { xs: 88, md: 130 } },
  { left: "72%", top: "30%", rot: 10, w: { xs: 64, md: 100 } },
  { left: "50%", top: "6%", rot: -2, w: { xs: 80, md: 120 } },
];

const Hero = () => {
  const { t } = useLanguage();
  const [index, setIndex] = useState(0);
  const intervalRef = (useRef < number) | (null > null);
  const [reduceMotion, setReduceMotion] = useState(false);

  // preload images
  useEffect(() => {
    IMAGES.forEach((src) => {
      const img = new Image();
      img.src = src;
    });

    // respect prefers-reduced-motion
    const mq = window.matchMedia?.("(prefers-reduced-motion: reduce)");
    setReduceMotion(mq?.matches ?? false);
    const listener = (e) => setReduceMotion(e.matches);
    if (mq?.addEventListener) mq.addEventListener("change", listener);
    else if (mq?.addListener) mq.addListener(listener);
    return () => {
      if (mq?.removeEventListener) mq.removeEventListener("change", listener);
      else if (mq?.removeListener) mq.removeListener(listener);
    };
  }, []);

  // cycle every 4s
  useEffect(() => {
    if (intervalRef.current) window.clearInterval(intervalRef.current);
    intervalRef.current = window.setInterval(() => {
      setIndex((i) => (i + 1) % IMAGES.length);
    }, 4000);
    return () => {
      if (intervalRef.current) window.clearInterval(intervalRef.current);
    };
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep original page gradient as requested
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative framed thumbnail cluster (discrete, low-opacity, behind content) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* subtle vignette to keep center content readable */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "radial-gradient(ellipse at center, rgba(0,0,0,0) 0%, rgba(0,0,0,0.18) 75%)",
            zIndex: 1,
            pointerEvents: "none",
          }}
        />

        {cardLayouts.map((pos, idx) => {
          // each card shows a rotating image, but staggered by its index for variety
          const imgSrc = IMAGES[(index + idx) % IMAGES.length];

          return (
            <Box
              key={idx}
              sx={{
                position: "absolute",
                left: pos.left,
                top: pos.top,
                zIndex: 0,
                width: pos.w,
                // maintain aspect ratio by using a fixed height via padding trick
                // but simpler: give explicit height for nicer shapes
                height: {
                  xs: `calc(${pos.w.xs}px * 0.66)`,
                  md: `calc(${pos.w.md}px * 0.66)`,
                },
                transform: `rotate(${pos.rot}deg)`,
                transformOrigin: "center",
                transition: reduceMotion
                  ? "none"
                  : "transform 700ms cubic-bezier(.2,.9,.2,1), opacity 900ms ease",
                display: { xs: "block", md: "block" },
                // keep them slightly faded and subtle
                opacity: { xs: 0.2, md: 0.16 },
                // enable smoother layering
                willChange: "transform, opacity",
              }}
            >
              {/* framed card */}
              <Box
                sx={{
                  position: "relative",
                  width: "100%",
                  height: "100%",
                  borderRadius: 2,
                  overflow: "hidden",
                  boxShadow: "0 8px 26px rgba(6,10,20,0.26)",
                  border: "1px solid rgba(243,226,212,0.12)",
                  backgroundColor: "rgba(255,255,255,0.02)",
                }}
              >
                <Box
                  component="img"
                  src={imgSrc}
                  alt={`hero-thumb-${idx}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    // subtle color grading, but keep detail
                    filter: "saturate(0.95) contrast(1.02) brightness(0.98)",
                    transition: "opacity 900ms ease, transform 900ms ease",
                    transform: reduceMotion ? "none" : "scale(1.02)",
                    // set full opacity inside frame so image looks crisp; the card container handles overall subtlety
                    opacity: 1,
                  }}
                />

                {/* very soft overlay to give photos a unified warm tint */}
                <Box
                  sx={{
                    position: "absolute",
                    inset: 0,
                    background:
                      "linear-gradient(180deg, rgba(243,226,212,0.0) 0%, rgba(243,226,212,0.06) 100%)",
                    pointerEvents: "none",
                  }}
                />
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* Decorative, soft-glow circle to keep original flavor */}
      <Box
        sx={{
          position: "absolute",
          top: "-18%",
          right: "-8%",
          width: { xs: 180, md: 300 },
          height: { xs: 180, md: 300 },
          bgcolor: "rgba(243,226,212,0.22)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
        }}
      />

      {/* CENTRAL CONTENT (logo / headings / CTA) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.25rem", md: "3.5rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.45)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={600}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "1rem", md: "1.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.24)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 10px 26px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave svg */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
