import {
  Box,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Stack,
} from '@mui/material'
import { projects } from '../data/projects'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'
import Slider from 'react-slick'

const Projects = () => (
  <Box
    id="projects"
    sx={{
      position: 'relative',
      backgroundColor: '#EFE6F0',
      color: '#17313E',
      pt: { xs: 4, md: 6 },
      pb: { xs: 10, md: 14 },
      px: { xs: 3, md: 8 },
      overflow: 'hidden',
    }}
  >
    <Typography
      variant="h3"
      sx={{
        fontWeight: 500,
        textAlign: 'center',
        mb: { xs: 5, md: 8 },
        color: '#5A3E61',
        letterSpacing: '0.5px',
      }}
    >
      Our Projects
    </Typography>

    <Slider
  dots
  infinite={projects.length > 2}
  speed={500}
  slidesToShow={2}
  slidesToScroll={1}
  arrows
  responsive={[
    {
      breakpoint: 960,
      settings: {
        slidesToShow: 1,
      },
    },
  ]}
>
  {projects.map((p, index) => (
    <Box
      key={p.title}
      sx={{ px: 2 }} // Padding between slides
    >
      <Card
        elevation={3}
        sx={{
          display: 'flex',
          flexDirection: {
            xs: 'column',
            md: index % 2 === 0 ? 'row' : 'row-reverse',
          },
          borderRadius: 5,
          overflow: 'hidden',
          backgroundColor: '#FFFFFF',
          maxWidth: '100%',
          margin: '0 auto',
        }}
      >
        <CardMedia
          component="img"
          image={p.image}
          alt={p.title}
          sx={{
            width: { xs: '100%', md: '40%' },
            height: { xs: 220, md: 320 },
            objectFit: 'cover',
          }}
        />

        <CardContent
          sx={{
            px: { xs: 3, md: 4 },
            py: { xs: 3, md: 4 },
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            width: { md: '60%' },
          }}
        >
          <Typography
            variant="h6"
            sx={{ fontWeight: 500, color: '#5A3E61', mb: 1 }}
          >
            {p.title}
          </Typography>

          <Stack direction="row" spacing={2} mb={2}>
            <Stack direction="row" spacing={1} alignItems="center">
              <CalendarMonthIcon fontSize="small" />
              <Typography variant="caption" color="#6E4D74">
                {p.date}
              </Typography>
            </Stack>
            <Stack direction="row" spacing={1} alignItems="center">
              <LocationOnIcon fontSize="small" />
              <Typography variant="caption" color="#6E4D74">
                {p.location}
              </Typography>
            </Stack>
          </Stack>

          <Box
            sx={{
              fontWeight: 300,
              color: '#3A2F3B',
              lineHeight: 1.6,
              fontSize: '0.85rem',
              maxHeight: { xs: 'auto', md: 200 },
              overflowY: 'auto',
            }}
            dangerouslySetInnerHTML={{ __html: p.description }}
          />
        </CardContent>
      </Card>
    </Box>
  ))}
</Slider>


    <Box
      component="svg"
      viewBox="0 0 1440 100"
      preserveAspectRatio="none"
      sx={{
        position: 'absolute',
        bottom: 0,
        left: 0,
        width: '100%',
        height: 80,
      }}
    >
      <path
        d="M0,0 C480,100 960,0 1440,100 L1440,100 L0,100 Z"
        fill="#F3E2D4"
      />
    </Box>
  </Box>
)

export default Projects
