import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const paneLayout = [
  { top: "6%", right: "6%", width: 240, height: 150, rotate: -7, z: 6 },
  { top: "20%", right: "14%", width: 280, height: 180, rotate: 5, z: 5 },
  { top: "44%", right: "8%", width: 210, height: 140, rotate: -3, z: 7 },
  { top: "62%", right: "18%", width: 250, height: 160, rotate: 4, z: 4 },
  { top: "78%", right: "6%", width: 200, height: 120, rotate: -10, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images for smooth swaps
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // respect reduced-motion
  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  // rotate index every 4s (slower if reduced motion)
  useEffect(() => {
    clearInterval(intervalRef.current);
    const delay = prefersReducedMotion ? 8000 : 4000;
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, delay);
    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep original gradient as the grounding layer
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* ---------- Decorative image cluster for md+ (subtle, frameless panes) ---------- */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          display: { xs: "none", md: "block" }, // cluster only on md+
        }}
      >
        {/* soft veil to integrate photos into gradient */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.02) 0%, rgba(20,30,40,0.06) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {paneLayout.map((p, paneIndex) => {
          const imgIdx = (index + paneIndex) % images.length;
          const src = images[imgIdx];

          return (
            <Box
              key={paneIndex}
              sx={{
                position: "absolute",
                top: p.top,
                right: p.right,
                width: p.width,
                height: p.height,
                transform: `rotate(${p.rotate}deg) translateZ(0)`,
                zIndex: p.z,
                borderRadius: 14,
                overflow: "hidden",
                boxShadow:
                  "0 12px 40px rgba(5,14,26,0.36), inset 0 1px 0 rgba(255,255,255,0.02)",
                border: "1px solid rgba(243,226,212,0.08)",
                transition:
                  "transform 600ms cubic-bezier(.2,.9,.2,1), opacity 600ms ease",
                opacity: 0.98,
                // tiny hover lift for those who can move
                ...(prefersReducedMotion
                  ? {}
                  : {
                      ":hover": {
                        transform: `rotate(${p.rotate}deg) translateY(-6px) scale(1.02)`,
                      },
                    }),
              }}
            >
              <Box
                component="img"
                src={src}
                alt="" // decorative; content still available elsewhere
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // make images decorative: subtle desat, warm tint, and reduced contrast
                  filter:
                    "grayscale(18%) saturate(78%) contrast(0.98) brightness(0.96)",
                  transition: "opacity 800ms ease, transform 12000ms linear",
                  transform: prefersReducedMotion ? "scale(1)" : "scale(1.03)",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* ---------- Cinematic strip for small screens (full-width, low-height) ---------- */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          left: "50%",
          transform: "translateX(-50%)",
          bottom: { xs: "8%", md: "12%" },
          width: { xs: "92%", md: "60%" },
          height: { xs: 120, md: 140 },
          borderRadius: { xs: 16, md: 18 },
          overflow: "hidden",
          zIndex: 1,
          display: { xs: "block", md: "none" }, // only show strip on xs
          boxShadow: "0 10px 30px rgba(3,12,24,0.28)",
          border: "1px solid rgba(243,226,212,0.08)",
          backgroundColor: "rgba(10,14,20,0.04)",
          pointerEvents: "none",
        }}
      >
        {/* layered images inside the strip, crossfading */}
        {images.map((src, i) => {
          const isActive = i === index;
          return (
            <Box
              key={src}
              component="img"
              src={src}
              alt=""
              sx={{
                position: "absolute",
                inset: 0,
                width: "100%",
                height: "100%",
                objectFit: "cover",
                opacity: isActive ? 0.96 : 0,
                transition: "opacity 800ms ease",
                filter:
                  "grayscale(20%) saturate(85%) contrast(0.96) brightness(0.92)",
                transform:
                  isActive && !prefersReducedMotion
                    ? "scale(1.03)"
                    : "scale(1)",
                transitionProperty: "opacity, transform",
                transitionDuration: "800ms, 12000ms",
                transitionTimingFunction: "ease-in-out, linear",
              }}
            />
          );
        })}
        {/* warm micro-overlay to keep text legible (very subtle) */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(90deg, rgba(65,94,114,0.08), rgba(65,94,114,0.02))",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />
      </Box>

      {/* ---------- soft decorative accent (big blurred circle) ---------- */}
      <Box
        sx={{
          position: "absolute",
          top: "-22%",
          right: "-10%",
          width: { xs: 180, md: 320 },
          height: { xs: 180, md: 340 },
          bgcolor: "rgba(243,226,212,0.20)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(18px)",
        }}
      />

      {/* ---------- Main content (logo, heading, subheading, CTA) ---------- */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: { xs: 1.5, md: 2 },
          filter: "drop-shadow(0 8px 22px rgba(0,0,0,0.36))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.5,
          textShadow: "2px 2px 14px rgba(0,0,0,0.36)",
          zIndex: 4,
          maxWidth: 900,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={680}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.20)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 22px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* ---------- bottom svg wave (original) ---------- */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
