import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const hoverRef = useRef(false);
  const touchRef = useRef(false);
  const { t } = useLanguage();

  // Preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Cycle postcards every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      if (!hoverRef.current && !touchRef.current) {
        setIndex((i) => (i + 1) % images.length);
      }
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  const handleMouseEnter = () => (hoverRef.current = true);
  const handleMouseLeave = () => (hoverRef.current = false);
  const handleTouchStart = () => (touchRef.current = true);
  const handleTouchEnd = () =>
    setTimeout(() => {
      touchRef.current = false;
    }, 350);

  const rotationFor = (i) => {
    const delta = i - index;
    const capped = Math.max(-2, Math.min(2, delta));
    return `${capped * 3}deg`;
  };

  return (
    <Box
      id="home"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onTouchStart={handleTouchStart}
      onTouchEnd={handleTouchEnd}
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative left orb */}
      <Box
        sx={{
          position: "absolute",
          left: { xs: -36, md: -70 },
          top: { xs: "-6%", md: "-14%" },
          width: { xs: 140, md: 260 },
          height: { xs: 140, md: 260 },
          bgcolor: "rgba(243,226,212,0.16)",
          borderRadius: "50%",
          zIndex: 0,
          filter: "blur(22px)",
          pointerEvents: "none",
        }}
      />

      {/* Main content */}
      <Box
        sx={{ position: "relative", zIndex: 3, width: "100%", maxWidth: 920 }}
      >
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 180, md: 260 },
            mb: { xs: 1.5, md: 2 },
            mx: "auto",
            filter: "drop-shadow(0 10px 28px rgba(6,24,44,0.28))",
          }}
        />
        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "1.8rem", md: "3rem" },
            color: "#F3E2D4",
            letterSpacing: "0.01em",
            mb: { xs: 0.5, md: 1.5 },
            textShadow: "2px 2px 10px rgba(0,0,0,0.38)",
            fontWeight: 800,
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>
        <Typography
          variant="h6"
          maxWidth={720}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "1rem", md: "1.12rem" },
            color: "rgba(243,226,212,0.95)",
            letterSpacing: "0.01em",
            lineHeight: 1.55,
            mb: { xs: 3, md: 4 },
            textShadow: "1px 1px 6px rgba(0,0,0,0.22)",
            mx: "auto",
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            mb: { xs: 3, md: 4 },
          }}
        >
          <Button
            endIcon={<ExpandMoreIcon />}
            variant="contained"
            size="large"
            sx={{
              backgroundColor: "#F3E2D4",
              color: "#415E72",
              px: { xs: 4, md: 5 },
              py: 1.5,
              borderRadius: "30px",
              fontFamily: "Inter, sans-serif",
              fontWeight: 700,
              textTransform: "none",
              boxShadow: "0 10px 30px rgba(6,24,44,0.18)",
              transition: "transform 0.28s ease, box-shadow 0.28s ease",
              "&:hover": {
                transform: "translateY(-3px)",
                boxShadow: "0 16px 48px rgba(6,24,44,0.24)",
                backgroundColor: "#e9dbd1",
              },
            }}
            onClick={() =>
              document
                .getElementById("about")
                ?.scrollIntoView({ behavior: "smooth" })
            }
          >
            {t("Learn more")}
          </Button>
        </Box>
      </Box>

      {/* Postcards */}
      <Box
        aria-hidden="true"
        sx={{
          zIndex: 4,
          width: "100%",
          display: "flex",
          justifyContent: { xs: "center", md: "flex-end" },
          px: { xs: 2, md: 6 },
          mb: { xs: 4, md: 0 },
          position: { xs: "relative", md: "absolute" },
          right: { md: 48 },
          top: { md: "50%" },
          transform: { md: "translateY(-50%)", xs: "translateY(0)" },
        }}
      >
        <Box
          sx={{
            display: "flex",
            flexDirection: { xs: "row", md: "column" },
            gap: { xs: 2.75, md: 2.5 },
            alignItems: "center",
            overflowX: { xs: "auto", md: "visible" },
            overflowY: "visible",
            px: { xs: 1, md: 0 },
            py: { xs: 1, md: 0 },
            "&::-webkit-scrollbar": { height: 8 },
            "&::-webkit-scrollbar-thumb": {
              background: "rgba(0,0,0,0.12)",
              borderRadius: 99,
            },
          }}
        >
          {images.map((src, i) => {
            const isActive = i === index;
            const width = {
              xs: isActive ? 280 : 220,
              md: isActive ? 180 : 140,
            };
            const height = {
              xs: isActive ? 200 : 160,
              md: isActive ? 120 : 96,
            };

            return (
              <Box
                key={src}
                onClick={() => setIndex(i)}
                sx={{
                  width,
                  height,
                  borderRadius: 2.5,
                  background: "#fff",
                  boxShadow: isActive
                    ? "0 32px 72px rgba(6,24,44,0.28)"
                    : "0 12px 28px rgba(6,24,44,0.16)",
                  transform: {
                    xs: isActive
                      ? "translateY(-6px) scale(1.03)"
                      : "translateY(0) scale(0.985)",
                    md: isActive
                      ? "translateX(0) rotate(0deg) scale(1.03)"
                      : `translateX(8px) rotate(${rotationFor(
                          i
                        )}) scale(0.975)`,
                  },
                  transition: "all 420ms cubic-bezier(.2,.9,.2,1)",
                  display: "flex",
                  flexDirection: "column",
                  overflow: "visible",
                  border: "1px solid rgba(6,24,44,0.06)",
                  cursor: { xs: "pointer", md: "default" },
                  position: "relative",
                  scrollSnapAlign: { xs: "center", md: "none" },
                }}
              >
                <Box
                  component="img"
                  src={src}
                  alt={`postcard-${i}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    borderRadius: 3,
                    filter: isActive
                      ? "saturate(1) contrast(1.04)"
                      : "grayscale(0.35) contrast(0.97) opacity(0.95)",
                    transition: "filter 360ms ease, transform 360ms ease",
                  }}
                />
              </Box>
            );
          })}
        </Box>
      </Box>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
