import { AppBar, Toolbar, Button, Box } from '@mui/material'
import Logo from '../assets/logo2.png'

const Navbar = () => {
  const scrollTo = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <AppBar
      position="sticky"
      elevation={6}
      sx={{
        backgroundColor: '#17313E',
        px: { xs: 2, md: 4 },
        py: { xs: 0.75, md: 1 },
        boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
        backdropFilter: 'saturate(180%) blur(8px)',
      }}
    >
      <Toolbar
        disableGutters
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          height: { xs: 56, md: 64 },
          maxWidth: 1200,
          mx: 'auto',
        }}
      >
        {/* Logo */}
        <Box
          onClick={() => scrollTo('home')}
          sx={{
            display: 'flex',
            alignItems: 'center',
            cursor: 'pointer',
            transition: 'transform 0.3s ease',
            '&:hover': { transform: 'scale(1.05) rotate(-3deg)' },
          }}
        >
          <Box
            component="img"
            src={Logo}
            alt="Logo"
            sx={{
              height: { xs:  Forty, md:  Fifty }, // 40px on mobile, 50px on desktop
              userSelect: 'none',
              filter: 'drop-shadow(0 0 4px rgba(255,255,255,0.2))',
              borderRadius: 1,
            }}
          />
        </Box>

        {/* Navigation */}
        <Box sx={{ display: 'flex', gap: { xs: 1.5, md: 3 } }}>
          {['about', 'projects', 'feedback'].map((id) => (
            <Button
              key={id}
              onClick={() => scrollTo(id)}
              sx={{
                color: '#F3E2D4',
                textTransform: 'none',
                fontWeight: 600,
                letterSpacing: '0.08em',
                fontSize: { xs: '0.8rem', md: '0.9rem' },
                minWidth: 0,
                px: 1.5,
                py: 0.5,
                borderRadius: 2,
                transition: 'all 0.25s ease',
                '&:hover': {
                  backgroundColor: '#415E72',
                  boxShadow: '0 4px 12px rgba(65,94,114,0.6)',
                  transform: 'translateY(-2px)',
                },
              }}
            >
              {id[0].toUpperCase() + id.slice(1)}
            </Button>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  )
}

export default Navbar
