import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

/**
 * Discreet hero:
 * - images are shown inside large masked shapes (circle, diagonal panel, angled band)
 * - images are desaturated + low opacity so they read as visual texture rather than background photos
 * - subtle slow zoom + 1s crossfade every 4s
 */
const shapeStyles = [
  // left angled panel
  {
    clipPath: "polygon(0 0, 55% 0, 45% 100%, 0 100%)",
    left: 0,
    top: 0,
    width: "70%",
    height: "100%",
    transformOrigin: "center",
  },
  // soft circle (top-right)
  {
    clipPath: "circle(42% at 85% 18%)",
    left: 0,
    top: 0,
    width: "100%",
    height: "100%",
    transformOrigin: "center",
  },
  // diagonal band (bottom-right)
  {
    clipPath: "polygon(45% 0, 100% 0, 100% 100%, 30% 100%)",
    left: 0,
    top: 0,
    width: "100%",
    height: "100%",
    transformOrigin: "center",
  },
  // large subtle vignette center-left
  {
    clipPath: "ellipse(50% 60% at 25% 60%)",
    left: 0,
    top: 0,
    width: "100%",
    height: "100%",
    transformOrigin: "center",
  },
  // top-left soft rounded rect
  {
    clipPath: "polygon(0 0, 55% 0, 45% 65%, 0 65%)",
    left: 0,
    top: 0,
    width: "70%",
    height: "100%",
    transformOrigin: "center",
  },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient as the base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative image layer (discrete masked shapes) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {images.map((src, i) => {
          // choose a shape for each image (cycles through shapeStyles)
          const shape = shapeStyles[i % shapeStyles.length];
          const isActive = i === index;

          return (
            <Box
              key={src}
              sx={{
                position: "absolute",
                left: shape.left,
                top: shape.top,
                width: shape.width,
                height: shape.height,
                clipPath: shape.clipPath,
                WebkitClipPath: shape.clipPath,
                // softly fade edges with a mask so shapes feel airy
                maskImage:
                  "radial-gradient(circle at 30% 30%, rgba(0,0,0,1) 0%, rgba(0,0,0,0.85) 25%, rgba(0,0,0,0.5) 55%, rgba(0,0,0,0) 85%)",
                WebkitMaskImage:
                  "-webkit-radial-gradient(circle at 30% 30%, rgba(0,0,0,1) 0%, rgba(0,0,0,0.85) 25%, rgba(0,0,0,0.5) 55%, rgba(0,0,0,0) 85%)",
                transform: isActive ? "scale(1.025)" : "scale(1)",
                transition:
                  "opacity 1000ms ease-in-out, transform 8000ms linear, filter 1000ms ease-in-out",
                opacity: isActive ? 0.22 : 0, // discreet: visible but subtle
                // visual treatment: desaturate, gentle contrast, slightly muted
                // NO heavy blur — images remain crisp but subtle
                filter:
                  "grayscale(70%) saturate(0.8) contrast(0.95) brightness(0.95)",
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`bg-${i}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // ensure the image follows the parent transform timing for smooth Ken Burns
                  transformOrigin: shape.transformOrigin || "center",
                }}
              />
            </Box>
          );
        })}

        {/* a gentle color wash on top of images so they read as texture (keeps text readable) */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 1,
            pointerEvents: "none",
            background:
              "linear-gradient(180deg, rgba(65,94,114,0.18) 0%, rgba(65,94,114,0.32) 60%, rgba(25,34,42,0.45) 100%)",
            mixBlendMode: "multiply",
          }}
        />
      </Box>

      {/* Decorative blurred circle remains but reduced intensity so it doesn't compete */}
      <Box
        sx={{
          position: "absolute",
          top: "-22%",
          right: "-8%",
          width: { xs: 160, md: 260 },
          height: { xs: 160, md: 260 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(10px)",
        }}
      />

      {/* Logo + text (foreground) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.1rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.5,
          textShadow: "2px 2px 10px rgba(0,0,0,0.45)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.1rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.2)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.16)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 22px rgba(0,0,0,0.26)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
