import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const [prevIndex, setPrevIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // Respect reduced motion
  const prefersReducedMotion =
    typeof window !== "undefined"
      ? window.matchMedia &&
        window.matchMedia("(prefers-reduced-motion: reduce)").matches
      : false;

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s; store prevIndex for smooth fades
  useEffect(() => {
    if (prefersReducedMotion) return; // don't animate if user prefers reduced motion

    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setPrevIndex((i) => i);
      setIndex((i) => (i + 1) % images.length);
    }, 4000);

    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  // utility: get 3 indices for the collage stack starting at base
  const getStackIndices = (base) => {
    const len = images.length;
    return [0, 1, 2].map((offset) => (base + offset) % len);
  };

  const currStack = getStackIndices(index);
  const prevStack = getStackIndices(prevIndex);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient as the primary background
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative stacked photos (bottom-left) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          left: { xs: 12, md: 40 },
          bottom: { xs: 24, md: 48 },
          zIndex: 3,
          width: { xs: 160, md: 260 },
          height: { xs: 220, md: 320 },
          pointerEvents: "none",
          display: "flex",
          alignItems: "flex-end",
          justifyContent: "center",
        }}
      >
        {/* Each slot is a 'polaroid' card. We render prev + current to create a soft fade */}
        {[0, 1, 2].map((slot) => {
          const prevSrc = images[prevStack[slot]];
          const currSrc = images[currStack[slot]];

          // style variants per slot (staggered rotation & offsets)
          const slotStyles = [
            {
              transform: "rotate(-8deg) translate(-8px, 6px)",
              z: 1,
              bottomOffset: 12,
            },
            {
              transform: "rotate(2deg) translate(0px, 0px)",
              z: 2,
              bottomOffset: 0,
            },
            {
              transform: "rotate(8deg) translate(8px, -8px)",
              z: 1,
              bottomOffset: -12,
            },
          ][slot];

          return (
            <Box
              key={slot}
              sx={{
                position: "absolute",
                width: { xs: 120, md: 180 },
                height: { xs: 160, md: 220 },
                bottom: `${slotStyles.bottomOffset}px`,
                left: `${slot * 12}px`,
                transform: slotStyles.transform,
                zIndex: slotStyles.z,
                transition: prefersReducedMotion
                  ? "none"
                  : "transform 700ms ease",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              {/* Previous image layer: fades out */}
              <Box
                component="img"
                src={prevSrc}
                alt={`photo-prev-${slot}`}
                sx={{
                  position: "absolute",
                  inset: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  borderRadius: 2,
                  // Polaroid-like frame
                  boxShadow: "0 10px 30px rgba(10,20,30,0.25)",
                  border: "6px solid rgba(243,226,212,0.95)",
                  transform: "scale(1)",
                  // stylistic desaturation and lowered contrast to keep them discrete
                  filter:
                    "saturate(0.75) brightness(0.95) contrast(0.98) grayscale(0.05)",
                  opacity: 0, // start hidden; will transition out
                  transition: prefersReducedMotion
                    ? "none"
                    : "opacity 900ms ease",
                }}
              />

              {/* Current image layer: fades in */}
              <Box
                component="img"
                src={currSrc}
                alt={`photo-${slot}`}
                sx={{
                  position: "absolute",
                  inset: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  borderRadius: 2,
                  boxShadow: "0 10px 30px rgba(10,20,30,0.28)",
                  border: "6px solid rgba(243,226,212,0.95)",
                  transform: "scale(1)",
                  filter:
                    "saturate(0.75) brightness(0.95) contrast(0.98) grayscale(0.05)",
                  opacity: 1,
                  transition: prefersReducedMotion
                    ? "none"
                    : "opacity 900ms ease",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* Faint warm vignette to gently separate photos & keep hero readable */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          background:
            "radial-gradient(1200px 600px at 25% 75%, rgba(243,226,212,0.06), rgba(243,226,212,0) 40%), linear-gradient(180deg, rgba(65,94,114,0.06) 0%, rgba(25,34,42,0.14) 100%)",
        }}
      />

      {/* Decorative soft circle (keeps original motif) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 200, md: 300 },
          height: { xs: 200, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 2,
          filter: "blur(24px)",
        }}
      />

      {/* Logo */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.4))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.25rem", md: "3.5rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 10px rgba(0,0,0,0.4)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={600}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "1rem", md: "1.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.25)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* Bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
