import React, { useEffect, useState, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // slideshow (4s)
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your initial gradient as the base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* BACKGROUND LAYER: very discrete image panels */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {images.map((src, i) => {
          const isActive = i === index;

          // subtle angle and offset so panels don't feel identical every cycle
          const angle = (i * 14 + index * 3) % 360;

          return (
            <Box
              key={`${src}-${i}`}
              component="img"
              src={src}
              alt={`background-${i}`}
              sx={{
                position: "absolute",
                // show panels more on the sides for a decorative, discrete look
                left: i % 2 === 0 ? "-10%" : "10%",
                top: `${-6 + ((i * 6) % 12)}%`,
                width: "120%", // larger so angled crop still feels full
                height: "120%",
                objectFit: "cover",
                transform: isActive
                  ? `translateZ(0) scale(1.03) rotate(${angle}deg)`
                  : `translateZ(0) scale(1.02) rotate(${angle - 3}deg)`,
                transition:
                  "opacity 900ms ease-in-out, transform 6000ms ease-in-out",
                // make photos discrete: desaturated, slightly muted, low opacity
                opacity: isActive ? 0.22 : 0,
                filter:
                  "grayscale(75%) saturate(0.6) contrast(0.95) blur(0.5px)",
                mixBlendMode: "overlay",
                borderRadius: "14px",
                boxShadow: isActive ? "0 40px 80px rgba(6,12,34,0.22)" : "none",
                // give them a soft tilted mask using clip-path (angled card)
                clipPath: "polygon(6% 6%, 94% 2%, 100% 94%, 4% 100%)",
                // small transform origin so scale feels natural
                transformOrigin: i % 2 === 0 ? "left center" : "right center",
                willChange: "opacity, transform",
              }}
            />
          );
        })}

        {/* Warm frosted sheet to mute photos further — keeps text readable */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 1,
            pointerEvents: "none",
            // a subtle translucent sheet with a soft diagonal gradient
            background:
              "linear-gradient(120deg, rgba(243,226,212,0.12) 10%, rgba(65,94,114,0.14) 60%)",
            mixBlendMode: "normal",
          }}
        />

        {/* soft vignette to draw attention to center */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 2,
            pointerEvents: "none",
            background:
              "radial-gradient(circle at 50% 45%, rgba(0,0,0,0.0) 25%, rgba(0,0,0,0.18) 100%)",
            mixBlendMode: "multiply",
          }}
        />
      </Box>

      {/* Decorative translucent frosted cards (layered) — contribute to discreet look */}
      <Box
        sx={{
          position: "absolute",
          zIndex: 3,
          pointerEvents: "none",
          inset: "12% 6% auto 6%",
          display: "flex",
          gap: 2,
          justifyContent: "center",
          alignItems: "center",
          transform: "translateY(-6%)",
        }}
      >
        {/* left small frosted card */}
        <Box
          sx={{
            width: { xs: 90, md: 140 },
            height: { xs: 120, md: 170 },
            borderRadius: 3,
            bgcolor: "rgba(243,226,212,0.06)",
            backdropFilter: "blur(6px)",
            boxShadow: "0 10px 24px rgba(6,12,34,0.12)",
            transform: "rotate(-6deg)",
          }}
        />

        {/* center large frosted card */}
        <Box
          sx={{
            width: { xs: 140, md: 240 },
            height: { xs: 170, md: 260 },
            borderRadius: 4,
            bgcolor: "rgba(243,226,212,0.08)",
            backdropFilter: "blur(8px)",
            boxShadow: "0 18px 48px rgba(6,12,34,0.14)",
            transform: "rotate(2deg)",
          }}
        />

        {/* right small frosted card */}
        <Box
          sx={{
            width: { xs: 90, md: 140 },
            height: { xs: 120, md: 170 },
            borderRadius: 3,
            bgcolor: "rgba(243,226,212,0.06)",
            backdropFilter: "blur(6px)",
            boxShadow: "0 10px 24px rgba(6,12,34,0.12)",
            transform: "rotate(8deg)",
          }}
        />
      </Box>

      {/* small decorative blurred circle (keeps original concept but subtler) */}
      <Box
        sx={{
          position: "absolute",
          top: "-22%",
          right: "-6%",
          width: { xs: 160, md: 260 },
          height: { xs: 160, md: 260 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 4,
          filter: "blur(14px)",
        }}
      />

      {/* CONTENT */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.36))",
          position: "relative",
          zIndex: 5,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.25rem", md: "3.5rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 10px rgba(0,0,0,0.45)",
          zIndex: 5,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={600}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "1rem", md: "1.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.28)",
          zIndex: 5,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 5,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 6,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>

      {/* Respect prefers-reduced-motion: stop animations */}
      <style>
        {`@media (prefers-reduced-motion: reduce) {
            #home img { transition: none !important; transform: none !important; }
            #home * { transition: none !important; animation: none !important; }
         }`}
      </style>
    </Box>
  );
};

export default Hero;
