import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const hoverRef = useRef(false);
  const touchRef = useRef(false);
  const { t } = useLanguage();

  // Preload images for smooth transitions
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Cycle postcards every 4s; pause on hover or touch
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      if (!hoverRef.current && !touchRef.current) {
        setIndex((i) => (i + 1) % images.length);
      }
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // pause handlers (desktop)
  const handleMouseEnter = () => {
    hoverRef.current = true;
  };
  const handleMouseLeave = () => {
    hoverRef.current = false;
  };

  // touch handlers (mobile)
  const handleTouchStart = () => {
    touchRef.current = true;
  };
  const handleTouchEnd = () => {
    // small delay to avoid immediately resuming while user is still interacting
    setTimeout(() => {
      touchRef.current = false;
    }, 350);
  };

  // helper: subtle rotation degrees so stack feels natural (mobile-first small rotations)
  const rotationFor = (i) => {
    const delta = i - index;
    // normalize delta to -2..2 range for nicer rotation
    const capped = Math.max(-2, Math.min(2, delta));
    return `${capped * 3}deg`;
  };

  return (
    <Box
      id="home"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onTouchStart={handleTouchStart}
      onTouchEnd={handleTouchEnd}
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // base gradient background (keeps identity)
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative left paper orb for balance */}
      <Box
        sx={{
          position: "absolute",
          left: { xs: -36, md: -70 },
          top: { xs: "-6%", md: "-14%" },
          width: { xs: 140, md: 260 },
          height: { xs: 140, md: 260 },
          bgcolor: "rgba(243,226,212,0.16)",
          borderRadius: "50%",
          zIndex: 0,
          filter: "blur(22px)",
          pointerEvents: "none",
        }}
      />

      {/* Main content */}
      <Box sx={{ position: "relative", zIndex: 3, width: "100%", maxWidth: 920 }}>
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 160, md: 240 },
            mb: { xs: 1.5, md: 2 },
            mx: "auto",
            filter: "drop-shadow(0 10px 28px rgba(6,24,44,0.28))",
          }}
        />

        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "1.6rem", md: "3rem" },
            color: "#F3E2D4",
            letterSpacing: "0.01em",
            mb: { xs: 0.5, md: 1.5 },
            textShadow: "2px 2px 10px rgba(0,0,0,0.38)",
            fontWeight: 800,
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>

        <Typography
          variant="h6"
          maxWidth={720}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "0.95rem", md: "1.08rem" },
            color: "rgba(243,226,212,0.95)",
            letterSpacing: "0.01em",
            lineHeight: 1.55,
            mb: { xs: 3, md: 4 },
            textShadow: "1px 1px 6px rgba(0,0,0,0.22)",
            mx: "auto",
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        {/* CTA */}
        <Box sx={{ display: "flex", justifyContent: "center", mb: { xs: 3, md: 4 } }}>
          <Button
            endIcon={<ExpandMoreIcon />}
            variant="contained"
            size="large"
            sx={{
              backgroundColor: "#F3E2D4",
              color: "#415E72",
              px: { xs: 4, md: 5 },
              py: 1.5,
              borderRadius: "30px",
              fontFamily: "Inter, sans-serif",
              fontWeight: 700,
              textTransform: "none",
              boxShadow: "0 10px 30px rgba(6,24,44,0.18)",
              transition: "transform 0.28s ease, box-shadow 0.28s ease",
              "&:hover": {
                transform: "translateY(-3px)",
                boxShadow: "0 16px 48px rgba(6,24,44,0.24)",
                backgroundColor: "#e9dbd1",
              },
            }}
            onClick={() =>
              document.getElementById("about")?.scrollIntoView({ behavior: "smooth" })
            }
          >
            {t("Learn more")}
          </Button>
        </Box>
      </Box>

      {/* Postcards area: mobile-first responsive */}
      <Box
        aria-hidden="true"
        sx={{
          zIndex: 4,
          width: "100%",
          // mobile: a centered horizontal scroll with nice spacing; desktop: vertical stack on right
          display: "flex",
          justifyContent: { xs: "center", md: "flex-end" },
          px: { xs: 2, md: 6 },
          mb: { xs: 4, md: 0 },
          // ensure postcards area sits above main content while keeping balance
          position: { xs: "relative", md: "absolute" },
          right: { md: 48 },
          top: { md: "50%" },
          transform: { md: "translateY(-50%)", xs: "translateY(0)" },
        }}
      >
        {/* On mobile: a horizontally scrollable rail (postcards) */}
        <Box
          sx={{
            display: "flex",
            flexDirection: { xs: "row", md: "column" },
            gap: { xs: 2.25, md: 2 },
            alignItems: "center",
            overflowX: { xs: "auto", md: "visible" },
            overflowY: "visible",
            px: { xs: 1, md: 0 },
            py: { xs: 1, md: 0 },
            // hide native scrollbar in a tasteful way
            "&::-webkit-scrollbar": {
              height: 8,
            },
            "&::-webkit-scrollbar-thumb": {
              background: "rgba(0,0,0,0.12)",
              borderRadius: 99,
            },
          }}
        >
          {images.map((src, i) => {
            const isActive = i === index;

            // sizes: mobile cards larger for touch; desktop cards smaller to be decorative
            const width = { xs: isActive ? 240 : 200, md: isActive ? 160 : 128 };
            const height = { xs: isActive ? 170 : 140, md: isActive ? 110 : 88 };

            return (
              <Box
                key={src}
                onClick={() => {
                  // make cards interactive on touch: focus that postcard
                  setIndex(i);
                }}
                sx={{
                  width,
                  height,
                  borderRadius: 2,
                  background: "#fff",
                  boxShadow: isActive
                    ? "0 28px 64px rgba(6,24,44,0.28)"
                    : "0 10px 26px rgba(6,24,44,0.16)",
                  transform: {
                    xs: isActive ? "translateY(-6px) scale(1.02)" : "translateY(0) scale(0.99)",
                    md: isActive ? "translateX(0) rotate(0deg) scale(1.02)" : `translateX(8px) rotate(${rotationFor(i)}) scale(0.98)`,
                  },
                  transition: "all 420ms cubic-bezier(.2,.9,.2,1)",
                  display: "flex",
                  flexDirection: "column",
                  overflow: "visible",
                  border: "1px solid rgba(6,24,44,0.06)",
                  cursor: { xs: "pointer", md: "default" },
                  // Polaroid-like frame: white paper border with small caption area
                  position: "relative",
                  // allow horizontal scroll snapping feel
                  scrollSnapAlign: { xs: "center", md: "none" },
                }}
              >
                {/* Image area */}
                <Box
                  component="img"
                  src={src}
                  alt={`postcard-${i}`}
                  sx={{
                    width: "100%",
                    height: `calc(100% - 34px)`, // leave space for caption below (polaroid)
                    objectFit: "cover",
                    display: "block",
                    borderTopLeftRadius: 6,
                    borderTopRightRadius: 6,
                    filter: isActive ? "saturate(1) contrast(1.04)" : "grayscale(0.4) contrast(0.96) opacity(0.95)",
                    transition: "filter 360ms ease, transform 360ms ease",
                  }}
                />

                {/* caption area (polaroid bottom) */}
                <Box
                  sx={{
                    height: 34,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    px: 1,
                    fontSize: 12,
                    color: "rgba(6,24,44,0.8)",
                    background: "linear-gradient(180deg, rgba(255,255,255,0.98), rgba(250,250,250,0.98))",
                    borderBottomLeftRadius: 6,
                    borderBottomRightRadius: 6,
                    boxShadow: "inset 0 1px 0 rgba(255,255,255,0.6)",
                  }}
                >
                  <Box sx={{ fontWeight: 600 }}>{`#${i + 1}`}</Box>
                  <Box sx={{ opacity: 0.65, fontSize: 11 }}>{isActive ? "Featured" : "Explore"}</Box>
                </Box>

                {/* tiny decorative pin on the top-left of active postcard (desktop only) */}
                {isActive && (
                  <Box
                    sx={{
                      display: { xs: "none", md: "block" },
                      position: "absolute",
                      top: -8,
                      left: -8,
                      width: 14,
                      height: 14,
                      bgcolor: "rgba(255,255,255,0.9)",
                      borderRadius: "50%",
                      boxShadow: "0 4px 10px rgba(6,24,44,0.15)",
                      transform: "rotate(18deg)",
                      zIndex: 6,
                    }}
                  />
                )}
              </Box>
            );
          })}
        </Box>
      </Box>

      {/* subtle bottom wave (keeps original look) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
