import { Box, Typography, Grid, Card, CardMedia, CardContent, Chip, Divider } from '@mui/material'
import { projects } from '../data/projects'

const Projects = () => (
  <Box
    id="projects"
    sx={{
      position: 'relative',
      background: 'linear-gradient(120deg, #EADAE3 0%, #C5B0CD 100%)',
      color: '#17313E',
    }}
  >
    <Box sx={{ position: 'relative', zIndex: 1, py: 10, px: { xs: 3, md: 10 } }}>
      <Typography
        variant="h4"
        sx={{
          fontWeight: 400,
          textAlign: 'center',
          mb: 4,
          color: '#415E72',
        }}
      >
        Our Projects
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {projects.map((p) => (
          <Grid item xs={12} sm={6} md={4} key={p.title}>
            <Card
              elevation={3}
              sx={{
                maxWidth: 320,
                margin: '0 auto',
                borderRadius: 3,
                display: 'flex',
                flexDirection: 'column',
                transition: 'transform 0.3s ease',
                '&:hover': { transform: 'translateY(-8px)' },
              }}
            >
              <CardMedia
                component="img"
                image={p.image}
                alt={p.title}
                sx={{
                  height: 330,
                  objectFit: 'cover',
                  borderTopLeftRadius: 12,
                  borderTopRightRadius: 12,
                }}
              />

              <CardContent sx={{ px: 3, py: 2.5 }}>
                <Typography
                  variant="h6"
                  sx={{
                    fontSize: '1.25rem',
                    color: '#415E72',
                    mb: 1,
                  }}
                >
                  {p.title}
                </Typography>

                <Typography
                  variant="caption"
                  sx={{
                    display: 'block',
                    fontWeight: 600,
                    color: '#17313E',
                    mb: 1,
                  }}
                >
                  {p.date} â€” {p.location}
                </Typography>

                <Typography
                  variant="body2"
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    color: '#415E72',
                    lineHeight: 1.5,
                    mt: 1,
                    display: '-webkit-box',
                    WebkitLineClamp: 5,
                    WebkitBoxOrient: 'vertical',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    minHeight: '7.5em',
                  }}
                >
                  {p.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  </Box>
)

export default Projects
