import { Box, Typography, Grid, Card, CardMedia, CardContent, Chip, Stack } from '@mui/material'
import { projects } from '../data/projects'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const Projects = () => (
  <Box
    id="projects"
    sx={{
      position: 'relative',
      background: 'linear-gradient(135deg, #EFE6F0 0%, #D4C1D6 100%)',
      color: '#17313E',
      py: 12,
      px: { xs: 3, md: 10 },
    }}
  >
    <Typography
      variant="h3"
      sx={{
        fontWeight: 600,
        textAlign: 'center',
        mb: 8,
        color: '#5A3E61',
        letterSpacing: '0.5px',
        fontFamily: 'DM Serif Display',
      }}
    >
      Our Projects
    </Typography>

    <Grid container spacing={6} justifyContent="center">
      {projects.map((p, index) => (
        <Grid item xs={12} md={10} key={p.title}>
          <Card
            elevation={0}
            sx={{
              display: 'flex',
              flexDirection: index % 2 === 0 ? 'row' : 'row-reverse',
              borderRadius: 6,
              overflow: 'hidden',
              boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
              backgroundColor: '#FFFFFF',
            }}
          >
            <CardMedia
              component="img"
              image={p.image}
              alt={p.title}
              sx={{
                width: { xs: '100%', md: '50%' },
                height: 320,
                objectFit: 'cover',
              }}
            />

            <CardContent
              sx={{
                px: { xs: 3, md: 5 },
                py: { xs: 4, md: 5 },
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
              }}
            >
              <Typography
                variant="h5"
                sx={{
                  fontWeight: 600,
                  color: '#5A3E61',
                  mb: 1,
                  fontFamily: 'DM Serif Display',
                }}
              >
                {p.title}
              </Typography>

              <Stack direction="row" spacing={2} mb={2}>
                <Stack direction="row" spacing={1} alignItems="center">
                  <CalendarMonthIcon fontSize="small" />
                  <Typography variant="body2" color="#6E4D74">
                    {p.date}
                  </Typography>
                </Stack>
                <Stack direction="row" spacing={1} alignItems="center">
                  <LocationOnIcon fontSize="small" />
                  <Typography variant="body2" color="#6E4D74">
                    {p.location}
                  </Typography>
                </Stack>
              </Stack>

              <Typography
                variant="body2"
                sx={{
                  fontWeight: 300,
                  color: '#3A2F3B',
                  lineHeight: 1.7,
                }}
              >
                {p.description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  </Box>
)

export default Projects
