import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const hoverRef = useRef(false);
  const { t } = useLanguage();

  // Preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Start cycling every 4s; respect pause-on-hover
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      if (!hoverRef.current) {
        setIndex((i) => (i + 1) % images.length);
      }
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // handlers for pause on hover
  const handleMouseEnter = () => {
    hoverRef.current = true;
  };
  const handleMouseLeave = () => {
    hoverRef.current = false;
  };

  return (
    <Box
      id="home"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep original gradient as base / fallback background
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Right-side discrete collage */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          right: { xs: 12, md: 48 },
          top: "50%",
          transform: "translateY(-50%)",
          zIndex: 1,
          display: "flex",
          flexDirection: "column",
          gap: { xs: 1.25, md: 2 },
          alignItems: "flex-end",
          pointerEvents: "none", // decorative only
        }}
      >
        {/* Stylized vertical stack — cards overlap slightly for a modern layered look */}
        {images.map((src, i) => {
          const isActive = i === index;
          // card sizes: active is larger
          const size = isActive
            ? { width: 160, height: 110 }
            : { width: 120, height: 84 };
          return (
            <Box
              key={src}
              sx={{
                width: size.width,
                height: size.height,
                borderRadius: 2.5,
                overflow: "hidden",
                boxShadow: isActive
                  ? "0 18px 40px rgba(6,24,44,0.35)"
                  : "0 8px 20px rgba(6,24,44,0.18)",
                transform: isActive
                  ? "translateX(0) scale(1.03)"
                  : "translateX(8px) scale(0.98)",
                transition: "all 420ms cubic-bezier(.2,.9,.2,1)",
                border: isActive
                  ? "1px solid rgba(255,255,255,0.12)"
                  : "1px solid rgba(255,255,255,0.06)",
                backgroundColor: "rgba(255,255,255,0.02)",
                display: "block",
                position: "relative",
                // layered offset to create a stack feeling
                mr: isActive ? 0 : 1,
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`hero-thumb-${i}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // discrete photo treatment:
                  // subtle desaturation and reduced opacity for non-active, full color for active
                  filter: isActive
                    ? "saturate(1) contrast(1.03)"
                    : "grayscale(0.5) contrast(0.95) opacity(0.75)",
                  transition:
                    "filter 420ms ease, opacity 420ms ease, transform 420ms ease",
                }}
              />
              {/* small caption badge (decorative) */}
              <Box
                sx={{
                  position: "absolute",
                  left: 8,
                  bottom: 8,
                  px: 0.75,
                  py: 0.35,
                  bgcolor: "rgba(0,0,0,0.45)",
                  color: "#fff",
                  fontSize: 11,
                  borderRadius: 1,
                  pointerEvents: "none",
                  opacity: isActive ? 1 : 0.6,
                }}
              >
                {`${i + 1} / ${images.length}`}
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* Decorative soft orb (left) to balance composition */}
      <Box
        sx={{
          position: "absolute",
          left: { xs: -40, md: -60 },
          top: { xs: "-10%", md: "-14%" },
          width: { xs: 160, md: 260 },
          height: { xs: 160, md: 260 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 0,
          filter: "blur(24px)",
          pointerEvents: "none",
        }}
      />

      {/* Main content sits above collage */}
      <Box sx={{ position: "relative", zIndex: 3 }}>
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 180, md: 260 },
            mb: 2,
            filter: "drop-shadow(0 8px 22px rgba(0,0,0,0.36))",
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        />

        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "2rem", md: "3.2rem" },
            color: "#F3E2D4",
            letterSpacing: "0.02em",
            mb: 1.5,
            textShadow: "2px 2px 12px rgba(0,0,0,0.4)",
            fontWeight: 700,
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>

        <Typography
          variant="h6"
          maxWidth={640}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "0.98rem", md: "1.12rem" },
            color: "rgba(243,226,212,0.95)",
            letterSpacing: "0.01em",
            lineHeight: 1.6,
            mb: 4,
            textShadow: "1px 1px 8px rgba(0,0,0,0.24)",
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        <Button
          endIcon={<ExpandMoreIcon />}
          variant="contained"
          size="large"
          sx={{
            backgroundColor: "#F3E2D4",
            color: "#415E72",
            px: { xs: 4, md: 5 },
            py: 1.6,
            borderRadius: "30px",
            fontFamily: "Inter, sans-serif",
            fontWeight: 600,
            textTransform: "none",
            boxShadow: "0 8px 26px rgba(6,24,44,0.22)",
            transition: "transform 0.28s ease, box-shadow 0.28s ease",
            "&:hover": {
              transform: "translateY(-3px)",
              boxShadow: "0 12px 40px rgba(6,24,44,0.28)",
              backgroundColor: "#e9dbd1",
            },
          }}
          onClick={() =>
            document
              .getElementById("about")
              ?.scrollIntoView({ behavior: "smooth" })
          }
        >
          {t("Learn more")}
        </Button>
      </Box>

      {/* subtle decorative bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
