import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button, useTheme } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania3.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

// layout positions for each card (tweakable)
const cardPositions = [
  { top: { xs: "6%", md: "6%" }, left: { xs: "6%", md: "6%" }, w: { xs: "36%", md: "28%" }, h: { xs: "26%", md: "36%" }, rotate: "-6deg" },
  { top: { xs: "6%", md: "8%" }, right: { xs: "6%", md: "8%" }, w: { xs: "40%", md: "30%" }, h: { xs: "28%", md: "34%" }, rotate: "4deg" },
  { top: { xs: "32%", md: "22%" }, left: { xs: "8%", md: "18%" }, w: { xs: "30%", md: "22%" }, h: { xs: "32%", md: "40%" }, rotate: "2deg" },
  { bottom: { xs: "10%", md: "12%" }, right: { xs: "8%", md: "18%" }, w: { xs: "36%", md: "28%" }, h: { xs: "28%", md: "36%" }, rotate: "-3deg" },
  { top: { xs: "38%", md: "30%" }, right: { xs: "28%", md: "40%" }, w: { xs: "26%", md: "20%" }, h: { xs: "22%", md: "28%" }, rotate: "6deg" },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();
  const theme = useTheme();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // autoplay highlight every 4s
  useEffect(() => {
    // respect user's reduced motion preference
    const prefersReduced = window.matchMedia && window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    if (prefersReduced) return; // do not animate

    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient as base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative image-cards collage (behind content) */}
      <Box
        aria-hidden
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* subtle textured vignette to tie photos to background without heavy blur */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "radial-gradient(60% 50% at 50% 35%, rgba(243,226,212,0.06), rgba(25,34,42,0.18) 70%)",
            zIndex: 1,
            pointerEvents: "none",
          }}
        />

        {images.map((src, i) => {
          const pos = cardPositions[i] || {};
          const isActive = i === index;

          return (
            <Box
              key={src}
              sx={{
                position: "absolute",
                zIndex: 2 + i,
                // responsive size & position spread from definitions
                width: pos.w,
                height: pos.h,
                top: pos.top,
                bottom: pos.bottom,
                left: pos.left,
                right: pos.right,
                transform: `rotate(${pos.rotate || "0deg"})`,
                transition: "opacity 700ms ease, transform 700ms ease, filter 700ms ease",
                // subtle base style: slightly desaturated and low opacity for discreteness
                opacity: isActive ? 0.95 : 0.28,
                filter: isActive ? "grayscale(0%) saturate(1.02) contrast(1.03)" : "grayscale(40%) saturate(0.7) contrast(0.95) blur(0px)",
                borderRadius: 3,
                overflow: "hidden",
                boxShadow: isActive
                  ? "0 18px 40px rgba(8,15,24,0.35)"
                  : "0 12px 28px rgba(8,15,24,0.18)",
                backgroundColor: "rgba(255,255,255,0.02)",
                // subtle 3D lift when active
                transformOrigin: "center",
                // when active slightly scale up and straighten rotation for focus
                ...(isActive && { transform: `rotate(0deg) scale(1.03)` }),
                pointerEvents: "none",
                display: { xs: "none", md: "block" }, // hide collage on very small screens for clarity
              }}
            >
              <Box
                component="img"
                src={src}
                alt=""
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                }}
              />
              {/* soft warm frame overlay for cohesion with palette */}
              <Box
                sx={{
                  position: "absolute",
                  inset: 0,
                  background: "linear-gradient(180deg, rgba(243,226,212,0.02), rgba(25,34,42,0.06))",
                  pointerEvents: "none",
                }}
              />
            </Box>
          );
        })}

        {/* On small screens show one subtle background panel instead of collage */}
        <Box
          sx={{
            display: { xs: "block", md: "none" },
            position: "absolute",
            inset: 0,
            zIndex: 1,
            pointerEvents: "none",
          }}
        >
          <Box
            component="img"
            src={images[index]}
            alt=""
            sx={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              opacity: 0.18,
              filter: "grayscale(30%) contrast(0.95) saturate(0.9)",
              transition: "opacity 700ms ease",
            }}
          />
          <Box
            sx={{
              position: "absolute",
              inset: 0,
              background:
                "linear-gradient(180deg, rgba(65,94,114,0.22) 0%, rgba(25,34,42,0.45) 100%)",
            }}
          />
        </Box>
      </Box>

      {/* soft decorative orb (keeps original feel) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
        }}
      />

      {/* Main content (kept high z-index) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 170, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 10,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.2rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.5,
          textShadow: "2px 2px 10px rgba(0,0,0,0.45)",
          zIndex: 10,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.12rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 10,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 18px rgba(0,0,0,0.24)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 10px 26px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 10,
        }}
        onClick={() =>
          document.getElementById("about")?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave (kept intact) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 10,
        }}
      >
        <path fill="#F3E2D4" d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z" />
      </Box>
    </Box>
  );
};

export default Hero;
