import React, { useEffect, useState, useRef } from "react";
import { Box, Typography, Button, useTheme } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const { t } = useLanguage();
  const theme = useTheme();
  const [index, setIndex] = useState(0);
  const intervalRef = useRef<number | null>(null);

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // rotate index every 4s; respects reduced motion by not animating transforms if user prefers
  useEffect(() => {
    // clear any existing interval
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    // use window.matchMedia to respect prefers-reduced-motion
    const reduce = typeof window !== "undefined" && window.matchMedia
      ? window.matchMedia("(prefers-reduced-motion: reduce)").matches
      : false;

    if (!reduce) {
      intervalRef.current = window.setInterval(() => {
        setIndex((i) => (i + 1) % images.length);
      }, 4000);
    } else {
      // if reduced motion, still change slowly but without animation
      intervalRef.current = window.setInterval(() => {
        setIndex((i) => (i + 1) % images.length);
      }, 6000);
    }

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, []);

  // small helper: compute which image sits in each card slot
  const cardImage = (slot: number) => {
    return images[(index + slot) % images.length];
  };

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        // keep original gradient as main background
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
      }}
    >
      {/* Subtle textured overlay to add warmth but keep images crisp */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
          background:
            "linear-gradient(180deg, rgba(243,226,212,0.02) 0%, rgba(25,34,42,0.03) 100%)",
        }}
      />

      {/* Floating decorative stack of photo-cards (discrete & elegant) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          right: { xs: 12, md: 48 },
          bottom: { xs: 36, md: 72 },
          zIndex: 2,
          width: { xs: 220, md: 320 },
          height: { xs: 120, md: 180 },
          pointerEvents: "none", // purely decorative
          display: "flex",
          alignItems: "flex-end",
          justifyContent: "center",
        }}
      >
        {/* 5 overlapping card slots â€” each shows a rotating image */}
        {[0, 1, 2, 3, 4].map((slot) => {
          // visual offsets and rotation for stacked, layered look
          const offsets = [
            { x: 44, y: -6, r: -14, scale: 0.86, opacity: 0.62 },
            { x: 22, y: -4, r: -6, scale: 0.92, opacity: 0.74 },
            { x: 0, y: 0, r: 0, scale: 1, opacity: 0.9 }, // center (most visible)
            { x: -22, y: -2, r: 6, scale: 0.92, opacity: 0.74 },
            { x: -44, y: -6, r: 14, scale: 0.86, opacity: 0.62 },
          ];
          const o = offsets[slot % offsets.length];

          return (
            <Box
              key={slot}
              sx={{
                // each card is absolutely positioned within the stack container
                position: "absolute",
                right: `calc(50% - ${o.x}px)`,
                bottom: `${o.y}px`,
                transformOrigin: "center center",
                zIndex: 2 + slot,
                width: { xs: 120, md: 160 },
                height: { xs: 78, md: 110 },
                borderRadius: 8,
                boxShadow:
                  "0 10px 24px rgba(10,20,30,0.18), 0 2px 6px rgba(0,0,0,0.12)",
                backgroundColor: "rgba(255,255,255,0.06)",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                overflow: "hidden",
                // Polaroid-like border frame
                border: "6px solid rgba(243,226,212,0.75)",
                // image transforms + transitions
                transition:
                  "transform 700ms cubic-bezier(.2,.9,.25,1), opacity 700ms ease",
                transform: `rotate(${o.r}deg) scale(${o.scale})`,
                opacity: o.opacity,
                // subtle hover lift if user can hover (still pointerEvents none, but keeps visuals consistent)
                "&:hover": {
                  transform: `rotate(${o.r}deg) scale(${o.scale + 0.02}) translateY(-4px)`,
                },
              }}
            >
              <Box
                component="img"
                src={cardImage(slot)}
                alt={`hero-thumb-${slot}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // keep photos crisp and vivid, just a touch of contrast
                  filter: "contrast(1.05) saturate(1.02)",
                  // subtle crossfade when the underlying src changes
                  transition: "opacity 600ms ease, transform 600ms ease",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* A soft glassy circular accent (keeps composition elegant) */}
      <Box
        sx={{
          position: "absolute",
          top: { xs: "-14%", md: "-18%" },
          left: { xs: "-6%", md: "-8%" },
          width: { xs: 160, md: 260 },
          height: { xs: 160, md: 260 },
          borderRadius: "50%",
          background:
            "radial-gradient(circle at 30% 30%, rgba(243,226,212,0.18), rgba(243,226,212,0.06) 40%, transparent 60%)",
          zIndex: 1,
          filter: "blur(14px)",
          pointerEvents: "none",
        }}
      />

      {/* Logo + copy (keep visually above photo cards) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 240 },
          mb: 2,
          filter: "drop-shadow(0 8px 20px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 3,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.03em",
          mb: 1.25,
          textShadow: "2px 2px 12px rgba(0,0,0,0.45)",
          zIndex: 3,
          fontWeight: 700,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.01em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.2)",
          zIndex: 3,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.6,
          borderRadius: "28px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 18px rgba(0,0,0,0.18)",
          transition: "transform 220ms ease, box-shadow 220ms ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 12px 30px rgba(0,0,0,0.25)",
            backgroundColor: "#e9dacf",
          },
          zIndex: 3,
        }}
        onClick={() =>
          document.getElementById("about")?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave, above cards and background */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
