import React from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Button,
  Stack,
  Divider,
  useTheme,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

const ProjectDetail = () => {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()
  const theme = useTheme()

  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
          bgcolor: 'transparent',
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn&apos;t find the project you were looking for.
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/')}
          size="large"
          sx={{ borderRadius: 3 }}
        >
          Back to Home
        </Button>
      </Box>
    )
  }

  // Combine images: first image is always project.image, then optional project.images array
  const allImages =
    project.images && project.images.length > 0
      ? [project.image, ...project.images]
      : [project.image]

  return (
    <Box
      sx={{
        maxWidth: 900,
        mx: 'auto',
        px: { xs: 3, md: 6 },
        pt: { xs: 4, md: 8 },
        pb: 8,
        backgroundColor: 'transparent',
        minHeight: '100vh',
        animation: 'fadeIn 0.5s ease forwards',
        '@keyframes fadeIn': {
          from: { opacity: 0, transform: 'translateY(10px)' },
          to: { opacity: 1, transform: 'translateY(0)' },
        },
      }}
    >
      {/* Breadcrumbs */}
      <Breadcrumbs
        separator="›"
        aria-label="breadcrumb"
        sx={{
          mb: 5,
          color: '#6a4f7d',
          fontWeight: 600,
          '& a': {
            color: '#6a4f7d',
            textDecoration: 'none',
            fontWeight: 600,
            display: 'flex',
            alignItems: 'center',
            transition: 'color 0.3s ease',
            '&:hover': { color: '#8f6cad' },
          },
          '& .MuiTypography-root': {
            color: '#a287b8',
            fontWeight: 700,
          },
        }}
      >
        <Link to="/" aria-label="Home">
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link
          to="/"
          onClick={(e) => {
            e.preventDefault()
            navigate('/#projects')
          }}
          aria-label="Projects"
        >
          Projects
        </Link>
        <Typography aria-current="page">{project.title}</Typography>
      </Breadcrumbs>

      {/* Title */}
      <Typography
        variant="h3"
        component="h1"
        sx={{
          fontWeight: 800,
          color: '#5a3e61',
          mb: 4,
          letterSpacing: 1.3,
          lineHeight: 1.15,
          userSelect: 'none',
          textShadow: '0 0 4px rgba(90, 62, 97, 0.15)',
        }}
      >
        {project.title}
      </Typography>

      {/* Project Meta */}
      <Stack
        direction={{ xs: 'column', sm: 'row' }}
        spacing={5}
        mb={5}
        sx={{ color: '#8f6cad' }}
      >
        <Stack direction="row" spacing={1} alignItems="center">
          <CalendarMonthIcon fontSize="medium" />
          <Typography variant="subtitle1" color="text.secondary">
            {project.date}
          </Typography>
        </Stack>
        <Stack direction="row" spacing={1} alignItems="center">
          <LocationOnIcon fontSize="medium" />
          <Typography variant="subtitle1" color="text.secondary">
            {project.location}
          </Typography>
        </Stack>
      </Stack>

      {/* Images Section */}
      <Box
        sx={{
          mb: 6,
          display: 'flex',
          flexDirection: 'column',
          gap: 3,
          userSelect: 'none',
        }}
      >
        {/* Main Image - large, fully visible, not cropped */}
        <Box
          component="img"
          src={allImages[0]}
          alt={`${project.title} main image`}
          loading="lazy"
          sx={{
            width: '100%',
            maxHeight: 480,
            borderRadius: 4,
            boxShadow: '0 12px 28px rgba(90, 62, 97, 0.2)',
            objectFit: 'contain',
            transition: 'transform 0.3s ease',
            '&:hover': {
              transform: 'scale(1.03)',
            },
          }}
        />

        {/* Thumbnails if more images */}
        {allImages.length > 1 && (
          <Stack
            direction="row"
            spacing={2}
            justifyContent="center"
            flexWrap="wrap"
            sx={{ mt: 1 }}
          >
            {allImages.slice(1).map((img, i) => (
              <Box
                key={i}
                component="img"
                src={img}
                alt={`${project.title} image ${i + 2}`}
                loading="lazy"
                sx={{
                  width: { xs: 80, sm: 110, md: 130 },
                  height: 'auto',
                  borderRadius: 3,
                  cursor: 'pointer',
                  objectFit: 'contain',
                  boxShadow: '0 6px 18px rgba(90, 62, 97, 0.15)',
                  transition: 'transform 0.25s ease, box-shadow 0.25s ease',
                  '&:hover': {
                    transform: 'scale(1.1)',
                    boxShadow: `0 10px 26px ${theme.palette.primary.main}99`,
                    border: `2px solid ${theme.palette.primary.main}`,
                  },
                }}
                onClick={() => {
                  // Scroll main image into view when clicking a thumbnail
                  document
                    .querySelector('img[alt^="' + project.title + ' main"]')
                    ?.scrollIntoView({ behavior: 'smooth' })
                }}
              />
            ))}
          </Stack>
        )}
      </Box>

      <Divider sx={{ mb: 6, borderColor: '#cdb4db' }} />

      {/* Description */}
      <Typography
        variant="body1"
        sx={{
          fontWeight: 400,
          color: '#3a2f3b',
          lineHeight: 1.85,
          fontSize: '1.125rem',
          whiteSpace: 'pre-line',
          letterSpacing: 0.25,
          userSelect: 'text',
        }}
        dangerouslySetInnerHTML={{ __html: project.description }}
      />

      {/* Back Button */}
      <Box sx={{ mt: 8, display: 'flex', justifyContent: 'center' }}>
        <Button
          variant="outlined"
          onClick={() => navigate('/#projects')}
          sx={{
            borderRadius: 4,
            color: '#5a3e61',
            borderColor: '#5a3e61',
            fontWeight: 700,
            fontSize: '1rem',
            px: 5,
            py: 1.5,
            transition: 'all 0.3s ease',
            '&:hover': {
              backgroundColor: '#5a3e61',
              color: '#fff',
              borderColor: '#5a3e61',
            },
          }}
        >
          ← Back to Projects
        </Button>
      </Box>
    </Box>
  )
}

export default ProjectDetail
