import React from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Card,
  CardMedia,
  CardContent,
  Stack,
  Button,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const ProjectDetail = () => {
  const { slug } = useParams()
  const project = projects.find((p) => p.slug === slug)
  const navigate = useNavigate()

  if (!project) {
    return (
      <Box sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h4" color="error">
          Project Not Found
        </Typography>
        <Button variant="outlined" onClick={() => navigate('/')}>
          Back to Home
        </Button>
      </Box>
    )
  }

  return (
    <Box sx={{ p: { xs: 3, md: 6 }, backgroundColor: '#EFE6F0', minHeight: '100vh' }}>
      {/* Breadcrumbs (Visibility of System Status - Nielsen) */}
      <Breadcrumbs separator="â€º" aria-label="breadcrumb" sx={{ mb: 3 }}>
        <Link to="/" style={{ color: '#5A3E61', display: 'flex', alignItems: 'center' }}>
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link to="/#projects" style={{ color: '#5A3E61' }}>
          Projects
        </Link>
        <Typography color="#8A6B95">{project.title}</Typography>
      </Breadcrumbs>

      {/* Project Card */}
      <Card
        elevation={4}
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          borderRadius: 5,
          overflow: 'hidden',
          backgroundColor: '#FFFFFF',
        }}
      >
        <CardMedia
          component="img"
          image={project.image}
          alt={project.title}
          sx={{
            width: { xs: '100%', md: '45%' },
            height: { xs: 240, md: 400 },
            objectFit: 'cover',
          }}
        />
        <CardContent
          sx={{
            px: { xs: 3, md: 4 },
            py: { xs: 3, md: 4 },
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
          }}
        >
          <Typography variant="h5" sx={{ fontWeight: 600, color: '#5A3E61', mb: 2 }}>
            {project.title}
          </Typography>
          <Stack direction="row" spacing={2} mb={2}>
            <Stack direction="row" spacing={1} alignItems="center">
              <CalendarMonthIcon fontSize="small" />
              <Typography variant="caption" color="#6E4D74">
                {project.date}
              </Typography>
            </Stack>
            <Stack direction="row" spacing={1} alignItems="center">
              <LocationOnIcon fontSize="small" />
              <Typography variant="caption" color="#6E4D74">
                {project.location}
              </Typography>
            </Stack>
          </Stack>
          <Box
            sx={{
              fontWeight: 300,
              color: '#3A2F3B',
              lineHeight: 1.6,
              fontSize: '0.9rem',
              mt: 1,
            }}
            dangerouslySetInnerHTML={{ __html: project.description }}
          />
        </CardContent>
      </Card>
    </Box>
  )
}

export default ProjectDetail
