import { AppBar, Toolbar, Button, Box } from '@mui/material'
import Logo from '../assets/logo2.png'

const Navbar = () => {
  const scrollTo = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <AppBar
      position="sticky"
      elevation={6}
      sx={{
        backgroundColor: '#17313E',
        px: { xs: 2, md: 6 },
        py: 1,
        boxShadow: '0 4px 20px rgba(0,0,0,0.25)',
        backdropFilter: 'blur(8px)',
        zIndex: 999,
      }}
    >
      <Toolbar
        disableGutters
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          maxWidth: 1280,
          mx: 'auto',
          width: '100%',
        }}
      >
        {/* Left side: Logo + Nav links */}
        <Box display="flex" alignItems="center" gap={{ xs: 2, md: 3 }}>
          <Box
            onClick={() => scrollTo('home')}
            sx={{
              cursor: 'pointer',
              transition: 'transform 0.3s ease',
              '&:hover': {
                transform: 'scale(1.05) rotate(-2deg)',
              },
            }}
          >
            <img
              src={Logo}
              alt="Odyssey Logo"
              style={{
                height: 48,
                filter: 'drop-shadow(0 0 4px rgba(255,255,255,0.15))',
                borderRadius: 6,
                userSelect: 'none',
              }}
            />
          </Box>

          <Box display="flex" gap={{ xs: 2, md: 3 }}>
            {['about', 'projects', 'feedback'].map((id) => (
              <Button
                key={id}
                onClick={() => scrollTo(id)}
                sx={{
                  color: '#F3E2D4',
                  textTransform: 'none',
                  fontWeight: 500,
                  fontSize: { xs: '0.75rem', md: '0.875rem' },
                  letterSpacing: '0.08em',
                  padding: '6px 12px',
                  borderRadius: 20,
                  transition: 'all 0.3s ease',
                  boxShadow: 'none',
                  '&:hover': {
                    backgroundColor: '#415E72',
                    color: '#F3E2D4',
                    transform: 'translateY(-2px)',
                    boxShadow: '0 4px 10px rgba(65, 94, 114, 0.4)',
                  },
                }}
              >
                {id.charAt(0).toUpperCase() + id.slice(1)}
              </Button>
            ))}
          </Box>
        </Box>

        {/* Right Side: Optional CTA or Social Icons (future expansion) */}
        <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 2 }}>
          {/* Add future icons or links here if needed */}
        </Box>
      </Toolbar>
    </AppBar>
  )
}

export default Navbar
