import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const pausedRef = useRef(false);
  const { t } = useLanguage();

  // Preload
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Cycle every 4s; respects pausedRef
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      if (!pausedRef.current) {
        setIndex((i) => (i + 1) % images.length);
      }
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // Pause controls (hover + touch)
  const pause = () => {
    pausedRef.current = true;
  };
  const resume = () => {
    pausedRef.current = false;
  };

  // choose image on click/tap
  const choose = (i) => {
    setIndex(i);
  };

  return (
    <Box
      id="home"
      onMouseEnter={pause}
      onMouseLeave={resume}
      onTouchStart={pause}
      onTouchEnd={resume}
      sx={{
        position: "relative",
        minHeight: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
        // subtle paper texture overlay (keeps original gradient visible)
        // not actual image, just a gentle noise-like feel via linear-gradients
        backgroundImage:
          "linear-gradient(180deg, rgba(255,255,255,0.02) 0%, rgba(255,255,255,0.00) 100%)",
      }}
    >
      {/* Decorative left orb for balance on larger screens */}
      <Box
        sx={{
          display: { xs: "none", md: "block" },
          position: "absolute",
          left: -60,
          top: -40,
          width: 260,
          height: 260,
          bgcolor: "rgba(243,226,212,0.14)",
          borderRadius: "50%",
          filter: "blur(26px)",
          zIndex: 0,
          pointerEvents: "none",
        }}
      />

      {/* Postcard stack: on mobile center horizontally under content; on md+ place vertical on right */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          zIndex: 1,
          // layout switch: mobile -> centered horizontal stack; md+ -> vertical right column
          left: { xs: "50%", md: "auto" },
          transform: { xs: "translateX(-50%)", md: "none" },
          bottom: { xs: 48, md: "50%" },
          right: { md: 48 },
          top: { md: "50%" },
          display: "flex",
          flexDirection: { xs: "row", md: "column" },
          gap: { xs: 2, md: 2.25 },
          alignItems: "center",
          pointerEvents: "auto", // let users tap cards on mobile
          px: { xs: 0, md: 0 },
        }}
      >
        {images.map((src, i) => {
          const isActive = i === index;

          // sizes responsive and "postcard" aspect ratio (like 4:3)
          const size = {
            width: { xs: isActive ? 130 : 104, md: isActive ? 160 : 120 },
            height: { xs: isActive ? 98 : 78, md: isActive ? 120 : 90 },
          };

          // rotations for the tactile stack look on mobile (soft & varied)
          const rotation = {
            xs: isActive
              ? "rotate(0deg)"
              : i % 2 === 0
              ? "rotate(-6deg)"
              : "rotate(6deg)",
            md: "rotate(0deg)",
          };

          return (
            <Box
              key={src}
              onClick={() => choose(i)}
              sx={{
                width: size.width,
                height: size.height,
                borderRadius: 1.2,
                overflow: "hidden",
                boxShadow: isActive
                  ? "0 18px 40px rgba(6,24,44,0.36)"
                  : "0 8px 20px rgba(6,24,44,0.14)",
                transform: `${rotation.xs} translateZ(0)`,
                transition: "all 420ms cubic-bezier(.2,.9,.2,1)",
                border: "1px solid rgba(255,255,255,0.12)",
                backgroundColor: "rgba(255,255,255,0.02)",
                display: "flex",
                alignItems: "stretch",
                justifyContent: "center",
                position: "relative",
                cursor: "pointer",
                // on md (right column) we align the active card with a slight shift
                ...(isActive && {
                  transform: {
                    xs: rotation.xs,
                    md: "translateX(0) scale(1.03)",
                  },
                }),
                "&:active": {
                  transform: "translateY(2px)",
                },
              }}
            >
              {/* paper border + photo */}
              <Box
                component="img"
                src={src}
                alt={`postcard-${i + 1}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // postcard treatment: non-active slightly desaturated and softer; active vivid
                  filter: isActive
                    ? "saturate(1.02) contrast(1.03)"
                    : "grayscale(0.45) contrast(0.95) opacity(0.88)",
                  transition:
                    "filter 420ms ease, opacity 420ms ease, transform 420ms ease",
                }}
              />

              {/* thin paper frame (inner) */}
              <Box
                sx={{
                  position: "absolute",
                  inset: 0,
                  borderRadius: 1.2,
                  boxShadow: "inset 0 1px 0 rgba(255,255,255,0.03)",
                  pointerEvents: "none",
                }}
              />

              {/* tiny stamp / badge for active card */}
              <Box
                sx={{
                  position: "absolute",
                  right: 6,
                  top: 6,
                  px: 0.6,
                  py: 0.35,
                  bgcolor: isActive
                    ? "rgba(255,255,255,0.95)"
                    : "rgba(255,255,255,0.8)",
                  color: isActive ? "#2b3b47" : "#2b3b47",
                  fontSize: 11,
                  borderRadius: 0.75,
                  fontWeight: 700,
                  transform: "rotate(-6deg)",
                  boxShadow: "0 3px 8px rgba(0,0,0,0.18)",
                  pointerEvents: "none",
                }}
              >
                {isActive ? "NOW" : "✦"}
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* Main content */}
      <Box
        sx={{ position: "relative", zIndex: 3, width: "100%", maxWidth: 920 }}
      >
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 160, md: 240 },
            mb: 1.5,
            filter: "drop-shadow(0 10px 26px rgba(0,0,0,0.36))",
            display: "block",
            mx: "auto",
          }}
        />

        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "1.7rem", sm: "1.95rem", md: "3rem" },
            color: "#F3E2D4",
            letterSpacing: "0.02em",
            mb: 1,
            textShadow: "2px 2px 12px rgba(0,0,0,0.38)",
            fontWeight: 700,
            px: { xs: 1, md: 0 },
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>

        <Typography
          variant="h6"
          maxWidth={720}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "0.95rem", md: "1.06rem" },
            color: "rgba(243,226,212,0.98)",
            letterSpacing: "0.01em",
            lineHeight: 1.6,
            mb: 3,
            textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
            px: { xs: 2, md: 0 },
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        <Button
          endIcon={<ExpandMoreIcon />}
          variant="contained"
          size="large"
          sx={{
            backgroundColor: "#F3E2D4",
            color: "#415E72",
            px: { xs: 4, md: 5 },
            py: 1.5,
            borderRadius: "28px",
            fontFamily: "Inter, sans-serif",
            fontWeight: 700,
            textTransform: "none",
            boxShadow: "0 10px 36px rgba(6,24,44,0.22)",
            transition: "transform 0.28s ease, box-shadow 0.28s ease",
            "&:hover": {
              transform: "translateY(-3px)",
              boxShadow: "0 16px 50px rgba(6,24,44,0.28)",
              backgroundColor: "#efe1d6",
            },
            mx: "auto",
            display: "flex",
            alignItems: "center",
            gap: 1,
          }}
          onClick={() =>
            document
              .getElementById("about")
              ?.scrollIntoView({ behavior: "smooth" })
          }
        >
          {t("Learn more")}
        </Button>
      </Box>

      {/* subtle bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
