import React from 'react'
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Link,
  Button,
  Stack,
  Divider,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

export default function ProjectDetail() {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()

  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
          backgroundColor: 'transparent',
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn’t find what you’re looking for.
        </Typography>
        <Button
          variant="contained"
          onClick={() => navigate('/')}
          size="large"
          sx={{ borderRadius: 3 }}
        >
          Back to Home
        </Button>
      </Box>
    )
  }

  // build images array
  const extra = Array.isArray(project.images) ? project.images : []
  const allImages = [project.image, ...extra].slice(0, 3)

  // helper to scroll to projects
  const goToProjects = () => {
    navigate('/')
    setTimeout(() => {
      document.getElementById('projects')?.scrollIntoView({ behavior: 'smooth' })
    }, 50)
  }

  return (
    <Box
      sx={{
        maxWidth: 900,
        mx: 'auto',
        px: { xs: 3, md: 6 },
        pt: { xs: 4, md: 8 },
        pb: 8,
        backgroundColor: 'transparent',
      }}
    >
      {/* Breadcrumbs */}
      <Breadcrumbs
        separator="›"
        aria-label="breadcrumb"
        sx={{
          mb: 3,
          '& a': {
            display: 'flex',
            alignItems: 'center',
            color: '#6E4D74',
            textDecoration: 'none',
            fontWeight: 500,
            '&:hover': { color: '#5A3E61' },
          },
          '& .MuiTypography-root': {
            color: '#5A3E61',
            fontWeight: 700,
          },
        }}
      >
        <Link component={RouterLink} to="/" underline="none" sx={{ color: '#6E4D74' }}>
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link
          component="button"
          onClick={goToProjects}
          underline="none"
          sx={{ color: '#6E4D74', fontWeight: 500, cursor: 'pointer' }}
        >
          Projects
        </Link>
        <Typography>{project.title}</Typography>
      </Breadcrumbs>

      {/* Title */}
      <Typography
        variant="h3"
        component="h1"
        sx={{
          fontWeight: 800,
          color: '#5A3E61',
          mb: 3,
          letterSpacing: 1,
        }}
      >
        {project.title}
      </Typography>

      {/* Meta */}
      <Stack direction="row" spacing={4} mb={4} flexWrap="wrap">
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#6E4D74' }}>
          <CalendarMonthIcon /> <Typography>{project.date}</Typography>
        </Stack>
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#6E4D74' }}>
          <LocationOnIcon /> <Typography>{project.location}</Typography>
        </Stack>
      </Stack>

      {/* Images */}
      <Box sx={{ mb: 5 }}>
        {/* Main image, natural size but constrained to container width */}
        <Box
          component="img"
          src={allImages[0]}
          alt={`${project.title} main`}
          loading="lazy"
          sx={{
            width: '100%',
            height: 'auto',
            objectFit: 'contain',
            mb: allImages.length > 1 ? 2 : 0,
          }}
        />
        {/* Thumbnails */}
        {allImages.length > 1 && (
          <Stack direction="row" spacing={2}>
            {allImages.slice(1).map((img, i) => (
              <Box
                key={i}
                component="img"
                src={img}
                alt={`${project.title} ${i + 2}`}
                loading="lazy"
                sx={{
                  width: 100,
                  height: 'auto',
                  objectFit: 'contain',
                  cursor: 'pointer',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'scale(1.05)' },
                }}
                onClick={() => {
                  document
                    .querySelector('img[alt^="' + project.title + ' main"]')
                    ?.scrollIntoView({ behavior: 'smooth' })
                }}
              />
            ))}
          </Stack>
        )}
      </Box>

      <Divider sx={{ mb: 5, borderColor: '#EFE6F0' }} />

      {/* Description */}
      <Typography
        variant="body1"
        sx={{
          color: '#3A2F3B',
          lineHeight: 1.7,
          mb: 6,
          whiteSpace: 'pre-line',
        }}
        dangerouslySetInnerHTML={{ __html: project.description }}
      />

      {/* Back to Projects */}
      <Box textAlign="center">
        <Button
          variant="outlined"
          onClick={goToProjects}
          sx={{
            borderRadius: 3,
            color: '#5A3E61',
            borderColor: '#5A3E61',
            px: 4,
            py: 1.2,
            fontWeight: 600,
            '&:hover': {
              backgroundColor: '#5A3E61',
              color: '#fff',
            },
          }}
        >
          ← Back to Projects
        </Button>
      </Box>
    </Box>
  )
}
