import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle index every 4 seconds
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // small helper to pick images for each card so they look staggered
  const cardSrc = (offset) => images[(index + offset) % images.length];

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "flex-start", // shift text a little left for balance with collage
        textAlign: "left",
        px: { xs: 4, md: 10 },
        zIndex: 2,
        // subtle textured gradient background
        background:
          "linear-gradient(140deg, rgba(197,176,205,0.14) 0%, rgba(65,94,114,0.06) 45%, rgba(65,94,114,0.16) 100%)",
      }}
    >
      {/* decorative vignette for contrast but no heavy blur */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          pointerEvents: "none",
          zIndex: 0,
          background:
            "radial-gradient(800px 400px at 20% 30%, rgba(20,30,40,0.08), transparent 20%), radial-gradient(600px 300px at 85% 70%, rgba(20,30,40,0.06), transparent 10%)",
        }}
      />

      {/* Polaroid collage container (right side, discrete & stylish) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          right: { xs: -30, md: 48 },
          top: { xs: "18%", md: "8%" },
          width: { xs: 220, md: 420 },
          height: { xs: 320, md: 520 },
          zIndex: 1,
          pointerEvents: "none",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          transform: { xs: "scale(0.9)", md: "scale(1)" },
        }}
      >
        {/* Subtle background card for depth */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            borderRadius: 3,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.04), rgba(25,34,42,0.02))",
            boxShadow: "0 12px 40px rgba(8,12,20,0.12)",
            transform: "rotate(-2deg) translateY(6px)",
            filter: "blur(0.5px)",
          }}
        />

        {/* Card 1 — front */}
        <Box
          sx={{
            position: "absolute",
            width: { xs: 140, md: 200 },
            height: { xs: 180, md: 260 },
            borderRadius: 2,
            overflow: "hidden",
            transform: "rotate(-6deg)",
            boxShadow: "0 18px 40px rgba(10,20,30,0.18)",
            transition: "transform 400ms ease, opacity 400ms ease",
            pointerEvents: "auto",
            // graceful entrance animation
            animation: "floatSlow 6s ease-in-out infinite",
          }}
        >
          <Box
            component="img"
            src={cardSrc(0)}
            alt="card-1"
            sx={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              // make images discrete: low opacity + gentle desaturation + warm wash
              opacity: 0.86,
              filter: "grayscale(10%) saturate(0.9) contrast(0.98)",
              transition: "transform 6s linear, opacity 300ms ease",
              transformOrigin: "center center",
            }}
          />
          {/* Polaroid border */}
          <Box
            sx={{
              position: "absolute",
              left: 6,
              right: 6,
              bottom: 8,
              height: 34,
              background: "rgba(243,226,212,0.9)",
              borderRadius: 1,
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 12,
              color: "#415E72",
              fontWeight: 600,
              letterSpacing: "0.02em",
              boxShadow: "inset 0 -2px 0 rgba(0,0,0,0.04)",
            }}
          >
            {t("Erasmus")}
          </Box>
        </Box>

        {/* Card 2 — middle */}
        <Box
          sx={{
            position: "absolute",
            width: { xs: 120, md: 170 },
            height: { xs: 150, md: 220 },
            borderRadius: 2,
            overflow: "hidden",
            transform: "rotate(6deg) translateY(8px) translateX(8px)",
            boxShadow: "0 12px 28px rgba(8,12,20,0.14)",
            transition: "transform 400ms ease, opacity 400ms ease",
            animation: "float 5.2s ease-in-out infinite",
          }}
        >
          <Box
            component="img"
            src={cardSrc(1)}
            alt="card-2"
            sx={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              opacity: 0.78,
              filter: "grayscale(18%) saturate(0.85) contrast(0.96)",
              transition: "transform 6s linear, opacity 300ms ease",
            }}
          />
        </Box>

        {/* Card 3 — back */}
        <Box
          sx={{
            position: "absolute",
            width: { xs: 100, md: 150 },
            height: { xs: 120, md: 180 },
            borderRadius: 2,
            overflow: "hidden",
            transform: "rotate(-12deg) translateY(16px) translateX(-8px)",
            boxShadow: "0 10px 26px rgba(8,12,20,0.12)",
            transition: "transform 400ms ease, opacity 400ms ease",
            animation: "float 7s ease-in-out infinite",
          }}
        >
          <Box
            component="img"
            src={cardSrc(2)}
            alt="card-3"
            sx={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              opacity: 0.72,
              filter: "grayscale(25%) saturate(0.82) contrast(0.94)",
              transition: "transform 6s linear, opacity 300ms ease",
            }}
          />
        </Box>

        {/* subtle vignette at collage edge */}
        <Box
          sx={{
            position: "absolute",
            right: -20,
            top: "50%",
            width: 120,
            height: 300,
            transform: "translateY(-50%)",
            background:
              "linear-gradient(90deg, rgba(255,255,255,0.0), rgba(20,30,40,0.06))",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />
      </Box>

      {/* CONTENT (keeps above collage) */}
      <Box sx={{ position: "relative", zIndex: 3, maxWidth: 880 }}>
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 170, md: 240 },
            mb: 2,
            filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.28))",
          }}
        />

        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "2rem", md: "3.2rem" },
            color: "#F3E2D4",
            letterSpacing: "0.04em",
            mb: 2,
            textShadow: "2px 2px 10px rgba(0,0,0,0.36)",
            lineHeight: 1.02,
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>

        <Typography
          variant="h6"
          maxWidth={640}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "0.98rem", md: "1.15rem" },
            color: "rgba(243,226,212,0.95)",
            letterSpacing: "0.01em",
            lineHeight: 1.6,
            mb: 4,
            textShadow: "1px 1px 6px rgba(0,0,0,0.22)",
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        <Button
          endIcon={<ExpandMoreIcon />}
          variant="contained"
          size="large"
          sx={{
            backgroundColor: "#F3E2D4",
            color: "#415E72",
            px: 5,
            py: 1.75,
            borderRadius: "30px",
            fontFamily: "Inter, sans-serif",
            fontWeight: 600,
            textTransform: "none",
            boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
            transition: "transform 0.28s ease, box-shadow 0.28s ease",
            "&:hover": {
              transform: "translateY(-2px)",
              boxShadow: "0 8px 22px rgba(0,0,0,0.28)",
              backgroundColor: "#e5d7ce",
            },
          }}
          onClick={() =>
            document
              .getElementById("about")
              ?.scrollIntoView({ behavior: "smooth" })
          }
        >
          {t("Learn more")}
        </Button>
      </Box>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>

      {/* keyframes (kept inside component for simplicity) */}
      <style>{`
        @keyframes float {
          0% { transform: translateY(0) }
          50% { transform: translateY(-8px) }
          100% { transform: translateY(0) }
        }
        @keyframes floatSlow {
          0% { transform: translateY(0) rotate(-6deg) }
          50% { transform: translateY(-12px) rotate(-4deg) }
          100% { transform: translateY(0) rotate(-6deg) }
        }

        /* Respect prefers-reduced-motion */
        @media (prefers-reduced-motion: reduce) {
