import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // preserve initial gradient as fallback/base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* --- Discrete layered-photo background (subtle, colorful, tasteful) --- */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* multiple soft image layers (full DOM of images so crossfade works) */}
        {images.map((src, i) => {
          const isActive = i === index;

          // subtle per-image rotation offset so layers don't feel identical
          const rotateDeg = (i - index) * 2; // small difference relative to active

          return (
            <Box
              key={src}
              component="img"
              src={src}
              alt={`background-${i}`}
              sx={{
                position: "absolute",
                // make images cover area but keep them as 'panels' by adding mask/fade
                left: "-10%",
                top: "-8%",
                width: "120%",
                height: "116%",
                objectFit: "cover",
                // much lower opacity so they're discrete; active one a little stronger
                opacity: isActive ? 0.38 : 0.12,
                transition:
                  "opacity 900ms ease-in-out, transform 7000ms linear",
                // tiny slow scale & rotation to give a calm motion
                transform: isActive
                  ? `scale(1.03) rotate(${rotateDeg}deg)`
                  : `scale(1.00) rotate(${rotateDeg}deg)`,
                // keep images crisp and colorful (no blur); boost saturation slightly
                filter: "saturate(1.08) contrast(1.02)",
                // blend with site colors softly so text remains legible
                mixBlendMode: "soft-light",
                // subtle vignette-like fade at edges so images feel like panels, not full-bleed
                // mask (modern browsers) + webkitMask fallback
                WebkitMaskImage:
                  "radial-gradient(circle at 35% 25%, rgba(0,0,0,1) 0%, rgba(0,0,0,0.75) 30%, rgba(0,0,0,0.5) 55%, rgba(0,0,0,0) 85%)",
                maskImage:
                  "radial-gradient(circle at 35% 25%, rgba(0,0,0,1) 0%, rgba(0,0,0,0.75) 30%, rgba(0,0,0,0.5) 55%, rgba(0,0,0,0) 85%)",
                // gentle rounding helps the discrete-panel aesthetic on large screens
                borderRadius: { xs: 0, md: "28px" },
                // soft drop to separate from content slightly
                boxShadow: isActive
                  ? "0 30px 60px rgba(6,20,30,0.25)"
                  : "0 18px 40px rgba(6,20,30,0.12)",
                // ensure stacking order under content but relative order among images is preserved
                zIndex: 0 + i,
                // on small screens make panels narrower (feels less loud)
                "@media (max-width:900px)": {
                  left: "-6%",
                  top: "-4%",
                  width: "112%",
                  height: "110%",
                },
              }}
            />
          );
        })}

        {/* Warm color wash overlay (very subtle) to tie photos to the brand palette */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 3,
            pointerEvents: "none",
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.06) 0%, rgba(243,226,212,0.04) 40%, rgba(65,94,114,0.06) 100%)",
            mixBlendMode: "normal",
          }}
        />

        {/* Slight darkening gradient at bottom for contrast with text/button */}
        <Box
          sx={{
            position: "absolute",
            left: 0,
            right: 0,
            bottom: 0,
            height: "36%",
            zIndex: 4,
            background:
              "linear-gradient(180deg, rgba(0,0,0,0) 0%, rgba(12,18,26,0.25) 60%, rgba(12,18,26,0.48) 100%)",
            pointerEvents: "none",
          }}
        />
      </Box>

      {/* decorative blurred circle (soft warm accent) */}
      <Box
        sx={{
          position: "absolute",
          top: "-18%",
          right: "-8%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.22)",
          borderRadius: "50%",
          zIndex: 5,
          filter: "blur(20px)",
          pointerEvents: "none",
        }}
      />

      {/* Logo */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 8px 22px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 6,
        }}
      />

      {/* Copy */}
      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.2rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 10px rgba(0,0,0,0.45)",
          zIndex: 6,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={680}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.12rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.28)",
          zIndex: 6,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      {/* CTA */}
      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 18px rgba(0,0,0,0.22)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 10px 30px rgba(0,0,0,0.28)",
            backgroundColor: "#e9dccc",
          },
          zIndex: 6,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 6,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
