import { Box, Typography, Grid, Card, CardMedia, CardContent } from '@mui/material'
import { projects } from '../data/projects'

const Projects = () => (
  <Box
    id="projects"
    sx={{
      position: 'relative',
      background: 'linear-gradient(120deg, #F4ECF7 0%, #D1C0D3 100%)',
      color: '#17313E',
      py: 10,
      px: { xs: 3, md: 10 },
    }}
  >
    <Typography
      variant="h4"
      sx={{
        fontWeight: 500,
        textAlign: 'center',
        mb: 6,
        color: '#415E72',
        letterSpacing: '0.5px',
      }}
    >
      Our Projects
    </Typography>

    <Grid container spacing={5} justifyContent="center">
      {projects.map((p) => (
        <Grid item xs={12} sm={6} md={4} key={p.title}>
          <Card
            elevation={6}
            sx={{
              maxWidth: 340,
              margin: '0 auto',
              borderRadius: 4,
              overflow: 'hidden',
              display: 'flex',
              flexDirection: 'column',
              backgroundColor: '#FDFBFA',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              '&:hover': {
                transform: 'translateY(-10px)',
                boxShadow: '0 12px 24px rgba(0, 0, 0, 0.15)',
              },
            }}
          >
            <CardMedia
              component="img"
              image={p.image}
              alt={p.title}
              sx={{
                height: 240,
                objectFit: 'cover',
              }}
            />

            <CardContent sx={{ px: 3, py: 2.5 }}>
              <Typography
                variant="h6"
                sx={{
                  fontWeight: 600,
                  fontSize: '1.2rem',
                  color: '#5A3E61',
                  mb: 0.5,
                }}
              >
                {p.title}
              </Typography>

              <Typography
                variant="caption"
                sx={{
                  fontWeight: 500,
                  color: '#6E4D74',
                  display: 'block',
                  mb: 1,
                }}
              >
                {p.date} â€” {p.location}
              </Typography>

              <Typography
                variant="body2"
                sx={{
                  fontWeight: 300,
                  color: '#3A2F3B',
                  lineHeight: 1.6,
                  mt: 0.5,
                  display: '-webkit-box',
                  WebkitLineClamp: 6,
                  WebkitBoxOrient: 'vertical',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  minHeight: '8em',
                }}
              >
                {p.description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  </Box>
)

export default Projects
