import React, { useEffect } from 'react'
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Link,
  Button,
  Stack,
  Card,
  CardContent,
  Divider,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

export default function ProjectDetail() {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()

  useEffect(() => {
    window.scrollTo({
	  top: 0,
	  left: 0,
	  behavior: 'auto',
  	})
  }, [])

  const goToProjects = () => {
    navigate('/#projects', { state: { fromProjectDetail: true } })
  }


  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn’t find what you’re looking for.
        </Typography>
        <Button variant="contained" onClick={() => navigate('/')} size="large" sx={{ borderRadius: 3 }}>
          Back to Home
        </Button>
      </Box>
    )
  }

  const extra = Array.isArray(project.images) ? project.images : []
  const allImages = [project.image, ...extra].slice(0, 3)

  return (
    <Box
      sx={{
        maxWidth: 960,
        mx: 'auto',
        px: { xs: 2, md: 4 },
        py: { xs: 4, md: 6 },
        backgroundColor: 'transparent',
      }}
    >
      <Breadcrumbs separator="›" aria-label="breadcrumb" sx={{ mb: 4 }}>
        <Link
          component={RouterLink}
          to="/"
          underline="none"
          sx={{
            display: 'flex',
            alignItems: 'center',
            color: '#5A3E61',
            fontWeight: 500,
            '&:hover': { color: '#6E4D74' },
          }}
        >
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link
		component="button"
		onClick={goToProjects}
		underline="none"
		sx={{
			color: '#5A3E61',
			fontWeight: 500,
			'&:hover': { color: '#6E4D74' },
			background: 'none',
			border: 'none',
			p: 0,
			font: 'inherit',
			cursor: 'pointer',
		}}
		>
		Projects
		</Link>
        <Typography sx={{ color: '#6E4D74', fontWeight: 700 }}>
          {project.title}
        </Typography>
      </Breadcrumbs>

      <Card elevation={6} sx={{ borderRadius: 6, overflow: 'hidden', bgcolor: '#FFFBFE' }}>
  {/* Hero Image */}
  <Box
    sx={{
      position: 'relative',
      width: '100%',
      height: { xs: 240, md: 400 },
      overflow: 'hidden',
    }}
  >
    <Box
      component="img"
      src={allImages[0]}
      alt={`${project.title} hero`}
      loading="lazy"
      sx={{
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        transition: 'transform 0.6s ease',
        '&:hover': {
          transform: 'scale(1.03)',
        },
      }}
    />
    <Box
      sx={{
        position: 'absolute',
        bottom: 0,
        width: '100%',
        bgcolor: 'rgba(0,0,0,0.5)',
        color: '#fff',
        py: 2,
        px: { xs: 2, md: 4 },
      }}
    >
      <Typography variant="h4" sx={{ fontWeight: 700 }}>
        {project.title}
      </Typography>
    </Box>
  </Box>

  {/* Image strip (optional) */}
  {allImages.length > 1 && (
    <Box
      sx={{
        display: 'flex',
        flexWrap: 'nowrap',
        overflowX: 'auto',
        gap: 1.5,
        px: 2,
        py: 2,
        bgcolor: '#F8F3F9',
      }}
    >
      {allImages.slice(1).map((img, i) => (
        <Box
          key={i}
          component="img"
          src={img}
          alt={`project additional ${i + 2}`}
          loading="lazy"
          sx={{
            flex: '0 0 auto',
            width: 180,
            height: 120,
            objectFit: 'cover',
            borderRadius: 2,
          }}
        />
      ))}
    </Box>
  )}

  {/* Content */}
  <CardContent sx={{ p: { xs: 3, md: 5 } }}>
    {/* Metadata */}
    <Stack
      direction="row"
      spacing={3}
      flexWrap="wrap"
      alignItems="center"
      mb={2}
      sx={{ color: '#6E4D74' }}
    >
      <Stack direction="row" spacing={1} alignItems="center">
        <CalendarMonthIcon fontSize="small" />
        <Typography variant="body2">{project.date}</Typography>
      </Stack>
      <Stack direction="row" spacing={1} alignItems="center">
        <LocationOnIcon fontSize="small" />
        <Typography variant="body2">{project.location}</Typography>
      </Stack>
    </Stack>

    {/* Animated divider */}
    <Box
      sx={{
        height: 4,
        width: 64,
        background: 'linear-gradient(to right, #A678B4, #D0B3DE)',
        borderRadius: 2,
        mb: 3,
      }}
    />

    {/* Description */}
    <Typography
      variant="body1"
      sx={{
        color: '#3A2F3B',
        lineHeight: 1.75,
        fontSize: '1.05rem',
        whiteSpace: 'pre-line',
        mb: 5,
      }}
      dangerouslySetInnerHTML={{ __html: project.description }}
    />

    {/* Back Button */}
    <Box textAlign="center">
      <Button
        variant="contained"
        onClick={goToProjects}
        sx={{
          borderRadius: 99,
          background: '#5A3E61',
          px: 5,
          py: 1.75,
          fontWeight: 600,
          fontSize: '1rem',
          textTransform: 'none',
          boxShadow: '0 4px 12px rgba(90, 62, 97, 0.2)',
          '&:hover': {
            background: '#6E4D74',
          },
        }}
      >
        ← Back to Projects
      </Button>
    </Box>
  </CardContent>
</Card>

    </Box>
  )
}
