import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const paneLayout = [
  { top: "6%", right: "6%", width: 260, height: 170, rotate: -6, z: 6 },
  { top: "20%", right: "16%", width: 300, height: 200, rotate: 6, z: 5 },
  { top: "44%", right: "10%", width: 220, height: 150, rotate: -3, z: 7 },
  { top: "64%", right: "18%", width: 260, height: 170, rotate: 4, z: 4 },
  { top: "78%", right: "6%", width: 200, height: 130, rotate: -8, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const pausedRef = useRef(false);
  const { t } = useLanguage();

  // preload
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  // start/stop helper
  const startTimer = (delay = 4000) => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      if (!pausedRef.current) setIndex((i) => (i + 1) % images.length);
    }, delay);
  };

  useEffect(() => {
    startTimer(prefersReducedMotion ? 8000 : 4000);
    return () => clearInterval(intervalRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [prefersReducedMotion]);

  // pause on hover for desktop cluster
  const handleMouseEnter = () => {
    pausedRef.current = true;
  };
  const handleMouseLeave = () => {
    pausedRef.current = false;
  };

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 3, md: 8 },
        zIndex: 2,
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* --- Desktop: Asymmetrical image cluster (md+) --- */}
      <Box
        aria-hidden="true"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          display: { xs: "none", md: "block" },
        }}
      >
        {/* very subtle veil to integrate with gradient (keeps images decorative) */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.02) 0%, rgba(20,30,40,0.06) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {paneLayout.map((p, paneIndex) => {
          const imgIdx = (index + paneIndex) % images.length;
          const src = images[imgIdx];
          return (
            <Box
              key={paneIndex}
              sx={{
                position: "absolute",
                top: p.top,
                right: p.right,
                width: p.width,
                height: p.height,
                transform: `rotate(${p.rotate}deg) translateZ(0)`,
                zIndex: p.z,
                borderRadius: 14,
                overflow: "hidden",
                boxShadow:
                  "0 18px 40px rgba(3,12,24,0.35), inset 0 1px 0 rgba(255,255,255,0.02)",
                border: "1px solid rgba(243,226,212,0.08)",
                backgroundColor: "rgba(10,14,20,0.03)",
                transition:
                  "transform 480ms cubic-bezier(.2,.9,.2,1), opacity 420ms ease",
                pointerEvents: "none",
                // interactive float on hover (visual only)
                ...(prefersReducedMotion
                  ? {}
                  : {
                      ":hover": {
                        transform: `rotate(${p.rotate}deg) translateY(-8px) scale(1.02)`,
                      },
                    }),
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`hero-thumb-${paneIndex}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // subtle brightening, NO blur, NO printed text
                  filter: "contrast(1.03) brightness(0.95)",
                  transition: "transform 12000ms linear, opacity 600ms ease",
                  transform: prefersReducedMotion ? "scale(1)" : "scale(1.025)",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* --- Mobile: full-width image band (xs) shown under logo/headline --- */}
      <Box
        aria-hidden="true"
        sx={{
          display: { xs: "block", md: "none" },
          width: "100%",
          maxWidth: 920,
          px: 2,
          mt: 2,
          zIndex: 1,
          pointerEvents: "none",
        }}
      >
        <Box
          sx={{
            position: "relative",
            width: "100%",
            height: { xs: 180, sm: 220 },
            borderRadius: 16,
            overflow: "hidden",
            boxShadow: "0 18px 40px rgba(3,12,24,0.32)",
            border: "1px solid rgba(243,226,212,0.06)",
            backgroundColor: "rgba(10,14,20,0.03)",
          }}
        >
          {images.map((src, i) => {
            const isActive = i === index;
            return (
              <Box
                key={src}
                component="img"
                src={src}
                alt={`mobile-hero-${i}`}
                sx={{
                  position: "absolute",
                  inset: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  opacity: isActive ? 1 : 0,
                  transition:
                    "opacity 700ms ease-in-out, transform 12000ms linear",
                  transform: prefersReducedMotion
                    ? "scale(1)"
                    : isActive
                    ? "scale(1.03)"
                    : "scale(1)",
                  filter: "contrast(1.02) brightness(0.95)",
                }}
              />
            );
          })}

          {/* gradient overlay to keep text readable when logo/headline overlap */}
          <Box
            sx={{
              position: "absolute",
              inset: 0,
              background:
                "linear-gradient(180deg, rgba(20,30,40,0.06) 0%, rgba(20,30,40,0.18) 100%)",
              zIndex: 2,
              pointerEvents: "none",
            }}
          />
        </Box>
      </Box>

      {/* Decorative soft circle accent */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
        }}
      />

      {/* content (above images) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: { xs: 1.5, md: 2 },
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3rem" },
          color: "#F3E2D4",
          letterSpacing: "0.03em",
          mb: 1.25,
          textShadow: "2px 2px 12px rgba(0,0,0,0.34)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={700}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 3,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 8px 20px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 10px 28px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom svg wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
