import { Box, Typography, IconButton, Stack } from "@mui/material";
import InstagramIcon from "@mui/icons-material/Instagram";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import FacebookIcon from "@mui/icons-material/Facebook";
import WhatsAppIcon from "@mui/icons-material/WhatsApp";
import { useLanguage } from "../contexts/LanguageContext";

const Footer = () => {
  const { t } = useLanguage();
  const year = new Date().getFullYear();

  return (
    <Box
      sx={{
        backgroundColor: "#17313E",
        color: "#F3E2D4",
        py: 4,
        textAlign: "center",
      }}
    >
      <Typography variant="body2">
        Â© {year} Odyssey of Wisdom. {t("All rights reserved.")}
      </Typography>

      <Stack direction="row" justifyContent="center" spacing={4} mt={2}>
        <Box textAlign="center">
          <IconButton
            href="https://www.instagram.com/odyssey.of.wisdom/"
            target="_blank"
            rel="noopener noreferrer"
            sx={{
              color: "#C5B0CD",
              transition: "color 0.3s",
              "&:hover": { color: "#F3E2D4" },
            }}
          >
            <InstagramIcon fontSize="large" />
          </IconButton>
          <Typography variant="caption" display="block">
            {t("Instagram")}
          </Typography>
        </Box>

        <Box textAlign="center">
          <IconButton
            href="https://www.facebook.com/profile.php?id=61570902790007"
            target="_blank"
            rel="noopener noreferrer"
            sx={{
              color: "#C5B0CD",
              transition: "color 0.3s",
              "&:hover": { color: "#F3E2D4" },
            }}
          >
            <FacebookIcon fontSize="large" />
          </IconButton>
          <Typography variant="caption" display="block">
            {t("Facebook")}
          </Typography>
        </Box>

        <Box textAlign="center">
          <IconButton
            href="mailto:odysseyofwisdom.greece@gmail.com"
            sx={{
              color: "#C5B0CD",
              transition: "color 0.3s",
              "&:hover": { color: "#F3E2D4" },
            }}
          >
            <MailOutlineIcon fontSize="large" />
          </IconButton>
          <Typography variant="caption" display="block">
            {t("Email")}
          </Typography>
        </Box>

        <Box textAlign="center">
          <IconButton
            href="https://wa.me/306934347747"
            target="_blank"
            rel="noopener noreferrer"
            sx={{
              color: "#C5B0CD",
              transition: "color 0.3s",
              "&:hover": { color: "#F3E2D4" },
            }}
          >
            <WhatsAppIcon fontSize="large" />
          </IconButton>
          <Typography variant="caption" display="block">
            {t("WhatsApp")}
          </Typography>
        </Box>
      </Stack>
    </Box>
  );
};

export default Footer;
