import { Box, Typography, Grid, Card, CardMedia, CardContent, Chip } from '@mui/material'
import { projects } from '../data/projects'

const Projects = () => (
  <Box
    id="projects"
    sx={{
      position: 'relative',
      background: 'linear-gradient(120deg, #EADAE3 0%, #F3E2D4 100%)',
      color: '#17313E',
      pt: 12,
      pb: 10,
      px: { xs: 3, md: 10 },
    }}
  >
    {/* Top wave to connect from About */}
    <Box component="svg" viewBox="0 0 1440 80" sx={{ position: 'absolute', top: 0, left: 0, width: '100%' }}>
      <path
        fill="#EADAE3"
        d="M0,0 C360,100 1080,-100 1440,0 L1440,80 L0,80 Z"
      />
    </Box>

    {/* Section Header */}
    <Typography
      variant="h3"
      sx={{
        fontWeight: 400,
        fontSize: { xs: '2rem', md: '3rem' },
        textAlign: 'center',
        color: '#415E72',
        mb: 2,
      }}
    >
      Our Projects
    </Typography>

    <Typography
      variant="h6"
      maxWidth={900}
      mx="auto"
      sx={{
        fontFamily: 'Inter, sans-serif',
        fontWeight: 300,
        fontSize: { xs: '1rem', md: '1.125rem' },
        textAlign: 'center',
        color: '#17313E',
        mb: 6,
        lineHeight: 1.6,
      }}
    >
      We design and participate in Erasmus+ youth exchanges that foster intercultural dialogue, sustainability, and personal growth. Explore some of our recent projects below.
    </Typography>

    {/* Projects Grid */}
    <Grid container spacing={6} justifyContent="center">
      {projects.map((p) => (
        <Grid item xs={12} sm={6} md={4} key={p.title} sx={{ zIndex: 1 }}>
          <Card
            elevation={4}
            sx={{
              borderRadius: 3,
              overflow: 'hidden',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
              '&:hover': { transform: 'translateY(-8px)', boxShadow: '0 8px 24px rgba(0,0,0,0.2)' },
            }}
          >
            <CardMedia
              component="img"
              image={p.image}
              alt={p.title}
              sx={{ height: 280, objectFit: 'cover' }}
            />
            <CardContent sx={{ px: 3, py: 2.5 }}>
              <Typography
                variant="h6"
                sx={{
                  fontSize: '1.25rem',
                  color: '#415E72',
                  mb: 1,
                }}
              >
                {p.title}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                <Chip
                  label={p.date}
                  size="small"
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    backgroundColor: '#C5B0CD',
                    color: '#17313E',
                    px: 1.5,
                    py: 0.25,
                    borderRadius: '12px',
                    fontSize: '0.75rem',
                  }}
                />
                <Chip
                  label={p.location}
                  size="small"
                  sx={{
                    fontFamily: 'Inter, sans-serif',
                    fontWeight: 300,
                    backgroundColor: '#C5B0CD',
                    color: '#17313E',
                    px: 1.5,
                    py: 0.25,
                    borderRadius: '12px',
                    fontSize: '0.75rem',
                  }}
                />
              </Box>
              <Typography
                variant="body2"
                sx={{
                  fontFamily: 'Inter, sans-serif',
                  fontWeight: 300,
                  color: '#17313E',
                  lineHeight: 1.6,
                  display: '-webkit-box',
                  WebkitLineClamp: 5,
                  WebkitBoxOrient: 'vertical',
                  overflow: 'hidden',
                }}
              >
                {p.description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  </Box>
)

export default Projects
