import React from 'react'
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Link,
  Button,
  Stack,
  Divider,
  useTheme,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

export default function ProjectDetail() {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()
  const theme = useTheme()

  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
          backgroundColor: 'transparent',
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn’t find what you’re looking for.
        </Typography>
        <Button
          variant="contained"
          onClick={() => navigate('/')}
          size="large"
          sx={{ borderRadius: 3 }}
        >
          Back to Home
        </Button>
      </Box>
    )
  }

  // build full image list
  const extra = Array.isArray(project.images) ? project.images : []
  const allImages = [project.image, ...extra].slice(0, 3)

  // scroll helper
  const goToProjects = () => {
    navigate('/')
    setTimeout(() => {
      document.getElementById('projects')?.scrollIntoView({ behavior: 'smooth' })
    }, 100)
  }

  return (
    <Box
      sx={{
        maxWidth: 880,
        mx: 'auto',
        px: { xs: 3, md: 6 },
        pt: { xs: 4, md: 8 },
        pb: 8,
        backgroundColor: 'transparent',
      }}
    >
      {/* breadcrumbs */}
      <Breadcrumbs
        separator="›"
        aria-label="breadcrumb"
        sx={{
          mb: 4,
          '& a': {
            display: 'flex',
            alignItems: 'center',
            color: '#555',             // neutral link color
            textDecoration: 'none',
            fontWeight: 500,
            '&:hover': { color: theme.palette.primary.main },
          },
          '& .MuiTypography-root': {
            fontWeight: 700,
            color: theme.palette.primary.main,  // highlight current
          },
        }}
      >
        <Link component={RouterLink} to="/">
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link
          component="button"
          onClick={goToProjects}
          underline="none"
          sx={{ cursor: 'pointer' }}
        >
          Projects
        </Link>
        <Typography>{project.title}</Typography>
      </Breadcrumbs>

      {/* title */}
      <Typography
        variant="h3"
        component="h1"
        sx={{
          fontWeight: 800,
          color: '#333',
          mb: 3,
          letterSpacing: 0.5,
        }}
      >
        {project.title}
      </Typography>

      {/* meta */}
      <Stack direction="row" spacing={4} mb={4} flexWrap="wrap">
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#777' }}>
          <CalendarMonthIcon /> <Typography>{project.date}</Typography>
        </Stack>
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#777' }}>
          <LocationOnIcon /> <Typography>{project.location}</Typography>
        </Stack>
      </Stack>

      {/* images */}
      <Box sx={{ mb: 5 }}>
        {/* main */}
        <Box
          component="img"
          src={allImages[0]}
          alt={`${project.title} main`}
          loading="lazy"
          sx={{
            width: '100%',
            maxHeight: 300,         // comfortable size
            objectFit: 'contain',
            borderRadius: 3,
            boxShadow: '0 6px 18px rgba(0,0,0,0.1)',
            transition: 'transform 0.3s',
            '&:hover': { transform: 'scale(1.02)' },
          }}
        />
        {/* thumbnails */}
        {allImages.length > 1 && (
          <Stack direction="row" spacing={2} mt={2}>
            {allImages.slice(1).map((img, i) => (
              <Box
                key={i}
                component="img"
                src={img}
                alt={`${project.title} ${i + 2}`}
                loading="lazy"
                sx={{
                  width: 80,
                  height: 80,
                  objectFit: 'contain',
                  borderRadius: 2,
                  cursor: 'pointer',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'scale(1.1)' },
                }}
                onClick={() => {
                  document
                    .querySelector('img[alt^="' + project.title + ' main"]')
                    ?.scrollIntoView({ behavior: 'smooth' })
                }}
              />
            ))}
          </Stack>
        )}
      </Box>

      <Divider sx={{ mb: 5, borderColor: '#eee' }} />

      {/* description */}
      <Typography
        variant="body1"
        sx={{
          color: '#444',
          lineHeight: 1.7,
          mb: 6,
          whiteSpace: 'pre-line',
        }}
        dangerouslySetInnerHTML={{ __html: project.description }}
      />

      {/* back button */}
      <Box textAlign="center">
        <Button
          variant="outlined"
          onClick={goToProjects}
          sx={{
            borderRadius: 3,
            color: theme.palette.primary.main,
            borderColor: theme.palette.primary.main,
            px: 4,
            py: 1.2,
            fontWeight: 600,
            '&:hover': {
              backgroundColor: theme.palette.primary.main,
              color: '#fff',
            },
          }}
        >
          ← Back to Projects
        </Button>
      </Box>
    </Box>
  )
}
