import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const paneLayout = [
  // desktop cluster layout (relative positions)
  { top: "8%", right: "6%", width: 220, height: 140, rotate: -7, z: 6 },
  { top: "20%", right: "18%", width: 260, height: 170, rotate: 6, z: 5 },
  { top: "42%", right: "8%", width: 200, height: 130, rotate: -3, z: 7 },
  { top: "60%", right: "20%", width: 240, height: 150, rotate: 4, z: 4 },
  { top: "78%", right: "10%", width: 180, height: 120, rotate: -9, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // reduced motion preference
  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  // cycle images every 4s (or slower if reduced-motion)
  useEffect(() => {
    clearInterval(intervalRef.current);
    const delay = prefersReducedMotion ? 8000 : 4000;
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, delay);
    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  // manual swipe / click helpers for mobile carousel
  const next = () => setIndex((i) => (i + 1) % images.length);
  const prev = () => setIndex((i) => (i - 1 + images.length) % images.length);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // original gradient retained as base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* --- Desktop: asymmetrical framed panes (decorative cluster) --- */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          display: { xs: "none", md: "block" }, // only on md+
        }}
      >
        {/* thin veil to keep text readable when panes overlap */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.02) 0%, rgba(10,18,26,0.04) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {paneLayout.map((p, i) => {
          const imgIndex = (index + i) % images.length;
          const src = images[imgIndex];
          return (
            <Box
              key={i}
              sx={{
                position: "absolute",
                top: p.top,
                right: p.right,
                width: p.width,
                height: p.height,
                transform: `rotate(${p.rotate}deg) translateZ(0)`,
                zIndex: p.z,
                borderRadius: 12,
                overflow: "hidden",
                boxShadow:
                  "0 18px 40px rgba(3,12,24,0.28), inset 0 1px 0 rgba(255,255,255,0.02)",
                border: "1px solid rgba(243,226,212,0.10)",
                backgroundColor: "rgba(10,14,20,0.03)",
                transition:
                  "transform 450ms cubic-bezier(.2,.9,.2,1), box-shadow 350ms",
                pointerEvents: "none",
                // slight hover lift for users who hover (desktop)
                "@media (hover: hover)": {
                  "&:hover": {
                    transform: `rotate(${p.rotate}deg) translateY(-8px) scale(1.03)`,
                    boxShadow:
                      "0 26px 60px rgba(3,12,24,0.34), inset 0 1px 0 rgba(255,255,255,0.02)",
                  },
                },
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`photo ${imgIndex + 1}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // subtle aesthetic: slightly warm tone, crisp
                  filter: "saturate(0.9) contrast(1.02)",
                  transform: prefersReducedMotion ? "none" : "scale(1.02)",
                  transition: "transform 8000ms linear",
                }}
              />
            </Box>
          );
        })}
      </Box>

      {/* --- Mobile / small screens: horizontal carousel shown under content --- */}
      <Box
        sx={{
          display: { xs: "block", md: "none" },
          width: "100%",
          maxWidth: 640,
          position: "relative",
          zIndex: 3,
          mt: 2,
          mb: 2,
          pointerEvents: "auto",
        }}
      >
        {/* carousel viewport */}
        <Box
          sx={{
            overflow: "hidden",
            borderRadius: 16,
            border: "1px solid rgba(243,226,212,0.10)",
            boxShadow: "0 14px 36px rgba(3,12,24,0.22)",
            backgroundColor: "rgba(10,14,20,0.02)",
          }}
        >
          <Box
            sx={{
              display: "flex",
              width: `${images.length * 100}%`,
              transform: `translateX(-${(index * 100) / images.length}%)`,
              transition: prefersReducedMotion
                ? "transform 800ms ease"
                : "transform 600ms ease",
            }}
          >
            {images.map((src, i) => (
              <Box
                key={i}
                sx={{
                  width: `${100 / images.length}%`,
                  flexShrink: 0,
                  height: 160,
                  position: "relative",
                }}
              >
                <Box
                  component="img"
                  src={src}
                  alt={`photo ${i + 1}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    filter: "saturate(0.95) contrast(1.02)",
                  }}
                />
              </Box>
            ))}
          </Box>
        </Box>

        {/* pager and controls */}
        <Box
          sx={{
            mt: 1,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            px: 1,
            gap: 2,
          }}
        >
          <Box
            onClick={prev}
            sx={{
              cursor: "pointer",
              userSelect: "none",
              fontSize: 14,
              color: "#F3E2D4",
              opacity: 0.9,
              px: 1,
              py: 0.5,
            }}
          >
            ‹
          </Box>

          <Box
            sx={{
              display: "flex",
              gap: 1,
              alignItems: "center",
              justifyContent: "center",
              flex: 1,
            }}
          >
            {images.map((_, i) => (
              <Box
                key={i}
                onClick={() => setIndex(i)}
                sx={{
                  width: 8,
                  height: 8,
                  borderRadius: "50%",
                  bgcolor: i === index ? "#F3E2D4" : "rgba(243,226,212,0.34)",
                  opacity: i === index ? 1 : 0.6,
                  cursor: "pointer",
                }}
              />
            ))}
          </Box>

          <Box
            onClick={next}
            sx={{
              cursor: "pointer",
              userSelect: "none",
              fontSize: 14,
              color: "#F3E2D4",
              opacity: 0.9,
              px: 1,
              py: 0.5,
            }}
          >
            ›
          </Box>
        </Box>
      </Box>

      {/* decorative soft circle accent (keeps original vibe) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.20)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
        }}
      />

      {/* Main content (logo + headings + CTA) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.36))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 14px rgba(0,0,0,0.36)",
          zIndex: 4,
          maxWidth: 900,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={720}
        sx={{
