import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [
  { src: flag_photo, alt: "Greek flag on a postcard" },
  { src: romania1, alt: "Romania cityscape postcard" },
  { src: romania2, alt: "Romania countryside postcard" },
  { src: romania3, alt: "Romania architecture postcard" },
  { src: romania2_3, alt: "Romania travel postcard" },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null); // will hold numeric id from setInterval
  const hoverRef = useRef(false);
  const touchStartX = useRef(null);
  const { t } = useLanguage();

  // Preload images
  useEffect(() => {
    images.forEach((it) => {
      const img = new Image();
      img.src = it.src;
    });
  }, []);

  // Auto-advance every 4s, respecting hover/touch pause
  useEffect(() => {
    // clear existing
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    intervalRef.current = setInterval(() => {
      if (!hoverRef.current) {
        setIndex((i) => (i + 1) % images.length);
      }
    }, 4000);

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    };
  }, []);

  // Helpers
  const prev = () => setIndex((i) => (i - 1 + images.length) % images.length);
  const next = () => setIndex((i) => (i + 1) % images.length);

  const handleMouseEnter = () => {
    hoverRef.current = true;
  };
  const handleMouseLeave = () => {
    hoverRef.current = false;
  };

  // Touch swipe handlers (mobile)
  const onTouchStart = (e) => {
    hoverRef.current = true; // pause auto-advance while touching
    touchStartX.current =
      e.touches && e.touches[0] ? e.touches[0].clientX : null;
  };
  const onTouchMove = () => {
    // intentionally left empty — we only need start/end for simple swipe
  };
  const onTouchEnd = (e) => {
    hoverRef.current = false;
    const startX = touchStartX.current;
    const endX =
      e.changedTouches && e.changedTouches[0]
        ? e.changedTouches[0].clientX
        : null;
    touchStartX.current = null;
    if (startX === null || endX === null) return;
    const delta = endX - startX;
    const threshold = 40; // pixels to qualify as swipe
    if (delta > threshold) prev();
    else if (delta < -threshold) next();
  };

  return (
    <Box
      id="home"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onTouchStart={onTouchStart}
      onTouchMove={onTouchMove}
      onTouchEnd={onTouchEnd}
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
      aria-roledescription="hero"
    >
      {/* LEFT decorative orb for balance (subtle) */}
      <Box
        sx={{
          position: "absolute",
          left: { xs: -28, md: -64 },
          top: { xs: "-6%", md: "-14%" },
          width: { xs: 120, md: 260 },
          height: { xs: 120, md: 260 },
          bgcolor: "rgba(243,226,212,0.14)",
          borderRadius: "50%",
          zIndex: 0,
          filter: "blur(22px)",
          pointerEvents: "none",
        }}
      />

      {/* MOBILE: center swipeable postcard row */}
      <Box
        sx={{
          display: { xs: "flex", md: "none" },
          position: "absolute",
          top: "18%",
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: 1,
          width: "100%",
          justifyContent: "center",
          alignItems: "center",
          pointerEvents: "auto",
        }}
        role="region"
        aria-label="postcard carousel"
      >
        <Box
          sx={{
            display: "flex",
            overflow: "hidden",
            width: "min(92%, 560px)",
            justifyContent: "center",
            alignItems: "center",
            py: 1,
          }}
        >
          <Box
            sx={{
              display: "flex",
              gap: 2,
              transform: `translateX(-${index * 100}%)`,
              transition: "transform 420ms cubic-bezier(.22,.9,.3,1)",
              width: `${images.length * 100}%`,
            }}
          >
            {images.map((it, i) => {
              const active = i === index;
              return (
                <Box
                  key={it.src}
                  sx={{
                    width: "100%",
                    maxWidth: 320,
                    minWidth: 260,
                    height: 180,
                    borderRadius: 2.25,
                    overflow: "hidden",
                    boxShadow: active
                      ? "0 18px 44px rgba(6,24,44,0.36)"
                      : "0 6px 18px rgba(6,24,44,0.18)",
                    transform: active ? "scale(1)" : "scale(0.94)",
                    transition: "transform 360ms ease, box-shadow 360ms ease",
                    position: "relative",
                    backgroundColor: "rgba(255,255,255,0.02)",
                    flex: "0 0 100%",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    pointerEvents: "auto",
                  }}
                >
                  <Box
                    component="img"
                    src={it.src}
                    alt={it.alt}
                    sx={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                      display: "block",
                      filter: active
                        ? "saturate(1) contrast(1.02)"
                        : "grayscale(0.35) opacity(0.9) contrast(0.98)",
                    }}
                  />
                  <Box
                    sx={{
                      position: "absolute",
                      right: 8,
                      top: 8,
                      px: 0.75,
                      py: 0.5,
                      bgcolor: "rgba(255,255,255,0.9)",
                      color: "#2b2b2b",
                      fontSize: 11,
                      borderRadius: 0.75,
                      fontWeight: 700,
                      letterSpacing: "0.06em",
                    }}
                  >
                    {`OD-${i + 1}`}
                  </Box>
                </Box>
              );
            })}
          </Box>
        </Box>

        {/* mobile pagination dots */}
        <Box
          sx={{
            position: "absolute",
            bottom: -28,
            left: "50%",
            transform: "translateX(-50%)",
            display: "flex",
            gap: 1,
            zIndex: 2,
          }}
        >
          {images.map((_, i) => (
            <Box
              key={i}
              onClick={() => setIndex(i)}
              sx={{
                width: i === index ? 18 : 8,
                height: 8,
                borderRadius: 8,
                backgroundColor:
                  i === index ? "#F3E2D4" : "rgba(243,226,212,0.35)",
                transition: "width 240ms ease, background-color 240ms ease",
                cursor: "pointer",
              }}
              aria-label={`Go to postcard ${i + 1}`}
              role="button"
            />
          ))}
        </Box>
      </Box>

      {/* DESKTOP: stacked rotated postcards on the right */}
      <Box
        sx={{
          display: { xs: "none", md: "flex" },
          position: "absolute",
          right: 48,
          top: "48%",
          transform: "translateY(-50%)",
          zIndex: 1,
          pointerEvents: "none",
          width: 220,
          height: 320,
          alignItems: "center",
          justifyContent: "center",
        }}
        aria-hidden="true"
      >
        {images.map((it, i) => {
          const active = i === index;
          const rotations = [-12, -6, 0, 6, 12];
          const rotate = rotations[i % rotations.length];
          const offsetY = (i - index) * 12;
          const scale = active ? 1.03 : 0.94;
          return (
            <Box
              key={it.src}
              sx={{
                position: "absolute",
                width: active ? 200 : 160,
                height: active ? 140 : 112,
                left: active ? 0 : 12,
                top: `calc(50% + ${offsetY}px)`,
                transform: `rotate(${rotate}deg) translateY(-50%) scale(${scale})`,
                transformOrigin: "center",
                borderRadius: 2,
                overflow: "hidden",
                boxShadow: active
                  ? "0 22px 56px rgba(6,24,44,0.42)"
                  : "0 10px 26px rgba(6,24,44,0.18)",
                transition: "all 420ms cubic-bezier(.22,.9,.3,1)",
                border: active
                  ? "1px solid rgba(255,255,255,0.12)"
                  : "1px solid rgba(255,255,255,0.06)",
                backgroundColor: "rgba(255,255,255,0.02)",
                pointerEvents: "none",
              }}
            >
              <Box
                component="img"
                src={it.src}
                alt={it.alt}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  filter: active
                    ? "saturate(1) contrast(1.03)"
                    : "grayscale(0.5) opacity(0.85)",
                }}
              />
              <Box
                sx={{
                  position: "absolute",
                  left: 10,
                  bottom: 8,
                  px: 0.75,
                  py: 0.35,
                  bgcolor: "rgba(0,0,0,0.4)",
                  color: "#fff",
                  fontSize: 11,
                  borderRadius: 1,
                  pointerEvents: "none",
                  opacity: active ? 1 : 0.6,
                }}
              >
                {`${i + 1} / ${images.length}`}
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* Main content */}
      <Box sx={{ position: "relative", zIndex: 3, maxWidth: 800 }}>
        <Box
          component="img"
          src={Logo}
          alt="Odyssey Logo"
          sx={{
            width: { xs: 160, md: 240 },
            mb: { xs: 1.5, md: 2 },
            filter: "drop-shadow(0 10px 28px rgba(0,0,0,0.36))",
            display: "block",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        />

        <Typography
          variant="h1"
          sx={{
            fontSize: { xs: "1.6rem", sm: "1.85rem", md: "3.2rem" },
            color: "#F3E2D4",
            letterSpacing: "0.01em",
            mb: { xs: 0.75, md: 1.5 },
            textShadow: "2px 2px 14px rgba(0,0,0,0.38)",
            fontWeight: 700,
          }}
        >
          {t("Odyssey of Wisdom")}
        </Typography>

        <Typography
          variant="h6"
          maxWidth={680}
          sx={{
            fontFamily: "Inter, sans-serif",
            fontSize: { xs: "0.95rem", md: "1.12rem" },
            color: "rgba(243,226,212,0.95)",
            letterSpacing: "0.01em",
            lineHeight: 1.6,
            mb: { xs: 3, md: 4 },
            textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          }}
        >
          {t(
            "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
          )}
        </Typography>

        <Button
          endIcon={<ExpandMoreIcon />}
          variant="contained"
          size="large"
          sx={{
            backgroundColor: "#F3E2D4",
            color: "#415E72",
            px: { xs: 4, md: 5 },
            py: 1.5,
            borderRadius: "30px",
            fontFamily: "Inter, sans-serif",
            fontWeight: 700,
            textTransform: "none",
            boxShadow: "0 10px 36px rgba(6,24,44,0.22)",
            transition: "transform 0.28s ease, box-shadow 0.28s ease",
            "&:hover": {
              transform: "translateY(-3px)",
              boxShadow: "0 14px 44px rgba(6,24,44,0.28)",
              backgroundColor: "#e9dbd1",
            },
          }}
          onClick={() =>
            document
              .getElementById("about")
              ?.scrollIntoView({ behavior: "smooth" })
          }
        >
          {t("Learn more")}
        </Button>
      </Box>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
