import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = (useRef < number) | (null > null);
  const { t } = useLanguage();

  // Preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Cycle every 4s
  useEffect(() => {
    // respect reduced motion
    const prefersReduced = window.matchMedia?.(
      "(prefers-reduced-motion: reduce)"
    ).matches;
    if (prefersReduced) return; // don't animate if user requested reduced motion

    if (intervalRef.current) window.clearInterval(intervalRef.current);
    intervalRef.current = window.setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => {
      if (intervalRef.current) window.clearInterval(intervalRef.current);
    };
  }, []);

  // helper: get a circular index
  const at = (i) => (index + i + images.length) % images.length;

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep the initial background you provided
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Subtle film behind text to keep readability without obscuring background */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
          // soft vertical vignette to anchor text
          background:
            "linear-gradient(180deg, rgba(20,30,40,0.08) 0%, rgba(20,30,40,0.18) 70%, rgba(20,30,40,0.34) 100%)",
        }}
      />

      {/* Decorative blurred round accent (keeps the same character as before) */}
      <Box
        sx={{
          position: "absolute",
          top: "-18%",
          right: "-6%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 1,
          filter: "blur(14px)",
        }}
      />

      {/* Polaroid stack - discrete, elegant photo treatment */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          bottom: { xs: 36, md: 48 },
          right: { xs: 16, md: 48 },
          width: { xs: 180, md: 260 },
          height: { xs: 220, md: 320 },
          zIndex: 2,
          display: "flex",
          alignItems: "flex-end",
          justifyContent: "center",
          pointerEvents: "none",
        }}
      >
        {/* subtle backing card to give depth */}
        <Box
          sx={{
            position: "absolute",
            width: "100%",
            height: "100%",
            bgcolor: "rgba(0,0,0,0.06)",
            borderRadius: 2,
            transform: "translateY(8px) scale(0.98)",
            filter: "blur(1px)",
            zIndex: 0,
          }}
        />

        {/* Show three overlapping cards: top (current), middle (next), back (next+1) */}
        {[0, 1, 2].map((slot) => {
          const img = images[at(slot)];
          // styling offsets for stacked look
          const rotate = slot === 0 ? -6 : slot === 1 ? -2 : 4;
          const translateY = slot === 0 ? 0 : slot === 1 ? 10 : 20;
          const scale = slot === 0 ? 1 : slot === 1 ? 0.98 : 0.96;
          const opacity = slot === 0 ? 1 : slot === 1 ? 0.86 : 0.62;
          const z = 10 + (3 - slot);

          return (
            <Box
              key={img + slot}
              sx={{
                position: "absolute",
                width: "100%",
                height: "100%",
                borderRadius: 2,
                zIndex: z,
                transform: `translateY(${translateY}px) rotate(${rotate}deg) scale(${scale})`,
                transition:
                  "transform 700ms cubic-bezier(.2,.8,.2,1), opacity 700ms ease",
                opacity,
                boxShadow:
                  "0 18px 36px rgba(6,12,34,0.28), inset 0 1px 0 rgba(255,255,255,0.03)",
                background: "#fff",
                display: "flex",
                flexDirection: "column",
                alignItems: "stretch",
                justifyContent: "stretch",
                overflow: "hidden",
                pointerEvents: "none",
              }}
            >
              {/* image area */}
              <Box
                component="img"
                src={img}
                alt={`hero-card-${slot}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  // make images discrete: slightly desaturated, warm tone, subtle vignette
                  filter:
                    "grayscale(0.04) saturate(0.9) contrast(0.98) brightness(0.98)",
                  transform: "translateZ(0)",
                }}
              />

              {/* polaroid-style caption strip (very minimal) */}
              <Box
                sx={{
                  position: "absolute",
                  left: "6%",
                  right: "6%",
                  bottom: "6%",
                  height: 28,
                  bgcolor: "rgba(243,226,212,0.95)",
                  borderRadius: 1,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  fontSize: 11,
                  color: "#415E72",
                  fontWeight: 600,
                  letterSpacing: 0.4,
                  boxShadow: "0 2px 6px rgba(0,0,0,0.12)",
                }}
              >
                {t("Erasmus glimpse")}
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* small dots for status (discrete) */}
      <Box
        sx={{
          position: "absolute",
          bottom: { xs: 20, md: 34 },
          right: { xs: 20 + 180, md: 48 + 260 },
          zIndex: 3,
          display: "flex",
          gap: 1,
          alignItems: "center",
          pointerEvents: "none",
        }}
      >
        {images.map((_, i) => (
          <Box
            key={i}
            sx={{
              width: 8,
              height: 8,
              borderRadius: "50%",
              bgcolor:
                i === index
                  ? "rgba(243,226,212,0.95)"
                  : "rgba(243,226,212,0.32)",
              boxShadow: i === index ? "0 2px 6px rgba(0,0,0,0.18)" : "none",
              transform: i === index ? "scale(1.15)" : "scale(1)",
              transition: "all 320ms ease",
            }}
          />
        ))}
      </Box>

      {/* Logo (keeps prominence) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.4))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.25rem", md: "3.5rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.38)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={600}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "1rem", md: "1.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.25)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave svg */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
