import React, { useEffect } from 'react'
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Link,
  Button,
  Stack,
  Card,
  CardContent,
  Divider,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

export default function ProjectDetail() {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()

  useEffect(() => {
    window.scrollTo({
	  top: 0,
	  left: 0,
	  behavior: 'auto',
  	})
  }, [])

  const goToProjects = () => {
    navigate('/#projects', { state: { fromProjectDetail: true } })
  }


  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn’t find what you’re looking for.
        </Typography>
        <Button variant="contained" onClick={() => navigate('/')} size="large" sx={{ borderRadius: 3 }}>
          Back to Home
        </Button>
      </Box>
    )
  }

  const extra = Array.isArray(project.images) ? project.images : []
  const allImages = [project.image, ...extra].slice(0, 3)

  return (
    <Box
      sx={{
        maxWidth: 960,
        mx: 'auto',
        px: { xs: 2, md: 4 },
        py: { xs: 4, md: 6 },
        backgroundColor: 'transparent',
      }}
    >
      <Breadcrumbs separator="›" aria-label="breadcrumb" sx={{ mb: 4 }}>
        <Link
          component={RouterLink}
          to="/"
          underline="none"
          sx={{
            display: 'flex',
            alignItems: 'center',
            color: '#5A3E61',
            fontWeight: 500,
            '&:hover': { color: '#6E4D74' },
          }}
        >
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} />
          Home
        </Link>
        <Link
		component="button"
		onClick={goToProjects}
		underline="none"
		sx={{
			color: '#5A3E61',
			fontWeight: 500,
			'&:hover': { color: '#6E4D74' },
			background: 'none',
			border: 'none',
			p: 0,
			font: 'inherit',
			cursor: 'pointer',
		}}
		>
		Projects
		</Link>
        <Typography sx={{ color: '#6E4D74', fontWeight: 700 }}>
          {project.title}
        </Typography>
      </Breadcrumbs>

      <Card
  elevation={1}
  sx={{
    borderRadius: 6,
    overflow: 'hidden',
    backgroundColor: '#FFFFFF',
    boxShadow: '0 8px 24px rgba(0,0,0,0.06)',
    animation: 'fadeSlideIn 0.5s ease',
    '@keyframes fadeSlideIn': {
      from: { opacity: 0, transform: 'translateY(20px)' },
      to: { opacity: 1, transform: 'translateY(0)' },
    },
  }}
>
  {/* Responsive Image Grid */}
  <Box sx={{ width: '100%', display: 'grid', gap: 1, backgroundColor: '#eee' }}>
    {allImages.length === 1 && (
      <Box
        component="img"
        src={allImages[0]}
        alt={project.title}
        sx={{
          width: '100%',
          height: { xs: 240, md: 420 },
          objectFit: 'cover',
        }}
      />
    )}

    {allImages.length === 2 && (
      <Box
        sx={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: 1,
          height: { xs: 240, md: 400 },
        }}
      >
        {allImages.map((img, i) => (
          <Box
            key={i}
            component="img"
            src={img}
            alt={`Project ${i + 1}`}
            sx={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
          />
        ))}
      </Box>
    )}

    {allImages.length >= 3 && (
      <Box
        sx={{
          display: 'grid',
          gridTemplateColumns: { xs: '1fr 1fr', md: '2fr 1fr' },
          gridTemplateRows: { xs: '1fr 1fr', md: '1fr 1fr' },
          gap: 1,
          height: { xs: 300, md: 400 },
        }}
      >
        <Box
          component="img"
          src={allImages[0]}
          alt="Primary"
          sx={{
            gridRow: { md: '1 / span 2' },
            width: '100%',
            height: '100%',
            objectFit: 'cover',
          }}
        />
        <Box
          component="img"
          src={allImages[1]}
          alt="Secondary 1"
          sx={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
          }}
        />
        <Box
          component="img"
          src={allImages[2]}
          alt="Secondary 2"
          sx={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
          }}
        />
      </Box>
    )}
  </Box>

  {/* Content */}
  <CardContent sx={{ px: { xs: 3, md: 5 }, py: { xs: 4, md: 5 } }}>
    <Typography
      variant="h4"
      sx={{
        fontWeight: 800,
        color: '#5A3E61',
        mb: 2,
        letterSpacing: '-0.02em',
        textAlign: 'left',
        fontSize: { xs: '1.75rem', md: '2.125rem' },
      }}
    >
      {project.title}
    </Typography>

    <Stack direction="row" spacing={3} mb={3} sx={{ color: '#6E4D74' }} flexWrap="wrap">
      <Stack direction="row" spacing={1} alignItems="center">
        <CalendarMonthIcon fontSize="small" />
        <Typography variant="body2">{project.date}</Typography>
      </Stack>
      <Stack direction="row" spacing={1} alignItems="center">
        <LocationOnIcon fontSize="small" />
        <Typography variant="body2">{project.location}</Typography>
      </Stack>
    </Stack>

    <Divider sx={{ borderColor: '#EFE6F0', mb: 3 }} />

    <Typography
      variant="body1"
      sx={{
        color: '#3A2F3B',
        lineHeight: 1.75,
        whiteSpace: 'pre-line',
        fontSize: '1.05rem',
      }}
      dangerouslySetInnerHTML={{ __html: project.description }}
    />

    <Box textAlign="center" mt={5}>
      <Button
        variant="outlined"
        onClick={goToProjects}
        sx={{
          borderRadius: 4,
          px: 4,
          py: 1.5,
          fontWeight: 600,
          color: '#5A3E61',
          borderColor: '#5A3E61',
          '&:hover': {
            backgroundColor: '#5A3E61',
            color: '#fff',
          },
        }}
      >
        ← Back to Projects
      </Button>
    </Box>
  </CardContent>
</Card>

    </Box>
  )
}
