import { AppBar, Toolbar, Button, Box } from '@mui/material'
import Logo from '../assets/logo2.png'

const Navbar = () => {
  const scrollTo = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' })
  }

  return (
    <AppBar
      position="sticky"
      elevation={4}
      sx={{
        backgroundColor: '#17313E',
        px: { xs: 2, md: 4 },
        py: 0.75,                     // less vertical padding
        boxShadow: '0 2px 12px rgba(0,0,0,0.25)',
        backdropFilter: 'saturate(180%) blur(6px)',
      }}
    >
      <Toolbar
        disableGutters
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          height: 56,                 // fixed smaller height
          maxWidth: 1200,
          mx: 'auto',
        }}
      >
        {/* Logo */}
        <Box
          onClick={() => scrollTo('home')}
          sx={{
            cursor: 'pointer',
            ml: { xs: 1, md: 0 },
            transition: 'transform 0.3s ease',
            '&:hover': { transform: 'scale(1.03) rotate(-2deg)' },
          }}
        >
          <img
            src={Logo}
            alt="Logo"
            style={{
              height: 48,               // smaller logo
              userSelect: 'none',
              filter: 'drop-shadow(0 0 3px rgba(255,255,255,0.2))',
              borderRadius: 6,
            }}
          />
        </Box>

        {/* Nav Links */}
        <Box display="flex" gap={2} mr={1}>
          {['about', 'projects', 'feedback'].map((id) => (
            <Button
              key={id}
              onClick={() => scrollTo(id)}
              sx={{
                color: '#F3E2D4',
                textTransform: 'none',
                fontWeight: 600,
                letterSpacing: '0.1em',
                fontSize: { xs: '0.8rem', md: '0.9rem' }, // smaller font
                px: 1.5,
                py: 0.5,
                borderRadius: 20,
                transition: 'all 0.2s ease',
                '&:hover': {
                  backgroundColor: '#415E72',
                  boxShadow: '0 2px 8px rgba(65,94,114,0.5)',
                  transform: 'translateY(-1px)',
                },
              }}
            >
              {id.charAt(0).toUpperCase() + id.slice(1)}
            </Button>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
  )
}

export default Navbar
