// src/components/Hero.jsx
import React from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

const Hero = () => {
  const { t } = useLanguage();

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* background images kept unchanged (not shown here) */}

      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.4))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography variant="h1" sx={{ fontSize: { xs: "2.25rem", md: "3.5rem" }, color: "#F3E2D4", letterSpacing: "0.04em", mb: 2, textShadow: "2px 2px 10px rgba(0,0,0,0.4)", zIndex: 4 }}>
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography variant="h6" maxWidth={600} sx={{ fontFamily: "Inter, sans-serif", fontSize: { xs: "1rem", md: "1.25rem" }, color: "#F3E2D4", letterSpacing: "0.02em", lineHeight: 1.6, mb: 4, textShadow: "1px 1px 6px rgba(0,0,0,0.25)", zIndex: 4 }}>
        {t("Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe.")}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document.getElementById("about")?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>
    </Box>
  );
};

export default Hero;
