import React from 'react'
import { useParams, Link as RouterLink, useNavigate } from 'react-router-dom'
import { projects } from '../data/projects'
import {
  Box,
  Typography,
  Breadcrumbs,
  Link,
  Button,
  Stack,
  Divider,
  Card,
  CardContent,
  useTheme,
} from '@mui/material'
import HomeIcon from '@mui/icons-material/Home'
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth'
import LocationOnIcon from '@mui/icons-material/LocationOn'

const slugify = (text) =>
  text.toLowerCase().replace(/[^\w ]+/g, '').replace(/ +/g, '-')

const projectsWithSlugs = projects.map((p) => ({
  ...p,
  slug: slugify(p.title),
}))

export default function ProjectDetail() {
  const { slug } = useParams()
  const project = projectsWithSlugs.find((p) => p.slug === slug)
  const navigate = useNavigate()
  const theme = useTheme()

  // Back button behaviour: navigate + scroll
  const goToProjects = () => {
    navigate('/')
    setTimeout(() => {
      document.getElementById('projects')?.scrollIntoView({ behavior: 'smooth' })
    }, 50)
  }

  if (!project) {
    return (
      <Box
        sx={{
          minHeight: '80vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          px: 3,
        }}
      >
        <Typography variant="h4" color="error" gutterBottom>
          Project Not Found
        </Typography>
        <Typography variant="body1" sx={{ mb: 3 }}>
          Sorry, we couldn’t find what you’re looking for.
        </Typography>
        <Button variant="contained" onClick={() => navigate('/')} size="large" sx={{ borderRadius: 3 }}>
          Back to Home
        </Button>
      </Box>
    )
  }

  // Images array
  const extra = Array.isArray(project.images) ? project.images : []
  const allImages = [project.image, ...extra].slice(0, 3)

  return (
    <Box
      sx={{
        maxWidth: 920,
        mx: 'auto',
        px: { xs: 2, md: 6 },
        pt: { xs: 4, md: 8 },
        pb: 8,
      }}
    >
      {/* Breadcrumbs */}
      <Breadcrumbs separator="›" aria-label="breadcrumb" sx={{ mb: 4 }}>
        <Link
          component={RouterLink}
          to="/"
          underline="none"
          sx={{
            display: 'flex',
            alignItems: 'center',
            color: '#5A3E61',
            fontWeight: 600,
            '&:hover': { color: '#6E4D74' },
          }}
        >
          <HomeIcon fontSize="small" sx={{ mr: 0.5 }} /> Home
        </Link>

        <Link
          component="a"
          href="/#projects"
          underline="none"
          sx={{
            color: '#5A3E61',
            fontWeight: 600,
            '&:hover': { color: '#6E4D74' },
          }}
        >
          Projects
        </Link>

        <Typography sx={{ color: '#6E4D74', fontWeight: 700 }}>
          {project.title}
        </Typography>
      </Breadcrumbs>

      {/* Title & Meta */}
      <Typography
        variant="h3"
        component="h1"
        sx={{ fontWeight: 800, color: '#5A3E61', mb: 2 }}
      >
        {project.title}
      </Typography>
      <Stack direction="row" spacing={4} mb={5} flexWrap="wrap">
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#6E4D74' }}>
          <CalendarMonthIcon /> <Typography>{project.date}</Typography>
        </Stack>
        <Stack direction="row" spacing={1} alignItems="center" sx={{ color: '#6E4D74' }}>
          <LocationOnIcon /> <Typography>{project.location}</Typography>
        </Stack>
      </Stack>

      {/* Images Gallery */}
      <Stack spacing={2} mb={6}>
        {/* Main image */}
        <Box
          component="img"
          src={allImages[0]}
          alt={`${project.title} main`}
          loading="lazy"
          sx={{
            width: '100%',
            height: 'auto',
            objectFit: 'contain',
            borderRadius: 2,
          }}
        />
        {/* Thumbnails */}
        {allImages.length > 1 && (
          <Stack direction="row" spacing={2}>
            {allImages.slice(1).map((img, i) => (
              <Box
                key={i}
                component="img"
                src={img}
                alt={`${project.title} ${i + 2}`}
                loading="lazy"
                sx={{
                  width: 100,
                  height: 'auto',
                  objectFit: 'contain',
                  borderRadius: 1,
                  cursor: 'pointer',
                  transition: 'transform 0.2s',
                  '&:hover': { transform: 'scale(1.05)' },
                }}
                onClick={() =>
                  document
                    .querySelector('img[alt^="' + project.title + ' main"]')
                    ?.scrollIntoView({ behavior: 'smooth' })
                }
              />
            ))}
          </Stack>
        )}
      </Stack>

      <Divider sx={{ mb: 6, borderColor: '#EFE6F0' }} />

      {/* Description Card */}
      <Card elevation={2} sx={{ borderRadius: 3, bgcolor: 'transparent' }}>
        <CardContent>
          <Typography
            variant="body1"
            sx={{ color: '#3A2F3B', lineHeight: 1.75 }}
            dangerouslySetInnerHTML={{ __html: project.description }}
          />
        </CardContent>
      </Card>

      {/* Back to Projects */}
      <Box textAlign="center" mt={6}>
        <Button
          variant="outlined"
          onClick={goToProjects}
          sx={{
            borderRadius: 3,
            color: '#5A3E61',
            borderColor: '#5A3E61',
            px: 5,
            py: 1.5,
            fontWeight: 600,
            transition: 'all 0.3s',
            '&:hover': {
              backgroundColor: '#5A3E61',
              color: '#fff',
            },
          }}
        >
          ← Back to Projects
        </Button>
      </Box>
    </Box>
  )
}
