import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const paneLayout = [
  // relative positions and rotation for an asymmetrical cluster
  { top: "8%", right: "6%", width: 220, height: 140, rotate: -6, z: 6 },
  { top: "20%", right: "16%", width: 260, height: 170, rotate: 6, z: 5 },
  { top: "42%", right: "8%", width: 200, height: 130, rotate: -3, z: 7 },
  { top: "60%", right: "18%", width: 240, height: 150, rotate: 4, z: 4 },
  { top: "74%", right: "6%", width: 190, height: 120, rotate: -8, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // respects user preference to reduce motion
  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  useEffect(() => {
    clearInterval(intervalRef.current);
    if (!prefersReducedMotion) {
      intervalRef.current = setInterval(() => {
        setIndex((i) => (i + 1) % images.length);
      }, 4000);
    } else {
      // if reduced motion, still rotate but much slower and without transforms
      intervalRef.current = setInterval(() => {
        setIndex((i) => (i + 1) % images.length);
      }, 8000);
    }

    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient background as the base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Discrete image cluster (keeps images subtle and decorative) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
          // hide cluster on small screens to prioritise readability
          display: { xs: "none", md: "block" },
        }}
      >
        {/* decorative soft veil so images read as detail */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.03) 0%, rgba(20,30,40,0.06) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {/* panes */}
        {paneLayout.map((p, paneIndex) => {
          // each pane shows a different image from the rotating index (offset per pane)
          const imgIdx = (index + paneIndex) % images.length;
          const src = images[imgIdx];

          return (
            <Box
              key={paneIndex}
              sx={{
                position: "absolute",
                top: p.top,
                right: p.right,
                width: p.width,
                height: p.height,
                transform: `rotate(${p.rotate}deg) translateZ(0)`,
                zIndex: p.z,
                borderRadius: 12,
                overflow: "hidden",
                boxShadow:
                  "0 10px 30px rgba(3,12,24,0.35), inset 0 1px 0 rgba(255,255,255,0.03)",
                border: "1px solid rgba(243,226,212,0.12)",
                backgroundColor: "rgba(10,14,20,0.04)",
                transition:
                  "transform 600ms cubic-bezier(.2,.9,.2,1), opacity 600ms ease",
                opacity: 0.95,
                // subtle float animation on active pane (if motion allowed)
                ...(prefersReducedMotion
                  ? {}
                  : {
                      ":hover": {
                        transform: `rotate(${p.rotate}deg) translateY(-6px) scale(1.02)`,
                      },
                    }),
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`hero-thumb-${paneIndex}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // make them discrete: slightly desaturated, faint opacity, warm tint
                  filter: "grayscale(12%) saturate(85%) contrast(0.98)",
                  opacity: 0.92,
                  transition: "opacity 600ms ease, transform 12000ms linear",
                  // long, subtle internal scale for that designer feel
                  transform: prefersReducedMotion ? "scale(1)" : "scale(1.03)",
                }}
              />

              {/* subtle tasteful caption stripe (decorative) */}
              <Box
                sx={{
                  position: "absolute",
                  left: 8,
                  bottom: 8,
                  px: 1.25,
                  py: 0.5,
                  bgcolor: "rgba(243,226,212,0.14)",
                  backdropFilter: "blur(2px)",
                  borderRadius: 4,
                  pointerEvents: "none",
                  display: "flex",
                  alignItems: "center",
                  gap: 1,
                }}
              >
                <Box
                  sx={{
                    width: 8,
                    height: 8,
                    borderRadius: "50%",
                    bgcolor: "rgba(65,94,114,0.85)",
                    boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
                  }}
                />
                <Box
                  component="span"
                  sx={{
                    fontSize: 11,
                    color: "rgba(243,226,212,0.95)",
                    fontWeight: 600,
                    fontFamily: "Inter, sans-serif",
                    letterSpacing: "0.02em",
                  }}
                >
                  Odyssey
                </Box>
              </Box>
            </Box>
          );
        })}
      </Box>

      {/* Decorative soft circle accent (keeps original vibe) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 200, md: 300 },
          height: { xs: 200, md: 300 },
          bgcolor: "rgba(243,226,212,0.22)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(12px)",
        }}
      />

      {/* content (above the image cluster) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.35)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.975rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 22px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom svg wave (keeps your original) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
