import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Polaroid = ({ src, alt, style, isActive }) => (
  <Box
    component="img"
    src={src}
    alt={alt}
    sx={{
      width: { xs: 110, md: 160 },
      height: { xs: 78, md: 110 },
      objectFit: "cover",
      borderRadius: 6,
      boxShadow: isActive
        ? "0 18px 40px rgba(8,18,34,0.35)"
        : "0 10px 24px rgba(8,18,34,0.22)",
      border: "6px solid rgba(243,226,212,0.95)",
      transformOrigin: "center",
      transition:
        "transform 450ms cubic-bezier(.2,.9,.2,1), opacity 450ms ease, filter 450ms ease",
      opacity: isActive ? 1 : 0.85,
      filter: "grayscale(40%) contrast(0.95) saturate(0.9) brightness(0.95)",
      ...style,
    }}
  />
);

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle index every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // pick 3 images to show in the stack: current, next, next+1
  const stack = [
    images[index % images.length],
    images[(index + 1) % images.length],
    images[(index + 2) % images.length],
  ];

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        background:
          "linear-gradient(120deg, rgba(197,176,205,0.12) 12%, rgba(65,94,114,0.10) 40%, rgba(65,94,114,0.14) 100%)",
      }}
    >
      {/* Subtle vignette for depth */}
      <Box
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
          background:
            "radial-gradient(40% 50% at 50% 30%, rgba(255,255,255,0.02), transparent 20%), linear-gradient(180deg, rgba(10,14,18,0.02), rgba(10,14,18,0.06))",
        }}
      />

      {/* Decorative soft circle */}
      <Box
        sx={{
          position: "absolute",
          top: { xs: "-12%", md: "-18%" },
          right: { xs: "-6%", md: "-10%" },
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.14)",
          borderRadius: "50%",
          zIndex: 1,
          filter: "blur(14px)",
        }}
      />

      {/* Logo & text (above image accent) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 170, md: 240 },
          mb: 2,
          filter: "drop-shadow(0 10px 24px rgba(0,0,0,0.28))",
          position: "relative",
          zIndex: 5,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.2rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          mb: 1,
          textShadow: "0 6px 22px rgba(0,0,0,0.35)",
          fontWeight: 700,
          zIndex: 5,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={680}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "rgba(243,226,212,0.95)",
          letterSpacing: "0.01em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "0 4px 18px rgba(0,0,0,0.22)",
          zIndex: 5,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 8px 28px rgba(18,28,42,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 12px 40px rgba(18,28,42,0.24)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 5,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* Polaroid stack accent — discrete, tasteful, cycles images */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          right: { xs: "50%", md: 48 },
          bottom: { xs: 20, md: 36 },
          transform: { xs: "translateX(50%)", md: "none" },
          display: "flex",
          alignItems: "center",
          gap: { xs: 0, md: -6 },
          zIndex: 4,
          pointerEvents: "none",
        }}
      >
        {/* Back-most: slightly rotated and smaller */}
        <Polaroid
          src={stack[2]}
          alt="thumb-back"
          isActive={false}
          style={{
            transform: "rotate(-10deg) translateY(6px) scale(0.96)",
            opacity: 0.72,
            border: "5px solid rgba(243,226,212,0.92)",
          }}
        />

        {/* Middle: gentle offset */}
        <Polaroid
          src={stack[1]}
          alt="thumb-mid"
          isActive={false}
          style={{
            transform: "rotate(6deg) translateY(2px)",
            opacity: 0.85,
          }}
        />

        {/* Front-most: active, slightly larger and clearer */}
        <Polaroid
          src={stack[0]}
          alt="thumb-front"
          isActive={true}
          style={{
            transform: "rotate(-3deg) translateY(-2px) scale(1.02)",
            opacity: 1,
          }}
        />
      </Box>

      {/* small caption under photos when hovered (optional — kept decorative) */}
      <Box
        sx={{
          position: "absolute",
          right: { xs: "50%", md: 48 },
          bottom: { xs: -6, md: 6 },
          transform: { xs: "translateX(50%)", md: "none" },
          zIndex: 4,
          pointerEvents: "none",
          color: "rgba(243,226,212,0.9)",
          fontSize: 12,
          opacity: 0.9,
          letterSpacing: "0.02em",
        }}
      >
        {t("Moments from Erasmus+")}
      </Box>

      {/* bottom decorative wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
