import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button, useTheme } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const cardLayouts = [
  // left column (stacked)
  {
    top: "18%",
    left: { xs: "6%", md: "4%" },
    width: { xs: 110, md: 160 },
    rotate: -6,
    opacity: 0.2,
    z: 1,
  },
  {
    top: "36%",
    left: { xs: "3%", md: "2%" },
    width: { xs: 130, md: 180 },
    rotate: 4,
    opacity: 0.18,
    z: 1,
  },
  {
    top: "56%",
    left: { xs: "6%", md: "5%" },
    width: { xs: 100, md: 140 },
    rotate: -4,
    opacity: 0.16,
    z: 1,
  },

  // right column
  {
    top: "22%",
    right: { xs: "6%", md: "4%" },
    width: { xs: 120, md: 170 },
    rotate: 6,
    opacity: 0.18,
    z: 1,
  },
  {
    top: "50%",
    right: { xs: "8%", md: "6%" },
    width: { xs: 140, md: 190 },
    rotate: -5,
    opacity: 0.22,
    z: 1,
  },

  // small accent near logo (optional; not in cardLayouts to keep five visible)
];

const Hero = () => {
  const { t } = useLanguage();
  const theme = useTheme();
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s
  useEffect(() => {
    // respect reduced motion: if user prefers reduced motion, don't animate/cycle
    const prefersReduced =
      window.matchMedia &&
      window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    if (prefersReduced) return;

    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // helper: produce card style for left/right position variants
  const renderCards = () =>
    cardLayouts.slice(0, 5).map((layout, i) => {
      // chosen image index: stagger the image offset so the collage feels more dynamic
      // (e.g. each card shows (index + i) % images.length)
      const imgSrc = images[(index + i) % images.length];

      // compute dynamic left/right props because MUI sx won't accept both left and right keys together easily in object form
      const posStyles = {};
      if (layout.left) posStyles.left = layout.left;
      if (layout.right) posStyles.right = layout.right;

      return (
        <Box
          key={i}
          aria-hidden="true"
          sx={{
            position: "absolute",
            top: layout.top,
            ...posStyles,
            width: layout.width,
            height: {
              xs: `calc(${layout.width}.6)`,
              md: `calc(${layout.width}px * 0.65)`,
            }, // keep aspect
            zIndex: layout.z,
            transform: `rotate(${layout.rotate}deg)`,
            borderRadius: 2,
            overflow: "hidden",
            boxShadow: (theme) => `0 10px 30px rgba(7,12,20,0.18)`,
            border: "1px solid rgba(255,255,255,0.06)",
            backgroundColor: "rgba(0,0,0,0.06)",
            pointerEvents: "none",
            // subtle glassy frame effect
            backdropFilter: "saturate(120%)",
            // transition and crossfade for image inside
            "& .photo": {
              width: "100%",
              height: "100%",
              objectFit: "cover",
              display: "block",
              opacity: layout.opacity,
              transition:
                "opacity 900ms ease-in-out, transform 900ms ease-in-out",
              transform: "scale(1.03)",
            },
            // a thin inner vignette to make the card feel discrete
            "&::after": {
              content: '""',
              position: "absolute",
              inset: 0,
              borderRadius: 8,
              boxShadow: "inset 0 30px 80px rgba(10,14,18,0.15)",
              pointerEvents: "none",
            },
            // responsive tweaks
            "@media (max-width:600px)": {
              transform: `rotate(${layout.rotate / 2}deg)`,
              width: `calc(${
                typeof layout.width === "object"
                  ? layout.width.xs
                  : layout.width
              }px * 0.9)`,
            },
          }}
        >
          <Box
            component="img"
            src={imgSrc}
            alt={`accent-${i}`}
            className="photo"
            sx={{
              // keep image crisp â€” reduce any blur filters
              filter: "none",
            }}
          />
        </Box>
      );
    });

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original background as requested:
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative soft textured layer â€” subtle pattern for depth (keeps photos discrete) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
          // a very gentle noise + color overlay that keeps contrast but keeps photos discreet
          background:
            "linear-gradient(180deg, rgba(65,94,114,0.08) 0%, rgba(65,94,114,0.12) 60%, rgba(25,34,42,0.16) 100%)",
        }}
      />

      {/* Photo collage (as discrete floating cards) */}
      <Box
        aria-hidden="true"
        sx={{ position: "absolute", inset: 0, zIndex: 1 }}
      >
        {renderCards()}

        {/* An extra small, crisp circular accent near the logo (sharp, high-opacity) */}
        <Box
          sx={{
            position: "absolute",
            top: { xs: "10%", md: "12%" },
            left: { xs: "50%", md: "46%" },
            transform: "translateX(-50%)",
            width: { xs: 72, md: 96 },
            height: { xs: 72, md: 96 },
            borderRadius: "50%",
            overflow: "hidden",
            boxShadow: "0 8px 30px rgba(7,12,20,0.22)",
            border: "2px solid rgba(243,226,212,0.12)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        >
          <Box
            component="img"
            src={images[index % images.length]}
            alt="accent-logo"
            sx={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              transform: "scale(1.04)",
            }}
          />
        </Box>
      </Box>

      {/* Decorative floating soft circle (keeps original visual cue) */}
      <Box
        sx={{
          position: "absolute",
          top: "-16%",
          right: "-8%",
          width: { xs: 180, md: 260 },
          height: { xs: 180, md: 260 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(36px)",
          pointerEvents: "none",
        }}
      />

      {/* Content (logo + text + button) on top */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 160, md: 240 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.3rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.5,
          textShadow: "2px 2px 12px rgba(0,0,0,0.45)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.18rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.28)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.22)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 10px 30px rgba(0,0,0,0.30)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
