import { Typography, Grid, Card, CardContent, Avatar } from "@mui/material";

const feedbackList = [
  {
    name: "Maria K.",
    comment: "It was life-changing! I met so many people and learned a lot.",
    image: "/assets/maria.jpg",
  },
  // Add more feedback...
];

export default function Feedback() {
  return (
    <>
      <Typography variant="h4" gutterBottom>Feedback</Typography>
      <Grid container spacing={4}>
        {feedbackList.map((f, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card>
              <CardContent>
                <Avatar src={f.image} alt={f.name} sx={{ width: 56, height: 56, mb: 2 }} />
                <Typography variant="body1" gutterBottom>"{f.comment}"</Typography>
                <Typography variant="subtitle2" color="text.secondary">{f.name}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </>
  );
}
