import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const DESKTOP_POSITIONS = [
  { right: 48, top: "10%", rotate: -8, scale: 0.96 },
  { right: 12, top: "22%", rotate: 6, scale: 0.94 },
  { right: 22, top: "46%", rotate: -4, scale: 1.02 }, // center (will pop when active)
  { right: 50, top: "60%", rotate: 8, scale: 0.92 },
  { right: 14, top: "76%", rotate: -10, scale: 0.9 },
];

// styles for mobile ribbon (horizontal)
const MOBILE_POSITIONS = [
  { leftPct: 2 },
  { leftPct: 22 },
  { leftPct: 42 },
  { leftPct: 62 },
  { leftPct: 82 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const [paused, setPaused] = useState(false);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle index every 4s (pauses on hover)
  useEffect(() => {
    clearInterval(intervalRef.current);
    if (!paused) {
      intervalRef.current = setInterval(() => {
        setIndex((i) => (i + 1) % images.length);
      }, 4000);
    }
    return () => clearInterval(intervalRef.current);
  }, [paused]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // kept your original background as requested
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative photo collage — desktop: stacked floating frames on the right;
          mobile: condensed ribbon at bottom-left area. */}
      <Box
        aria-hidden="true"
        onMouseEnter={() => setPaused(true)}
        onMouseLeave={() => setPaused(false)}
        sx={{
          position: "absolute",
          right: { xs: 8, md: 48 },
          top: { xs: "auto", md: 48 },
          bottom: { xs: 18, md: "auto" },
          width: { xs: "90%", md: 360 },
          height: { xs: 96, md: "80%" },
          pointerEvents: "none", // decorative
          zIndex: 1,
        }}
      >
        {/* Desktop stack (visible md+) */}
        <Box
          sx={{
            display: { xs: "none", md: "block" },
            position: "relative",
            width: "100%",
            height: "100%",
          }}
        >
          {images.map((src, i) => {
            const pos = DESKTOP_POSITIONS[i % DESKTOP_POSITIONS.length];
            const isActive = i === index;
            return (
              <Box
                key={src}
                component="img"
                src={src}
                alt=""
                aria-hidden="true"
                sx={{
                  position: "absolute",
                  right: pos.right,
                  top: pos.top,
                  width: 220,
                  height: 140,
                  objectFit: "cover",
                  borderRadius: 2,
                  border: "1px solid rgba(255,255,255,0.12)",
                  boxShadow: isActive
                    ? "0 18px 40px rgba(6,24,44,0.48)"
                    : "0 8px 18px rgba(6,24,44,0.28)",
                  transform: `rotate(${pos.rotate}deg) scale(${
                    isActive ? pos.scale * 1.03 : pos.scale
                  })`,
                  transition:
                    "transform 600ms cubic-bezier(.2,.9,.2,1), opacity 450ms ease, box-shadow 600ms ease",
                  opacity: isActive ? 1 : 0.36,
                  filter: isActive
                    ? "saturate(1.06) contrast(1.02) brightness(0.98)"
                    : "grayscale(0.18) contrast(0.96) brightness(0.95)",
                  zIndex: isActive ? 6 : 3,
                  transformOrigin: "center center",
                }}
              />
            );
          })}
          {/* subtle frame glow to distinguish from background */}
          <Box
            sx={{
              position: "absolute",
              inset: 0,
              zIndex: 2,
              pointerEvents: "none",
              background:
                "linear-gradient(180deg, rgba(243,226,212,0.02), rgba(25,34,42,0.03))",
              borderRadius: 2,
            }}
          />
        </Box>

        {/* Mobile ribbon (visible xs-sm) */}
        <Box
          sx={{
            display: { xs: "block", md: "none" },
            position: "relative",
            width: "100%",
            height: "100%",
            px: 1,
          }}
        >
          {images.map((src, i) => {
            const pos = MOBILE_POSITIONS[i % MOBILE_POSITIONS.length];
            const isActive = i === index;
            return (
              <Box
                key={src + "-mob"}
                component="img"
                src={src}
                alt=""
                aria-hidden="true"
                sx={{
                  position: "absolute",
                  left: `${pos.leftPct}%`,
                  bottom: 0,
                  width: 96,
                  height: 64,
                  objectFit: "cover",
                  borderRadius: 1.25,
                  border: "1px solid rgba(255,255,255,0.10)",
                  boxShadow: isActive
                    ? "0 10px 24px rgba(6,24,44,0.42)"
                    : "0 4px 10px rgba(6,24,44,0.2)",
                  transform: `translateX(-50%) scale(${
                    isActive ? 1.06 : 0.94
                  })`,
                  transition: "transform 450ms ease, opacity 450ms ease",
                  opacity: isActive ? 1 : 0.48,
                  filter: isActive
                    ? "none"
                    : "grayscale(0.25) brightness(0.92)",
                  zIndex: isActive ? 6 : 3,
                }}
              />
            );
          })}
        </Box>
      </Box>

      {/* Main content (logo + text + CTA) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.38))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.4rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 10px rgba(0,0,0,0.42)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={620}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.18rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.26)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* Decorative bottom wave — preserved */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
