import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import { keyframes } from "@emotion/react";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

// gentle floating motion for tiles
const float = keyframes`
  0% { transform: translateY(0) rotate(-1deg); }
  50% { transform: translateY(-8px) rotate(1deg); }
  100% { transform: translateY(0) rotate(-1deg); }
`;

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle every 4s
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // positions for the three decorative tiles (desktop first; mobile will stack)
  const tileConfigs = [
    {
      // left, larger
      sx: {
        left: { xs: "6%", md: "6%" },
        top: { xs: "12%", md: "8%" },
        width: { xs: "34%", md: "34%" },
        height: { xs: "26%", md: "46%" },
        transform: { md: "rotate(-7deg)" },
      },
    },
    {
      // center, small
      sx: {
        left: { xs: "48%", md: "52%" },
        top: { xs: "6%", md: "14%" },
        width: { xs: "30%", md: "28%" },
        height: { xs: "22%", md: "40%" },
        transform: { md: "rotate(6deg)" },
      },
    },
    {
      // right, medium
      sx: {
        right: { xs: "6%", md: "8%" },
        bottom: { xs: "6%", md: "6%" },
        width: { xs: "36%", md: "28%" },
        height: { xs: "24%", md: "38%" },
        transform: { md: "rotate(-4deg)" },
      },
    },
  ];

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient fallback
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative photo-tiles layer (discrete, low-opacity) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* soft tonal overlay to integrate photos with gradient but keep them discrete */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 1,
            pointerEvents: "none",
            background:
              "linear-gradient(180deg, rgba(65,94,114,0.18) 0%, rgba(65,94,114,0.28) 60%, rgba(25,34,42,0.38) 100%)",
            mixBlendMode: "normal",
          }}
        />

        {/* render three tiles; each tile shows a different image from the rotating set (staggered) */}
        {tileConfigs.map((cfg, i) => {
          // cycle images but stagger them so background feels dynamic
          const imgSrc = images[(index + i) % images.length];
          return (
            <Box
              key={i}
              component="figure"
              sx={{
                position: "absolute",
                zIndex: 0,
                margin: 0,
                // position config (left/top/right/bottom) spread from tileConfigs
                ...cfg.sx,
                // visual style: subtle border, soft shadow, rounded corners, and low opacity
                borderRadius: 2,
                overflow: "hidden",
                border: "1px solid rgba(243,226,212,0.06)",
                boxShadow: "0 8px 30px rgba(4,12,20,0.18)",
                // make them subtle: low opacity and desaturated look
                opacity: { xs: 0.14, md: 0.18 },
                transition: "opacity 700ms ease, transform 700ms ease",
                // small floating motion
                animation: `${float} 9s ease-in-out ${i * 0.6}s infinite`,
                // ensure they sit behind content but above base overlay
                zIndex: 0,
              }}
            >
              <Box
                component="img"
                src={imgSrc}
                alt={`decorative-${i}`}
                sx={{
                  display: "block",
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  // make images discrete: desaturate a little, slightly darker, preserve sharpness
                  filter:
                    "grayscale(22%) saturate(70%) contrast(0.95) brightness(0.92)",
                  // subtle blend so details don't fight with text
                  mixBlendMode: "normal",
                }}
              />
              {/* faint paper-like caption bar for elegance (invisible but provides texture) */}
              <Box
                sx={{
                  position: "absolute",
                  left: 0,
                  right: 0,
                  bottom: 0,
                  height: "8%",
                  background:
                    "linear-gradient(180deg, rgba(255,255,255,0.00), rgba(255,255,255,0.02))",
                  pointerEvents: "none",
                }}
              />
            </Box>
          );
        })}

        {/* a subtle grain layer to gently texture the background (very faint) */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            zIndex: 2,
            opacity: 0.02,
            backgroundImage:
              "radial-gradient(rgba(255,255,255,0.02) 1px, transparent 1px)",
            backgroundSize: "12px 12px",
            pointerEvents: "none",
          }}
        />
      </Box>

      {/* soft decorative circle (keeps original vibe) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 200, md: 300 },
          height: { xs: 200, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(14px)",
          pointerEvents: "none",
        }}
      />

      {/* Foreground content (logo + text) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 180, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 8px 22px rgba(0,0,0,0.45))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2rem", md: "3.2rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 12px rgba(0,0,0,0.45)",
          zIndex: 4,
          fontWeight: 700,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.12rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 18px rgba(0,0,0,0.22)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 10px 30px rgba(0,0,0,0.32)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* decorative bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
