import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const paneLayout = [
  // desktop absolute positions (keeps the asymmetrical editorial look)
  { top: "8%", right: "6%", width: 220, height: 140, rotate: -6, z: 6 },
  { top: "20%", right: "16%", width: 260, height: 170, rotate: 6, z: 5 },
  { top: "42%", right: "8%", width: 200, height: 130, rotate: -3, z: 7 },
  { top: "60%", right: "18%", width: 240, height: 150, rotate: 4, z: 4 },
  { top: "74%", right: "6%", width: 190, height: 120, rotate: -8, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images for smooth swaps
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // reduced motion preference
  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  useEffect(() => {
    clearInterval(intervalRef.current);
    const delay = prefersReducedMotion ? 8000 : 4000;
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, delay);
    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // original gradient background kept as baseline
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* -------------------------
          Discrete image cluster
          - Desktop: absolute asymmetrical panes (decorative)
          - Mobile: small horizontal strip under logo (keeps images visible)
         ------------------------- */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
        }}
      >
        {/* faint veil keeps photos decorative and subtle */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.03) 0%, rgba(20,30,40,0.055) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {/* DESKTOP: asymmetrical absolute panes */}
        <Box
          sx={{
            display: { xs: "none", md: "block" },
            position: "absolute",
            inset: 0,
            zIndex: 3,
            pointerEvents: "none",
          }}
        >
          {paneLayout.map((p, paneIndex) => {
            const imgIdx = (index + paneIndex) % images.length;
            const src = images[imgIdx];
            return (
              <Box
                key={paneIndex}
                sx={{
                  position: "absolute",
                  top: p.top,
                  right: p.right,
                  width: p.width,
                  height: p.height,
                  transform: `rotate(${p.rotate}deg) translateZ(0)`,
                  zIndex: p.z,
                  borderRadius: 12,
                  overflow: "hidden",
                  boxShadow:
                    "0 12px 30px rgba(2,10,18,0.35), inset 0 1px 0 rgba(255,255,255,0.03)",
                  border: "1px solid rgba(243,226,212,0.10)",
                  transition:
                    "transform 520ms cubic-bezier(.2,.9,.2,1), box-shadow 400ms ease, opacity 420ms ease",
                  opacity: 0.98,
                  pointerEvents: "none",
                  // hover float only on desktop and when motion isn't reduced
                  ...(prefersReducedMotion
                    ? {}
                    : {
                        ":hover": {
                          transform: `rotate(${p.rotate}deg) translateY(-8px) scale(1.03)`,
                        },
                      }),
                }}
              >
                {/* image */}
                <Box
                  component="img"
                  src={src}
                  alt={`hero-thumb-${paneIndex}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    // discrete look: slightly muted + warm vignette overlay
                    filter: "saturate(0.85) contrast(0.98)",
                    transform: prefersReducedMotion ? "none" : "scale(1.03)",
                    transition: "transform 12000ms linear, filter 600ms ease",
                  }}
                />

                {/* subtle inner overlay for consistent tone (no text) */}
                <Box
                  sx={{
                    position: "absolute",
                    inset: 0,
                    background:
                      "linear-gradient(180deg, rgba(255,245,238,0.02), rgba(20,30,40,0.06))",
                    mixBlendMode: "normal",
                    pointerEvents: "none",
                  }}
                />
              </Box>
            );
          })}
        </Box>

        {/* MOBILE: small horizontal strip under logo (visible on xs) */}
        <Box
          sx={{
            display: { xs: "flex", md: "none" },
            position: "absolute",
            left: "50%",
            bottom: 28,
            transform: "translateX(-50%)",
            gap: 10,
            zIndex: 3,
            pointerEvents: "none",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          {images.slice(0, 3).map((src, i) => {
            const imgIdx = (index + i) % images.length;
            const s = images[imgIdx];
            return (
              <Box
                key={i}
                sx={{
                  width: 88,
                  height: 60,
                  borderRadius: 8,
                  overflow: "hidden",
                  flex: "0 0 auto",
                  boxShadow: "0 8px 20px rgba(2,10,18,0.25)",
                  border: "1px solid rgba(243,226,212,0.10)",
                  transition: "transform 360ms ease, opacity 360ms ease",
                }}
              >
                <Box
                  component="img"
                  src={s}
                  alt={`mobile-thumb-${i}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    filter: "saturate(0.86) contrast(0.98)",
                  }}
                />
                {/* micro overlay for tone */}
                <Box
                  sx={{
                    position: "absolute",
                    inset: 0,
                    background:
                      "linear-gradient(180deg, rgba(255,255,255,0.01), rgba(10,14,20,0.06))",
                    pointerEvents: "none",
                  }}
                />
              </Box>
            );
          })}
        </Box>
      </Box>

      {/* Decorative soft circle accent */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 4,
          filter: "blur(12px)",
          pointerEvents: "none",
        }}
      />

      {/* Main content / logo */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: { xs: 1.5, md: 2 },
          filter: "drop-shadow(0 8px 20px rgba(0,0,0,0.36))",
          position: "relative",
          zIndex: 5,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.035em",
          mb: 1.25,
          textShadow: "2px 2px 12px rgba(0,0,0,0.34)",
          zIndex: 5,
          fontWeight: 700,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={720}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 3.5,
          textShadow: "1px 1px 8px rgba(0,0,0,0.20)",
          zIndex: 5,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 700,
          textTransform: "none",
          boxShadow: "0 8px 20px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 10px 28px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 5,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave (original) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 6,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
