import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const PHOTO_CONFIG = [
  // positions for each decorative card (percentage-based)
  { top: "8%", left: "6%", w: 180, h: 120, rotate: "-6deg" },
  { top: "12%", right: "8%", w: 200, h: 130, rotate: "4deg" },
  { bottom: "18%", left: "10%", w: 220, h: 140, rotate: "-3deg" },
  { bottom: "10%", right: "12%", w: 190, h: 130, rotate: "6deg" },
  {
    top: "45%",
    left: "50%",
    transform: "translateX(-50%)",
    w: 260,
    h: 160,
    rotate: "0deg",
  },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // cycle active image every 4 seconds
  useEffect(() => {
    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);
    return () => clearInterval(intervalRef.current);
  }, []);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep your original gradient as base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative photo cards layer (subtle, discrete, non-interactive) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* soft vignette to ground content */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "radial-gradient(80% 60% at 50% 45%, rgba(255,255,255,0.02), rgba(0,0,0,0.18))",
            zIndex: 1,
            pointerEvents: "none",
          }}
        />

        {images.map((src, i) => {
          const cfg = PHOTO_CONFIG[i % PHOTO_CONFIG.length];
          const isActive = i === index;

          return (
            <Box
              key={src}
              sx={{
                position: "absolute",
                zIndex: 2,
                // allow individual configs: top/left or top/right or bottom/...
                ...(cfg.top ? { top: cfg.top } : {}),
                ...(cfg.bottom ? { bottom: cfg.bottom } : {}),
                ...(cfg.left ? { left: cfg.left } : {}),
                ...(cfg.right ? { right: cfg.right } : {}),
                ...(cfg.transform ? { transform: cfg.transform } : {}),
                width: { xs: cfg.w * 0.7, md: cfg.w },
                height: { xs: cfg.h * 0.7, md: cfg.h },
                borderRadius: 3,
                overflow: "hidden",
                // subtle 3D effect and border
                boxShadow: isActive
                  ? "0 18px 40px rgba(8,18,28,0.45)"
                  : "0 10px 22px rgba(8,18,28,0.28)",
                border: "1px solid rgba(255,255,255,0.06)",
                transformOrigin: "center",
                transition:
                  "transform 700ms cubic-bezier(.2,.9,.2,1), opacity 600ms ease, filter 600ms ease",
                transform: isActive
                  ? `scale(1.06) rotate(${cfg.rotate})`
                  : `scale(0.98) rotate(${cfg.rotate})`,
                opacity: isActive ? 1 : 0.72,
                filter: isActive
                  ? "grayscale(10%) saturate(95%) contrast(1.02) brightness(0.98)"
                  : "grayscale(60%) saturate(70%) contrast(0.98) brightness(0.88) opacity(0.88)",
                // subtle movement animation for life — reduced for users who prefer less motion:
                animation: !isActive
                  ? "floatSlow 10s ease-in-out infinite"
                  : "none",
                pointerEvents: "none",
                display: "block",
                left: cfg.left, // keep typescript/lint happy when present
              }}
            >
              <Box
                component="img"
                src={src}
                alt={`decor-${i}`}
                sx={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  display: "block",
                  // a soft overlay within the card to give editorial tone
                  position: "relative",
                }}
              />
              {/* inner subtle gradient overlay to ensure contrast with text if a card overlaps */}
              <Box
                sx={{
                  position: "absolute",
                  inset: 0,
                  pointerEvents: "none",
                  background:
                    "linear-gradient(180deg, rgba(255,255,255,0.00) 0%, rgba(20,30,40,0.12) 100%)",
                }}
              />
            </Box>
          );
        })}

        {/* keyframes for slow float (keeps things elegant) */}
        <Box component="style" sx={{ display: "block" }}>
          {`@keyframes floatSlow {
              0% { transform: translateY(0px) }
              50% { transform: translateY(-6px) }
              100% { transform: translateY(0px) }
            }
            @media (prefers-reduced-motion: reduce) {
              .reduce-motion { animation: none !important; transition: none !important; }
            }
          `}
        </Box>
      </Box>

      {/* Decorative soft halo behind content for readability */}
      <Box
        sx={{
          position: "absolute",
          width: { xs: 360, md: 680 },
          height: { xs: 260, md: 360 },
          borderRadius: 3,
          background:
            "radial-gradient(60% 60% at 50% 40%, rgba(243,226,212,0.06), rgba(243,226,212,0.00) 45%)",
          zIndex: 1,
          pointerEvents: "none",
          filter: "blur(12px)",
        }}
      />

      {/* LOGO + TITLE + DESCRIPTION (kept crisp & on top) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 170, md: 240 },
          mb: { xs: 1.5, md: 2 },
          filter: "drop-shadow(0 10px 24px rgba(0,0,0,0.35))",
          position: "relative",
          zIndex: 3,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.9rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.5,
          textShadow: "2px 2px 14px rgba(0,0,0,0.45)",
          zIndex: 3,
          fontWeight: 700,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.98rem", md: "1.15rem" },
          color: "rgba(243,226,212,0.95)",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.28)",
          zIndex: 3,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 8px 22px rgba(0,0,0,0.22)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-3px)",
            boxShadow: "0 12px 34px rgba(0,0,0,0.32)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 3,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom wave — preserved */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 3,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
