import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

// asymmetrical panes for larger screens
const paneLayout = [
  { top: "8%", right: "6%", width: 220, height: 140, rotate: -6, z: 6 },
  { top: "20%", right: "16%", width: 260, height: 170, rotate: 6, z: 5 },
  { top: "42%", right: "8%", width: 200, height: 130, rotate: -3, z: 7 },
  { top: "60%", right: "18%", width: 240, height: 150, rotate: 4, z: 4 },
  { top: "74%", right: "6%", width: 190, height: 120, rotate: -8, z: 3 },
];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // preload images
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // handle reduced motion (SSR-safe check)
  const prefersReducedMotion =
    typeof window !== "undefined" &&
    window.matchMedia &&
    window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  useEffect(() => {
    clearInterval(intervalRef.current);
    const intervalMs = prefersReducedMotion ? 8000 : 4000;
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, intervalMs);

    return () => clearInterval(intervalRef.current);
  }, [prefersReducedMotion]);

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 3, md: 8 },
        zIndex: 2,
        // original gradient preserved as the page base
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative image cluster: shown on md+ as panes, on xs/md shown as thumbnails */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 1,
          pointerEvents: "none",
        }}
      >
        {/* subtle soft veil to keep images feeling as detail, not background */}
        <Box
          sx={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(180deg, rgba(243,226,212,0.03) 0%, rgba(10,14,20,0.06) 100%)",
            zIndex: 2,
            pointerEvents: "none",
          }}
        />

        {/* DESKTOP / LARGE: asymmetrical framed panes (md+) */}
        <Box
          sx={{
            display: { xs: "none", md: "block" },
            position: "absolute",
            inset: 0,
            zIndex: 3,
          }}
        >
          {paneLayout.map((p, paneIndex) => {
            const imgIdx = (index + paneIndex) % images.length;
            const src = images[imgIdx];

            return (
              <Box
                key={paneIndex}
                sx={{
                  position: "absolute",
                  top: p.top,
                  right: p.right,
                  width: p.width,
                  height: p.height,
                  transform: `rotate(${p.rotate}deg) translateZ(0)`,
                  zIndex: p.z,
                  borderRadius: 12,
                  overflow: "hidden",
                  boxShadow:
                    "0 10px 30px rgba(3,12,24,0.28), inset 0 1px 0 rgba(255,255,255,0.02)",
                  border: "1px solid rgba(243,226,212,0.08)",
                  backgroundColor: "rgba(255,255,255,0.02)",
                  transition:
                    "transform 520ms cubic-bezier(.2,.9,.2,1), opacity 520ms ease",
                  opacity: 0.95,
                  pointerEvents: "none",
                }}
              >
                <Box
                  component="img"
                  src={src}
                  alt={`hero-thumb-${paneIndex}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    display: "block",
                    // discrete look — slightly muted, warm tone, sharp
                    filter: "grayscale(8%) saturate(90%) contrast(0.99)",
                    opacity: 0.96,
                    transition: prefersReducedMotion
                      ? "none"
                      : "transform 12000ms linear",
                    transform: prefersReducedMotion
                      ? "scale(1)"
                      : "scale(1.02)",
                    willChange: "transform",
                  }}
                />

                {/* decorative dot only (no text) */}
                <Box
                  sx={{
                    position: "absolute",
                    left: 8,
                    bottom: 8,
                    width: 8,
                    height: 8,
                    borderRadius: "50%",
                    bgcolor: "rgba(65,94,114,0.9)",
                    boxShadow: "0 2px 6px rgba(0,0,0,0.18)",
                  }}
                />
              </Box>
            );
          })}
        </Box>

        {/* MOBILE / SMALL: compact thumbnail strip between logo & heading */}
        <Box
          sx={{
            display: { xs: "flex", md: "none" },
            position: "absolute",
            top: "18%",
            left: "50%",
            transform: "translateX(-50%)",
            gap: 2,
            zIndex: 3,
            pointerEvents: "none",
            alignItems: "center",
          }}
        >
          {/* show 3 small rounded thumbnails */}
          {[0, 1, 2].map((offset) => {
            const imgIdx = (index + offset) % images.length;
            const src = images[imgIdx];
            return (
              <Box
                key={offset}
                sx={{
                  width: 72,
                  height: 48,
                  borderRadius: 8,
                  overflow: "hidden",
                  boxShadow: "0 8px 20px rgba(3,12,24,0.18)",
                  border: "1px solid rgba(243,226,212,0.08)",
                  backgroundColor: "rgba(255,255,255,0.02)",
                }}
              >
                <Box
                  component="img"
                  src={src}
                  alt={`mobile-thumb-${offset}`}
                  sx={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    filter: "grayscale(6%) saturate(88%) contrast(0.98)",
                    opacity: 0.96,
                    transform: prefersReducedMotion ? "none" : "scale(1.02)",
                  }}
                />
              </Box>
            );
          })}
        </Box>
      </Box>

      {/* soft decorative circle accent */}
      <Box
        sx={{
          position: "absolute",
          top: "-18%",
          right: "-8%",
          width: { xs: 160, md: 300 },
          height: { xs: 160, md: 300 },
          bgcolor: "rgba(243,226,212,0.18)",
          borderRadius: "50%",
          zIndex: 4,
          filter: "blur(18px)",
          pointerEvents: "none",
        }}
      />

      {/* CONTENT: logo, heading, copy, CTA (always above images) */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 160, md: 260 },
          mb: 2,
          filter: "drop-shadow(0 6px 18px rgba(0,0,0,0.36))",
          position: "relative",
          zIndex: 5,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "1.95rem", md: "3.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 1.25,
          textShadow: "2px 2px 14px rgba(0,0,0,0.38)",
          zIndex: 5,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={640}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "0.95rem", md: "1.15rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 8px rgba(0,0,0,0.22)",
          zIndex: 5,
          px: { xs: 1, md: 0 },
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.18)",
          transition: "transform 0.28s ease, box-shadow 0.28s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 22px rgba(0,0,0,0.28)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 5,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* bottom svg wave (keeps your original) */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 5,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
