import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, Button } from "@mui/material";
import Logo from "../assets/logo3.png";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import { useLanguage } from "../contexts/LanguageContext";

import flag_photo from "../assets/flag_photo.png";
import romania1 from "../assets/romania1.png";
import romania2 from "../assets/romania1_2.png";
import romania3 from "../assets/romania3.jpg";
import romania2_3 from "../assets/romania2_2.jpg";

const images = [flag_photo, romania1, romania2, romania3, romania2_3];

const Hero = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);
  const { t } = useLanguage();

  // Preload
  useEffect(() => {
    images.forEach((src) => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  // Cycle every 4s, respects reduced motion preference
  useEffect(() => {
    const prefersReduced = window.matchMedia(
      "(prefers-reduced-motion: reduce)"
    ).matches;
    if (prefersReduced) return; // don't animate if user prefers reduced motion

    clearInterval(intervalRef.current);
    intervalRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, 4000);

    return () => clearInterval(intervalRef.current);
  }, []);

  // helper: pick image for a tile with offset for staggered feel
  const imgFor = (offset) => images[(index + offset) % images.length];

  return (
    <Box
      id="home"
      sx={{
        position: "relative",
        height: { xs: "calc(100vh - 52px)", md: "calc(100vh - 60px)" },
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        textAlign: "center",
        px: { xs: 4, md: 8 },
        zIndex: 2,
        // keep original gradient as the grounding background
        background: "linear-gradient(120deg, #C5B0CD 30%, #415E72 100%)",
      }}
    >
      {/* Decorative discrete-image layer (aria-hidden) */}
      <Box
        aria-hidden="true"
        sx={{
          position: "absolute",
          inset: 0,
          zIndex: 0,
          pointerEvents: "none",
        }}
      >
        {/* Left floating card */}
        <Box
          sx={{
            position: "absolute",
            left: { xs: "-6%", md: "4%" },
            top: { xs: "18%", md: "8%" },
            width: { xs: 160, md: 320 },
            height: { xs: 110, md: 220 },
            borderRadius: { xs: "14px", md: "22px" },
            backgroundImage: `url(${imgFor(0)})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            opacity: 0.18, // very discrete but colored
            transform: "rotate(-6deg)",
            boxShadow: "0 8px 22px rgba(12,20,30,0.18)",
            border: "1px solid rgba(243,226,212,0.08)",
            // soft fade at edges so the card blends into gradient
            maskImage:
              "radial-gradient(closest-side, rgba(0,0,0,1), rgba(0,0,0,0.2))",
            transition:
              "background-image 800ms ease-in-out, opacity 600ms ease",
          }}
        />

        {/* Center soft panel behind content (very subtle & wide) */}
        <Box
          sx={{
            position: "absolute",
            left: "50%",
            top: "22%",
            transform: "translateX(-50%)",
            width: { xs: "85%", md: "72%" },
            height: { xs: 220, md: 420 },
            borderRadius: { xs: "20px", md: "36px" },
            backgroundImage: `linear-gradient(180deg, rgba(243,226,212,0.06), rgba(243,226,212,0.02)), url(${imgFor(
              1
            )})`,
            backgroundBlendMode: "overlay, normal",
            backgroundSize: "cover",
            backgroundPosition: "50% 40%",
            opacity: 0.12,
            boxShadow: "0 30px 60px rgba(8,12,18,0.14)",
            border: "1px solid rgba(243,226,212,0.06)",
            filter: "saturate(1.04) contrast(1.03)",
            transition:
              "background-image 800ms ease-in-out, opacity 600ms ease",
            // subtle geometric mask so image is visible only in a soft band
            maskImage:
              "linear-gradient(180deg, rgba(0,0,0,0) 0%, rgba(0,0,0,1) 12%, rgba(0,0,0,1) 88%, rgba(0,0,0,0) 100%)",
          }}
        />

        {/* Right tall card */}
        <Box
          sx={{
            position: "absolute",
            right: { xs: "-4%", md: "6%" },
            bottom: { xs: "12%", md: "8%" },
            width: { xs: 140, md: 260 },
            height: { xs: 180, md: 360 },
            borderRadius: { xs: "14px", md: "20px" },
            backgroundImage: `url(${imgFor(2)})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            opacity: 0.16,
            transform: "rotate(8deg)",
            boxShadow: "0 10px 30px rgba(12,20,30,0.16)",
            border: "1px solid rgba(243,226,212,0.06)",
            maskImage:
              "radial-gradient(100% 80% at 50% 50%, rgba(0,0,0,1) 30%, rgba(0,0,0,0) 100%)",
            transition:
              "background-image 800ms ease-in-out, opacity 600ms ease",
          }}
        />

        {/* Small badge at top-left */}
        <Box
          sx={{
            position: "absolute",
            left: { xs: "6%", md: "8%" },
            top: { xs: "6%", md: "8%" },
            width: { xs: 72, md: 110 },
            height: { xs: 72, md: 110 },
            borderRadius: "50%",
            backgroundImage: `url(${imgFor(3)})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            opacity: 0.18,
            transform: "translateZ(0)",
            boxShadow: "0 8px 18px rgba(8,12,18,0.12)",
            border: "1px solid rgba(243,226,212,0.08)",
            transition:
              "background-image 800ms ease-in-out, opacity 600ms ease",
          }}
        />

        {/* Very faint corner smear for depth */}
        <Box
          sx={{
            position: "absolute",
            right: "12%",
            top: "8%",
            width: { xs: 120, md: 220 },
            height: { xs: 60, md: 140 },
            borderRadius: "12px",
            backgroundImage: `url(${imgFor(4)})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            opacity: 0.08,
            transform: "rotate(-12deg)",
            filter: "saturate(1.05)",
            transition: "background-image 800ms ease-in-out",
          }}
        />
      </Box>

      {/* Original decorative circle (soft) */}
      <Box
        sx={{
          position: "absolute",
          top: "-20%",
          right: "-10%",
          width: { xs: 200, md: 300 },
          height: { xs: 200, md: 300 },
          bgcolor: "rgba(243,226,212,0.25)",
          borderRadius: "50%",
          zIndex: 3,
          filter: "blur(8px)",
        }}
      />

      {/* Logo */}
      <Box
        component="img"
        src={Logo}
        alt="Odyssey Logo"
        sx={{
          width: { xs: 200, md: 280 },
          mb: 2,
          filter: "drop-shadow(0 6px 16px rgba(0,0,0,0.4))",
          position: "relative",
          zIndex: 4,
        }}
      />

      <Typography
        variant="h1"
        sx={{
          fontSize: { xs: "2.25rem", md: "3.5rem" },
          color: "#F3E2D4",
          letterSpacing: "0.04em",
          mb: 2,
          textShadow: "2px 2px 10px rgba(0,0,0,0.42)",
          zIndex: 4,
        }}
      >
        {t("Odyssey of Wisdom")}
      </Typography>

      <Typography
        variant="h6"
        maxWidth={600}
        sx={{
          fontFamily: "Inter, sans-serif",
          fontSize: { xs: "1rem", md: "1.25rem" },
          color: "#F3E2D4",
          letterSpacing: "0.02em",
          lineHeight: 1.6,
          mb: 4,
          textShadow: "1px 1px 6px rgba(0,0,0,0.25)",
          zIndex: 4,
        }}
      >
        {t(
          "Connecting Greek youth with Erasmus+ opportunities for travel, learning, and cultural exchange across Europe."
        )}
      </Typography>

      <Button
        endIcon={<ExpandMoreIcon />}
        variant="contained"
        size="large"
        sx={{
          backgroundColor: "#F3E2D4",
          color: "#415E72",
          px: 5,
          py: 1.75,
          borderRadius: "30px",
          fontFamily: "Inter, sans-serif",
          fontWeight: 600,
          textTransform: "none",
          boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
          "&:hover": {
            transform: "translateY(-2px)",
            boxShadow: "0 8px 20px rgba(0,0,0,0.3)",
            backgroundColor: "#e5d7ce",
          },
          zIndex: 4,
        }}
        onClick={() =>
          document
            .getElementById("about")
            ?.scrollIntoView({ behavior: "smooth" })
        }
      >
        {t("Learn more")}
      </Button>

      {/* Bottom wave */}
      <Box
        component="svg"
        viewBox="0 0 1440 100"
        sx={{
          position: "absolute",
          bottom: 0,
          left: 0,
          width: "100%",
          height: "auto",
          zIndex: 4,
        }}
      >
        <path
          fill="#F3E2D4"
          d="M0,40 C360,140 1080,-60 1440,40 L1440,100 L0,100 Z"
        />
      </Box>
    </Box>
  );
};

export default Hero;
